INSERT INTO `books` (`bid`, `bname`, `kinds`, `author`, `readnum`, `cover`, `wordnum`, `describe1`, `status`, `addtime`, `aid`) VALUES
	(1, '三寸人间', '仙侠', '耳根', NULL, 'uploads\\2019\\03\\155202589989877647663.jpg', '10万字', '举头三尺无神明，掌心三寸是人间。这是耳根继《仙逆》《求魔》《我欲封天》《一念永恒》后，创作的第五部长篇小说《三寸人间》。', 1, '2019-03-08', 1),
	(2, '我欲封天', '仙侠', '耳根', NULL, 'uploads\\2019\\03\\155222969401437266413.jpg', '40万字', '　我若要有 　　天不可无 　　我若要无 　　天不许有！ 　　这是一个起始于第八山与第九山之间的故事 　　一个“我命如妖欲封天”的世界！　', 1, '2019-03-10', 1),
	(3, '一念永恒', '仙侠', '耳根', NULL, 'uploads\\2019\\03\\155222995174823810204.jpg', '369万字', '悠悠万载一念间，斩千魔，诛万仙，唯我永恒', 1, '2019-03-10', 1),
	(4, '仙逆', '仙侠', '耳根', NULL, 'uploads\\2019\\03\\155223016626091009437.jpg', '651.44万字', '修仙觅长生，热血任逍遥，踏莲曳波涤剑骨，凭虚御风塑圣魂！', 1, '2019-03-10', 1),
	(5, '求魔', '仙侠', '耳根', NULL, 'uploads\\2019\\03\\155223072405592548769.jpg', '458.5万字', '魔前一叩三千年，回首凡尘不做仙，只为她……掌缘生灭', 1, '2019-03-10', 1),
	(6, '圣墟', '玄幻', '辰东', NULL, 'uploads\\2019\\03\\155229893714943008874.jpg', '125万字', '在破败中崛起，在寂灭中复苏。 沧海成尘，雷电枯竭，那一缕幽雾又一次临近大地，世间的枷锁被打开了，一个全新的世界就此揭开神秘的一角……', 1, '2019-03-11', 2),
	(7, '斗破苍穹', '玄幻', '天蚕土豆', NULL, 'uploads\\2019\\03\\155244760299157179176.jpg', '650万字', '这里是属于斗气的世界，没有花俏艳丽的魔法，有的，仅仅是繁衍到巅峰的斗气！ 新书刚开，请各位兄弟多多支持，用推荐票和收藏，砸烂偶吧.^_^ 新书等级制度：斗者，斗师，大斗师，斗灵，斗王，斗皇，斗宗，斗尊，斗圣，斗帝。', 1, '2019-03-13', 7),
	(8, '武动乾坤', '玄幻', '天蚕土豆', NULL, 'uploads\\2019\\03\\155245819402810321999.jpg', '600万字', '修炼一途，乃窃阴阳，夺造化，转涅盘，握生死，掌轮回。  武之极，破苍穹，动乾坤', 1, '2019-03-13', 7),
	(9, '大主宰', '玄幻', '天蚕土豆', NULL, 'uploads\\2019\\03\\155245857798642567037.jpg', '496.03万字', '大千世界，位面交汇，万族林立，群雄荟萃，一位位来自下位面的天之至尊，在这无尽世界，演绎着令人向往的传奇，追求着那主宰之路。\n', 1, '2019-03-13', 7),
	(10, '飞剑问道', '仙侠', '我吃西红柿', NULL, 'uploads\\2019\\03\\155245891084023931943.jpg', '186.15万字', '　　在这个世界，有狐仙、河神、水怪、大妖，也有求长生的修行者。\n　　修行者们，\n　　开法眼，可看妖魔鬼怪。\n　　炼一口飞剑，可千里杀敌。\n　　千里眼、顺风耳，更可探查四方。\n　　……\n　　秦府二公子‘秦云’，便是一位修行者……', 1, '2019-03-13', 3),
	(11, '星辰变', '仙侠', '我吃西红柿', NULL, 'uploads\\2019\\03\\155245900742772522128.jpg', '280.38万字', '一名孩童，天生无法修炼内功。为了得到父亲的重视关注，他毅然选择了修炼痛苦艰难的外功。春去秋来，时光如梭，这个孩童长大了……变成了一名青年，真正改变他的命运，是一颗流星化作的神秘晶石——流星泪。', 1, '2019-03-13', 3),
	(12, '盘龙', '玄幻', '我吃西红柿', NULL, 'uploads\\2019\\03\\155245913700096217960.jpg', '332.98万字', '　大小的血睛鬃毛狮，力大无穷的紫睛金毛猿，毁天灭地的九头蛇皇，携带着毁灭雷电的恐怖雷龙……这里无奇不有，这是一个广博的魔幻世界。强者可以站在黑色巨龙的头顶遨游天际，恐怖的魔法可以焚烧江河，可以毁灭城池，可以夷平山岳……\n　　这本书，讲述了一个拥有‘盘龙戒指’的少年的梦幻旅程。', 1, '2019-03-13', 3),
	(13, '全球高武', '都市', '老鹰吃小鸡', NULL, 'uploads\\2019\\03\\155246759143654998006.jpg', '46.35万字', '地窟入侵，武道崛起。\n　　小人物也有大情怀，为生存而战，为守护而战。\n　　且看小人物方平，一步步崛起，拯救（忽悠）全世界的故事！', 1, '2019-03-13', 11),
	(14, '我是至尊', '玄幻', '风凌天下', NULL, 'uploads\\2019\\03\\155247537592829130160.jpg', '2337.97万字', '药不成丹只是毒，人不成神终成灰。\n　　…………\n　　天道有缺，人间不平，红尘世外，魍魉横行；哀尔良善，怒尔不争；规则之外，吾来执行。\n　　布武天下，屠尽不平；手中有刀，心中有情；怀中美人，刀下奸雄；冷眼红尘，无憾今生。', 1, '2019-03-13', 12),
	(15, '凌天传说', '玄幻', '风凌天下', NULL, 'uploads\\2019\\03\\155247544997907583436.jpg', '235.38万字', '我名凌天，我欲凌天！\n　　穿越到异世的武学天才，超卓的武功，绝世的智慧；且看凌天如何在异世只手舞风云，左右逢源、文武双绝、威动天下；造就一个永恒的凌天传说！\n　　翻云覆雨天假手，兴衰两世我独狂！\n　　谈笑之中风云舞，剑气凌风任飞扬', 1, '2019-03-13', 12),
	(16, '异世邪君', '玄幻', '风凌天下', NULL, 'uploads\\2019\\03\\155247556615244724624.jpg', '417.43万字', '世间毁誉，世人冷眼，与我何干？我自淡然一笑；以吾本性，快意恩仇，以吾本心，遨游世间，我命由我不由天！一代牛人穿越异界，看其如何踏上异世巅峰，成为一代邪君！', 1, '2019-03-13', 12),
	(17, '傲世九重天', '玄幻', '风凌天下', NULL, 'uploads\\2019\\03\\155247560696746008969.jpg', '832.44万字', '一笑风雷震，一怒沧海寒；一手破苍穹，一剑舞长天！一人一剑，傲世九重天！', 1, '2019-03-13', 12),
	(18, '天域苍穹', '玄幻', '风凌天下', NULL, 'uploads\\2019\\03\\155247565663133366684.jpg', '513.70万字', '笑尽天下英雄，宇内我为君主！\n万水千山，以我为尊；八荒六合，唯我称雄！\n我欲舞风云，凌天下，踏天域，登苍穹！谁可争锋？！\n诸君可愿陪我，并肩凌天下，琼霄风云舞，征战这天域苍穹？！', 1, '2019-03-13', 12),
	(19, ' 牧神记', '玄幻', '宅猪', NULL, 'uploads\\2019\\03\\155247576612708833228.jpg', '466.75万字', '　大墟的祖训说，天黑，别出门。\n大墟残老村的老弱病残们从江边捡到了一个婴儿，取名秦牧，含辛茹苦将他养大。这一天夜幕降临，黑暗笼罩大墟，秦牧走出了家门……\n做个春风中荡漾的反派吧！\n瞎子对他说。\n秦牧的反派之路，正在崛起！', 1, '2019-03-13', 13),
	(20, '人道至尊', '玄幻', '宅猪', NULL, 'uploads\\2019\\03\\155247583738516851110.jpg', '488.58万字', '自盘古开天，三皇治世，五帝定伦，世界之间，人为天地灵长……\n　　此时正值三皇中的人皇末期，五帝未定，人族也不是天地灵长。\n　　这里是莽苍荒蛮的时代，妖神、邪神、天神，诸神林立，妖魔、邪魔、天魔，群魔乱舞；\n　　万族并存，野蛮生长，统治天下，而人皇已老，人族弱小，被当成祭牲和食粮……\n　　这不是洪荒，而是狂野奔放的蛮荒！', 1, '2019-03-13', 13),
	(21, '我真的不是富二代', '都市', '幻羽呀', NULL, 'uploads\\2019\\03\\155247614783788888472.jpg', '102.14万字', '姚衣放下豪宅的钥匙，退回母亲作为生日礼物送来的豪车，脱下一身名牌定制，看了眼镜子里的年轻人。 镜子里的人在说：重活这一世，我不想再做富二代。', 1, '2019-03-13', 14),
	(22, '大医凌然', '都市', '志鸟村', NULL, 'uploads\\2019\\03\\155247628635707889186.jpg', '146.86万字', '医学院学生凌然有一个小目标，要成为世界上最伟大的医生，结果不小心实现了。', 1, '2019-03-13', 15),
	(23, '时空走私从2000年开始', '都市', '志鸟村', NULL, 'uploads\\2019\\03\\155247635515626044807.jpg', '230.54万字', '多少人穿越后因为缺少知识而苦恼。多少人穿越后带着笔记本却面临缺电的窘境。\n', 1, '2019-03-13', 15),
	(24, '未来图书馆', '都市', '志鸟村', NULL, 'uploads\\2019\\03\\155247642803498893984.jpg', '194.55万字', '天性纨绔，以优渥生活为目标。天生强力，以虐待贵族为己任。在计算越强，战斗力越强的世界里，21世纪的知识已经足以傲视群雄。就是这样仍不够，一枚能够联通外域世界的戒指...', 1, '2019-03-13', 15),
	(25, '修真聊天群', '都市', '圣骑士的传说', NULL, 'uploads\\2019\\03\\155247653272636171751.jpg', '706.84万字', '某天，宋书航意外加入了一个仙侠中二病资深患者的交流群，里面的群友们都以‘道友’相称，群名片都是各种府主、洞主、真人、天师。连群主走失的宠物犬都称为大妖犬离家出走。...', 1, '2019-03-13', 16),
	(26, '你真是个天才', '仙侠', '国王陛下', NULL, 'uploads\\2019\\03\\155247668118952539036.jpg', '7.82万字', '看你干的好事！ 这小庙要容不下你了吧？ 你可真是个天才！ 天才少年白骁的千里寻亲记', 1, '2019-03-13', 17),
	(27, '从前有座灵剑山', '仙侠', '国王陛下', NULL, 'uploads\\2019\\03\\155247672601821861720.jpg', '287.25万字', '灵剑派成立于九州历四二三三年，几千年来始终致力于为行业提供一流的修仙人才，如今位列万仙盟五大超品宗派之一，掌门风吟真人担任万仙盟七大常务长老，修为盖世。灵剑派坚持...', 1, '2019-03-13', 17),
	(28, '崩坏星河', '仙侠', '国王陛下', NULL, 'uploads\\2019\\03\\155247677847795239904.jpg', '128.60万字', '随着星际殖民时代的开启，人类终于将文明核心价值观散布到了广袤的星河之中。粉碎星球的战舰巨炮为漆黑寒冷的宇宙点燃了正能量之火，大炮射程之内，遍地真理！这是最好的时代...', 1, '2019-03-13', 17),
	(29, '盗梦宗师', '玄幻', '国王陛下', NULL, 'uploads\\2019\\03\\155247683738939665194.jpg', '188.35万字', '春梦了无痕，形容的是再美妙的梦境也非真实。梦境与现实的隔阂，牢不可破。然而在筑梦师的国度中，梦境的力量却无比真实，修炼梦境，便可以随心所欲。无所不能。', 1, '2019-03-13', 17),
	(30, ' 同桌凶猛', '都市', '柳下挥', NULL, 'uploads\\2019\\03\\155247698981028169452.jpg', '59.82万字', '青梅竹马的同桌竟然成了大明星，又凶又萌。', 1, '2019-03-13', 18),
	(31, '六迹之贪狼', '都市', '柳下挥', NULL, 'uploads\\2019\\03\\155247703149770014331.jpg', '20.67万字', '神石破碎，无数碎片散落世界，灵气溃散使得修真消亡。平静的都市下，神石碎片的能量激发，一部分人更改基因，开启觉醒。天生痴呆的少年，意外被抓走进行了“爱因斯坦”实验，.', 1, '2019-03-13', 18),
	(32, '校花的贴身高手', '都市', ' 鱼人二代', NULL, 'uploads\\2019\\03\\155247718695558799305.jpg', '120.5万字', '传奇杀手回归都市，奉旨保护校花！\n　　我是校花的贴身高手，你们最好离我远一点，不然大小姐又要吃醋了！', 1, '2019-03-13', 19),
	(33, '重生似水青春', '都市', ' 鱼人二代', NULL, 'uploads\\2019\\03\\155247723211431029362.jpg', '120.5万字', '回到2001年的高中时代，申大鹏再一次站在了人生的十字路口。是随波逐流，还是逆流而上？申大鹏只想享受这纯真而似水的青春。', 1, '2019-03-13', 19),
	(34, '很纯很暧昧', '都市', ' 鱼人二代', NULL, 'uploads\\2019\\03\\155247726939247451785.jpg', '152.0万字', '杨明是一名普通的学生，某一天，他收到一份礼物，一只神奇的眼镜，从此生活变得丰富多彩。', 1, '2019-03-13', 19),
	(35, '极品修真强少', '都市', ' 鱼人二代', NULL, 'uploads\\2019\\03\\155247733525170592573.jpg', '150.25万字', '被赶出家族的修真纨绔成为未婚妻的贴身男佣！呆萌表妹，清纯校花，热辣女贼，美女侠客全都找我干啥？我再强，也应付不了你们啊，我还得修真呢！（鱼宝宝书友1群333702438）', 1, '2019-03-13', 19),
	(36, '学霸的黑科技系统', '科幻', '晨星LL', NULL, 'uploads\\2019\\03\\155272216933869956311.jpg', '197.90万字', '“系统，积分能兑钱吗？”“不能。”“干，那我要你何用！”“本系统能让你当上学爸，全人类爸爸的爸，你还要钱有什么用？”本书又名《我要当学霸》', 1, '2019-03-16', 20),
	(37, '我在末世有套房', '科幻', '晨星LL', NULL, 'uploads\\2019\\03\\155272242152803383819.jpg', '400万字', '核战之后的末世，到处都是一片狼藉。如果你一不小心活了下来，那么接下来你将不得不面对饥饿与疾病的恐惧，一到夜晚就会发狂的丧尸，还有那些因辐射而变得奇形怪状的异种.....', 1, '2019-03-16', 20),
	(38, '星界游民', '科幻', '晨星LL', NULL, 'uploads\\2019\\03\\155272255151780599850.jpg', '76.51万字', '星河时代，地球联邦与奥术帝国的战争，最终以两个世界的毁灭落下了帷幕。“星环时代”号舰长江枫乘坐的逃生舱，意外穿越了次元壁垒，跃迁到了一片陌生的星域，成为了地球联邦...', 1, '2019-03-16', 20),
	(39, '黎明之剑', '科幻', '远瞳', NULL, 'uploads\\2019\\03\\155272265730925006579.jpg', '159.51万字', '高文穿越了，但穿越的时候稍微出了点问题。在某个异界大陆上空飘了十几万年之后，他觉得自己可能需要一具身体才算是成为一个完整的穿越者，但他并没想到自己好...', 1, '2019-03-16', 21),
	(40, '希灵帝国', '科幻', '远瞳', NULL, 'uploads\\2019\\03\\155272274712418856796.jpg', '806.19万字', '不是帝国争霸，不是异界风云，更不是升级练功，其实，这是一本非常严肃正经认真的硬科幻救世文——你就当真的听。好吧，其实这就是一群没溜的领袖和一个坑爹的元首欢乐无节操...', 1, '2019-03-16', 21),
	(41, '异常生物见闻录', '科幻', '远瞳', NULL, 'uploads\\2019\\03\\155272278743154309953.jpg', '524.65万字', '郝仁，人如其名，是个好人，理想是平平安安过一辈子，当个穷不死但也发不了财的小房东——起码在他家里住进去一堆神经病生物之前是这样。一栋偏僻陈旧的大屋，一堆不怎么正', 1, '2019-03-16', 21),
	(42, '遮天', '仙侠', '辰东', NULL, 'uploads\\2019\\03\\155289476330673735285.jpg', '632.31万字', '冰冷与黑暗并存的宇宙深处，九具庞大的龙尸拉着一口青铜古棺，亘古长存。 这是太空探测器在枯寂的宇宙中捕捉到的一幅极其震撼的画面。 九龙拉棺，究竟是回到了上古，还是...', 1, '2019-03-18', 2),
	(43, '完美世界', '玄幻', '辰东', NULL, 'uploads\\2019\\03\\155289481806287855666.jpg', '657.66万字', '一粒尘可填海，一根草斩尽日月星辰，弹指间天翻地覆。群雄并起，万族林立，诸圣争霸，乱天动地。问苍茫大地，谁主沉浮？！一个少年从大荒中走出，一切从这里开始……', 1, '2019-03-18', 2),
	(44, '长生界', '玄幻', '辰东', NULL, 'uploads\\2019\\03\\155289486535654115346.jpg', '285.89万字', '世上谁人能不死？任你风华绝代，艳冠天下，到头来也是红粉骷髅；任你一代天骄，坐拥万里江山，到头来也终将化成一抔黄土。不过，关于长生不死的传说却始终流传于世。故老相传...', 1, '2019-03-18', 2);
