INSERT INTO `comment` (`cid`, `time`, `content`, `uimg`, `uid`, `uname`, `score`, `bid`, `status`) VALUES
	(1, '2019-03-18 00:00:00', '很好，非常好', 'https://ss0.bdstatic.com/70cFvHSh_Q1YnxGkpoWK1HF6hhy/it/u=3658142832,814471228&fm=115&gp=0.jpg', 1, 'peanut', 2.5, 9, 1),
	(2, '2019-03-18 20:10:32', '这本小说写的真精彩', 'https://ss0.bdstatic.com/70cFvHSh_Q1YnxGkpoWK1HF6hhy/it/u=3658142832,814471228&fm=115&gp=0.jpg', 1, 'peanut', 2.5, 9, 1),
	(3, '2019-03-18 00:00:00', '这是我看过的最好看的一部仙侠小说！！！', 'https://ss0.bdstatic.com/70cFvHSh_Q1YnxGkpoWK1HF6hhy/it/u=3658142832,814471228&fm=115&gp=0.jpg', 2, 'pander', 5, 1, 1),
	(4, '2019-03-18 00:00:00', '东哥，我看你的书好些年头了，从上初中开始，一直都是你的书让我坚持活下去，没有你我早就自杀了。我一直在默默的看着，从来没有评论过，这次之所以注册一个号来评论，是因为我想我快坚持不下去了。以前对你都是满满的期待，想的都是每天能看到你的玄幻世界，是他给了我动力，现在的你，好几天才一更，而且更的质量都不如以前了，你现在的十章可能才顶的上以前的一章，我现在活下去的动力越来越弱了，或许在这几天吧，我就会结束这样的生活，希望我走了以后，你能对得起其他的书友，认真更新，用心去写。不要记得曾经有一个书迷为了你断更而自杀，不要有任何内疚，那只会影响你的创作，影响书友的心情，我不想变成那样，不想当这个罪人，我希望你能当我是一只蚂蚁那样踩死都不难过，真的，希望你以后好好更新，我会留下遗言让朋友把完本烧给我', 'https://ss0.bdstatic.com/70cFvHSh_Q1YnxGkpoWK1HF6hhy/it/u=3658142832,814471228&fm=115&gp=0.jpg', 2, 'pander', 5, 6, 1),
	(5, '2019-03-18 00:00:00', '从初中开始追，一直都觉得辰东的风格很炫酷，中国风很浓厚。 不知道我写的小说怎么才能积累到你们这样的水平？？', 'https://ss0.bdstatic.com/70cFvHSh_Q1YnxGkpoWK1HF6hhy/it/u=3658142832,814471228&fm=115&gp=0.jpg', 2, 'pander', 5, 6, 1),
	(6, '2019-03-18 00:00:00', '哪怕一天一更也好啊', 'https://ss0.bdstatic.com/70cFvHSh_Q1YnxGkpoWK1HF6hhy/it/u=3658142832,814471228&fm=115&gp=0.jpg', 2, 'pander', 5, 6, 1),
	(7, '2019-03-18 00:00:00', '更新好慢呀', 'http://img2.imgtn.bdimg.com/it/u=3135339935,1064367009&fm=26&gp=0.jpg', 3, '小雨杨', 5, 1, 1),
	(11, '2019-03-18 00:00:00', '刺激，热血，不错的一部小说', 'https://ss0.bdstatic.com/70cFvHSh_Q1YnxGkpoWK1HF6hhy/it/u=3658142832,814471228&fm=115&gp=0.jpg', 1, 'peanut', 5, 2, 1),
	(12, '2019-03-18 00:00:00', '希望更新快点啦', 'https://ss0.bdstatic.com/70cFvHSh_Q1YnxGkpoWK1HF6hhy/it/u=3658142832,814471228&fm=115&gp=0.jpg', 1, 'peanut', 5, 2, 1),
	(13, '2019-03-18 00:00:00', '加油，支持你，希望你一如既往', 'https://ss0.bdstatic.com/70cFvHSh_Q1YnxGkpoWK1HF6hhy/it/u=3658142832,814471228&fm=115&gp=0.jpg', 1, 'peanut', 4.5, 10, 1),
	(14, '2019-03-18 00:00:00', '我是属于看小说很少评论的，也一直喜欢东哥的小说。最近看圣墟，感觉剧情相似，经常调动读者愤慨情绪，类似剧情看的也有点疲劳。小说作者状态起伏、请假、有事托更可以理解，毕竟是人不是机器。希望作者不要把每天2章当作负担，感受生活，寻找灵感，给小说添加点新色彩。', 'http://xiaoyang:81\\uploads\\2019\\03\\155266209459202405542.jpg', 4, '伏天', 5, 6, 1),
	(15, '2019-03-18 00:00:00', '心潮滂湃，相见恨晚，百万虽巨，亦难表吾之喜爱！', 'http://xiaoyang:81\\uploads\\2019\\03\\155266209459202405542.jpg', 4, '伏天', 4, 1, 1),
	(16, '2019-03-18 00:00:00', '天蚕土豆呀', 'http://xiaoyang:81\\uploads\\2019\\03\\155266209459202405542.jpg', 4, '伏天', 4, 8, 1),
	(17, '2019-03-18 00:00:00', '越来越水了呀！！', 'http://xiaoyang:81\\uploads\\2019\\03\\155266209459202405542.jpg', 4, '伏天', 3, 14, 1),
	(18, '2019-03-18 00:00:00', '一直重复写，有意思？再没创新的概念，就等着GG吧', 'http://xiaoyang:81\\uploads\\2019\\03\\155266209459202405542.jpg', 4, '伏天', 3.5, 9, 1),
	(19, '2019-03-22 00:00:00', '', 'https://ss0.bdstatic.com/70cFvHSh_Q1YnxGkpoWK1HF6hhy/it/u=3658142832,814471228&fm=115&gp=0.jpg', 1, 'peanut', 5, 26, 1),
	(20, '2019-03-22 00:00:00', '求魔这本小说真的很不错，希望作者加快更', 'https://ss0.bdstatic.com/70cFvHSh_Q1YnxGkpoWK1HF6hhy/it/u=3658142832,814471228&fm=115&gp=0.jpg', 1, 'peanut', 4, 5, 1);
INSERT INTO `section` (`sid`, `sname`, `scontent`, `status`, `bid`, `addstime`) VALUES
	(1, '我要减肥！', '炎炎夏日，位于联邦东部的池云雨林，云雾弥漫，好似一层薄纱环绕，一棵棵参天古树，纵横交错，繁茂的树冠中，时而有几只飞鸟腾空而起，嘶鸣着翱翔于天际间。\r\n\r\n    天空上，那仿佛可以永恒存在的太阳，已然不再是人们记忆里的样子，而是在多年前，被一把庞大到难以形容的青铜古剑，直接刺穿，露出小半个剑尖！\r\n\r\n    这古剑似经历万古岁月，自星空而来，透出无尽沧桑，更有一股强烈的威压，形成光晕，笼罩苍穹，仿佛能镇压大地，让众生膜拜！\r\n\r\n    远远看去，这一幕剑阳雨林，好似一副画卷，直至远处传来嗡嗡之声，才被打破。一艘红色的大热气球船，正于雨林上方缓缓飞来。\r\n\r\n    这热气球船舱很大，足以容纳数百人，能看到很多少年男女，在甲板上三五成群，时而传出欢声笑语。\r\n\r\n    他们，是来自凤凰城中，这一届考入联邦四大道院之一，缥缈道院的学子，正乘坐这属于缥缈道院的飞艇，跨越万里，前往缥缈道院求学。\r\n\r\n    或许是对求学的期待，旅程对于这些少年男女来说并不枯燥，男女之间，更有一些朦胧的吸引，使得这万里之旅，别有一些乐趣。\r\n\r\n    又因路途遥远，所以在这飞艇上，配备了食馆，修灵室、医务室等区域。\r\n\r\n    此刻，飞艇的食馆中，学子不少，其中一处饭桌旁，坐着一个挺着肚皮的小胖子。\r\n\r\n    这小胖子约莫十七八岁，穿着蓝色的宽大长袍，圆圆的小脸勉强也算眉清目秀，正一边拍着肚子，发出啪啪的声音，一边追悔莫及的望着面前七八个空空的盘子。\r\n\r\n    “这一顿吃下去又要涨三斤，我怎么就没忍住呢，想要成为联邦总统，我不能英年早逝啊。”小胖子愁眉苦脸，懊悔不已时，打了个饱嗝。\r\n\r\n    这小胖子叫王宝乐，也是这一次考入缥缈道院的学子，他这一生的梦想，就是当官，把成为联邦总统，当成了自己的毕生追求，之所以有这样伟大的理想，与他的童年经历密不可分。\r\n\r\n    他的记忆里，小的时候父亲经常语重心长的拍着他的头，感慨唏嘘。\r\n\r\n    “宝乐，还是当官好啊，你要记得，钱虽然可以解决一切，但还是会被人欺负，想要不被人欺负，只有当官，成为人上人。”\r\n\r\n    开始的时候王宝乐不懂，直至他小学时，因为没有按时交作业，被班长喝斥，在他送了两块糖后，又被班长记名，向老师打小报告，这一切，在他幼小的心灵中，留下了不可磨灭的烙印。\r\n\r\n    于是从那一刻起，他就想当班长，不是去欺负人，而是为了自己能不被别人欺负。\r\n\r\n    可随着长大，他发现能欺负班长的还有好多，于是觉得成为最大的官，也就是联邦总统，就真的没人敢欺负自己了。之所以削尖脑袋考入缥缈道院，也是因为联邦所有高官，都是在四大道院毕业的。\r\n\r\n    只是他命不太好，家族血脉很是奇葩，他至今还记得一年前的那天夜里，枯瘦如柴的父亲在家族的祠堂，给他看了一眼族谱。\r\n\r\n    那是王宝乐第一次看到族谱，他清晰的看到，一代代祖先，但凡体重超过二百斤的，无不英年早逝，活不过三十五。\r\n\r\n    那一夜，王宝乐做了一个梦……他还没等成为联邦总统，就与那些胖爷爷们，团聚了。\r\n\r\n    之后的一年，对他来说，减肥这种事情，已经上升到了一定的高度，可哪怕联邦步入灵元纪后，随着灵气的浓郁，随着古武的复起，减肥的办法也都五花八门，但王宝乐几乎尝试了所有，体重依旧稳中有进。\r\n\r\n    记忆的浮现，让王宝乐狠狠一咬牙，下定决心明天再减肥。\r\n\r\n    有了这样的决定后，王宝乐顿时觉得压力小了很多，一边剔着牙，一边哼着小曲，抬头望着远处窗外蓝天，脑子也开始活泛了起来。\r\n\r\n    “也不知道送给卢医师的礼物，他喜欢不喜欢，那可是我从家里顺出来的古董，那老家伙应该会喜欢吧。”王宝乐安慰自己，琢磨着只要傍上了卢医师，以后自己在道院里，也算有了个小靠山。\r\n\r\n    “老师权力有限，不过以后也要送点礼物，但这卢医师大把年纪，必定人脉不小，我这一步，应该是走对了。”想到这里，王宝乐美滋滋的，只觉自己向联邦总统的位置，又近了一步。\r\n\r\n    他对自己能成为联邦总统，很有信心，这信心主要是来自于他从小到大钻研的所有高官自传，甚至还总结出了几招当官杀手锏。\r\n\r\n    此刻身心愉悦，王宝乐看向窗外的蓝天，也都觉得格外美好。\r\n\r\n    可很快的，他就眼睛突地睁大，注意到远处天空，有一片黑色的云层凝聚，弥漫如欲遮天，其内有雷电，闪烁出一道道电光，正缓缓靠近，这一幕也引起不少学子的注意，传出惊呼。\r\n\r\n    “是雷磁暴！”\r\n\r\n    王宝乐也吃了一惊，实在是联邦踏入灵元纪后，因灵气的出现，导致天地间滋生了不少惊人的气象，曾经的飞行物难以安全，这才有了依靠灵石驱动的热气球飞艇的诞生。\r\n\r\n    随着不断地惊呼，轰隆隆的雷鸣突然传来，巨响惊天，能看到远处雷磁黑云飞速壮大，其内闪电已经蔓延开来，如同黑色的大网，闪烁天际，耀目惊心，让人心跳不由加快，原本行驶中的飞艇，此刻也慢慢减速。\r\n\r\n    就在这众人心神不宁时，食馆大门被人推开，仿佛有风呼啸，走进一个身穿白色道袍的老者，这老者满脸皱纹，可却仙风道骨，面容端正，尤其是双目精光闪耀，一身正气散及四方，刚一到来，他威严的声音，就传遍整个食馆。\r\n\r\n    “所有人，立刻于修灵室报道，穿上你们的磁灵服，半柱香后，我们将进入雷磁区！”\r\n\r\n    话语一出，食馆的众学子纷纷敬畏，赶紧站好。\r\n\r\n    王宝乐眼睛一亮，眼前这老者，正是他之前送过礼的卢医师，此刻望着对方那龙行虎步般的气势，王宝乐越发觉得自己之前的投资没错，暗道这老家伙必非常人，而自己更是棋高一手。\r\n\r\n    正美滋滋的得意时，那老医师目光一扫，看到了王宝乐，哼了一声，走了过来，从怀里拿出半块黑色的面具，扔在了王宝乐身上。\r\n\r\n    “小小年纪，心思不要放在一些乱七八糟的地方上，你还没入道院呢，就学会了送礼，老夫也是见多识广的人，你这面具，还是自己留着吧。”老医师神色肃然，一副清廉刚正的模样，仿佛恨铁不成钢一般训斥道。\r\n\r\n    王宝乐接过面具，心底咯噔一声，看出老医师这是生气了，有些着急，刚想去解释，可忽然想到自己在那些高官自传上总结的杀手锏，其中一条就是在上司面前，厚着脸皮第一时间承认错误，往往可大事化小。\r\n\r\n    于是深吸口气，赶紧摆出一副悔悟的模样，承认错误。\r\n\r\n    “老师说的对，我错了！”\r\n\r\n    老医师有些诧异，原本要训斥的话语，因对方如此痛快的认错，有些说不下去了。\r\n\r\n    王宝乐看到老医师的表情，松了口气，又有一些得意，暗道这高官自传，的确有用啊。\r\n\r\n    老医师哼了一声，抬头扫向整个食馆所有学子，右手一指众人。\r\n\r\n    “还有你们，你们都是我缥缈道院未来的学子啊，看看你们这些天是什么样子，你们要永远记得，我辈武者，当先立身，再立言，而后立行！”\r\n\r\n    老医师深恶痛绝，这番话语回荡食馆，让所有人听到后，都不由低头，有些惭愧，而王宝乐这里，则眼睛猛地一亮，觉得表现自己的时候到了。\r\n\r\n    于是飞速从怀里拿出一个小本，在上面记录起来，不时抬头看向老医师，露出聆听的表情，还时而认真的点头，仿佛要记住对方说的每一个字，这一招，也是他在高官自传上总结出的杀手锏。\r\n\r\n    其他同学看到这一幕，如看神人一般望着王宝乐。\r\n\r\n    老医师在看到后，也瞬间呆了一下，一种前所未有的怪异之感，不由浮上心头，实在是他迎来送往这么多届学生，这还是第一次遇到如此奇葩之人，不由多看了几眼，但却渐渐冷笑起来。\r\n\r\n    “小家伙，老夫的马屁可不是那么好拍的，你要感谢这雷磁暴，不然的话，老夫能一口气训上三天三夜，我看你能不能都写在小本上！”\r\n\r\n    老医师话语一出，气势顿时不同，一股强者的气息，从其身上散出，好似化作威压，笼罩整个食馆，众学子无不惊呆，只觉着这场斗法，还是老医师道高一尺。\r\n\r\n    王宝乐眨了眨眼，摸了摸口袋里的录音玉简，权衡了好久，又看了看不断逼近的雷磁暴，还是放弃了拿出的念头，他觉得在上司面前怂，不丢人。\r\n\r\n    眼看众人神情，老医师内心得意，又慷慨激昂的说了几句。\r\n\r\n    “你们记得，要把心思放在修炼上，日后做人，不能贪婪，不能无义，更别总想着找什么女伴，须知色字头上一把刀，这些天，你们卿卿我我的实在太不像话！”\r\n\r\n    正说到这里，突然的，一个娇滴滴的声音，从老医师的传音镯内飘出。\r\n\r\n    “医师老爷爷，你在哪里啊，人家不会系这个磁灵服，你快来帮帮我。”\r\n\r\n    这声音娇媚，老医师一听，身子不由自主的抖了一下，勉强干咳一声，严肃的瞪了一眼众人，这才心急火燎的转身离去，同时冲着传灵镯激动的开口。\r\n\r\n    “喔，来了来了，小宝贝不急，我这就过来帮你。”\r\n\r\n    这一幕逆转太快，反差太大，众人全部傻眼，就连王宝乐也都愣了一下，好半晌才倒吸口气，随着众人忍不住的哗然，王宝乐咬牙切齿，也骂了几句。\r\n\r\n    “无耻！这老色鬼，之前说的那些都是屁话！女同学就只有这么多，我们都不够分，他这么大把年纪，还来和我们抢资源，不就凭着大小是个官么！”王宝乐越想越气愤，与四周同学议论中，心底对自己当官的梦想，更加坚定了。\r\n\r\n    随着警报声的加剧，众人这才结束了议论，一个个心底愤愤的去了修灵室。\r\n\r\n    修灵室，处于飞艇核心区域，顾名思义，是给这些学子修炼的场所，同时也是飞艇在路过特殊区域的过程中，保护最严密的地方。\r\n\r\n    此刻在这修灵室内，随着众人汇聚，在缥缈道院随船的老师安排下，所有人端坐数排，穿上缥缈道院发放的飞艇专用磁灵服。\r\n\r\n    刚刚穿好磁灵服，王宝乐正沉浸在对卢医师的不忿中，随意抬头看了看四周，本就郁闷的情绪，因远处一道目光，顿时更为恶劣，不自觉的眉头皱起，露出嫌弃的样子。\r\n\r\n    他目光之处，坐着一个双腿修长的女生，很是动人，可眼下也是蹙眉，露出厌恶的模样，显然在他们相互的目光里，对方都是熟悉且极为厌恶的。\r\n\r\n    “晦气！”二人都心底嘀咕，赶紧避开目光，仿佛就连彼此看一眼，都觉得辣眼睛。\r\n\r\n    “在哪里都能遇到这个平板杜敏，烦死了！”王宝乐嘀咕一声，实在是他与这个叫做杜敏的女生，从小到大，都是在一个班级里，尤其对方平日里趾高气扬，凭着班长身份，不断地刁难自己，当年那两块糖，就是送她的……\r\n\r\n    “有什么了不起的，不就是个破班长么，等到了道院，凭着我的官场杀手锏，老子也能混个一官半职！”王宝乐哼了一声。\r\n\r\n    很快的，在众人都穿戴完毕，缥缈道院随船的老师一一检查后，他们交代了一番注意事项，更是严厉告诫众人，飞艇进入雷磁区后，存在危险，有一定可能出现生死危机。\r\n\r\n    待看到众人都神色变化，这些随船的老师才肃然的离去，修灵室的大门，也直接密封起来，灯光也渐渐暗下。\r\n\r\n    此刻的修灵室，虽还是有低声交谈，可随着时间的流逝，紧张感越发的强烈，渐渐无人说话，陷入彻底的安静。\r\n\r\n    在这仿佛可以听到自己心跳的寂静中，哪怕还处于不忿中的王宝乐，也都不由得紧张起来，直至半柱香后，整个飞艇猛地一震，进入雷磁区域！\r\n\r\n    从外面看去，整个雷磁黑云磅礴无比，好似一张大口，将与其比较，很是渺小的热气球飞艇，直接吞噬。\r\n\r\n    这种气象，似蕴含了毁灭之力，能横扫一切，或许只有苍穹上，那一轮触目惊心的剑阳，才可以无视所有，仿佛俯视人间三十七年不够，还要更久。\r\n\r\n    这一年，是灵元三十七年。\r\n\r\n    公元三零二九年，地球科技发展飞速，没有了国界，实现了地球大一统，进入了联邦时代，也正是在这个时候，一把大剑从星空飞来，穿透太阳，世界轰动。\r\n\r\n    这大剑的剑柄，或许是因本就残破，在这剧烈的震动中破裂大量碎片，洒遍星空，其中有一部分落在了地球各地。\r\n\r\n    随着青铜大剑的到来，随着碎片的落下，地球上突然多出了一种似乎弥漫天地间，源源不绝的新能源，后被命名为……灵气！\r\n\r\n    灵气如空气，有的地方浓郁，有的地方稀薄，又因那些洒落的碎片被联邦以及各方势力获得，在上面找到了有关修炼以及炼器，炼丹，还有炼灵石的种种功法，其上文字充满古意，导致人们接触古文，成为潮流。\r\n\r\n    而灵气的出现，也迅速淘汰了原有的能源，改变了人们的生活，不但形成了灵网，也改变了地球文明的进程，使得整个地球，开启了修行文明。\r\n\r\n    从此古武兴起，世界大变革，全民修仙的时代，因而开启。\r\n\r\n    史称，灵元纪。\r\n\r\n    而此刻，在雷磁黑云内，那艘缓缓行驶的红色热气球飞艇，其四周闪电无数，不断地轰击而来，好在有柔和光幕扩散防护左右，使得飞艇安稳无比。\r\n\r\n    至于船舱核心区域的修灵室内，此刻所有学子包括王宝乐在内，都已不知不觉的沉睡，好似有一股奇异的力量，引导他们进入梦境。\r\n\r\n    唯独在船首的主阁中，此刻有七八个老师，有的喝茶，有的含笑，正相互轻松的交谈，与他们之前吓唬学生们的样子，截然不同。\r\n\r\n    为首之人是个老者，满脸皱纹，拿着烟枪，正一口口抽着，若是王宝乐在这里，必定一眼认出，这老者，正是之前无耻的卢老医师。\r\n\r\n    “掌院，已经都准备好了，咱们缥缈道院这一届的分区试炼，是不是可以开始了？”随着一位中年老师的开口，那抽着烟的老医师，微微一笑。\r\n\r\n    “开始！”  ', 1, 1, '2018-10-12'),
	(2, '王宝乐，你干了什么！', '午夜，月明星稀。\r\n\r\n    依旧是池云雨林，只不过夜晚的这里多了一些阴凉，偶尔还能看到一些雨水汇聚的河流，于月光下明媚，但时而传来的鸟兽戾鸣，却让人忍不住升起不安。\r\n\r\n    此刻，在这雨林的一角，一处河流旁，月光下能看到两个虽有狼狈，可却清纯的少女，高挑可爱，春兰秋菊。其中那高挑之女紧张警惕左右，至于可爱娇娥则解下内衣，露出雪白的肌肤，正蹙眉清洗腋下的擦伤，眉目中带着迷茫，轻声低语。\r\n\r\n    “杜敏，都三天了，也不知道什么时候救援的人能来，咱们营地里的食物也快不够了。”\r\n\r\n    被唤做杜敏的高挑女生，闻言沉默，对于她们来说，这三天整个人生都转变了，三天前还是缥缈道院的学子，三天后却失陷在了此地，到处隐藏着危机。\r\n\r\n    这池云雨林看似美好，可实际上地面潮湿腐烂，时而露出兽骨，还有很多一尺多长的蜈蚣以及花花绿绿的小蛇出没，让人头皮发麻。除此之外，更有灵元纪以来，与人类一样飞速蜕变的各种凶兽，力大无穷，极为凶残，使得荒野成为了人们的禁区。\r\n\r\n    只是此刻，在二女苦涩时，于她们不远的一颗大树下，正有一个小胖子，正满是不忿的站在那里，抬头尿尿。\r\n\r\n    这小胖子正是王宝乐，他没有注意到正在清洗伤口的二女，也没有去看脚下地面上的一朵原本亭亭玉立般的小花，正在被水流压制的凌乱摇晃……\r\n\r\n    “该死的，我王宝乐枉称察言观色，能看透世间万般人心……居然没有想到，竟被缥缈道院给算计了！”\r\n\r\n    “这缥缈道院太贼了，演的跟真的一样，为了让我们相信，居然让所有人都看到飞船爆开！”王宝乐心底愤愤，实在是这三天，对他而言也是惊魂不已。\r\n\r\n    三天前，他与同学们在修灵室内不知不觉睡着，被一声巨大的轰鸣惊醒，来不及思索太多，身体就被一股冲击力直接推出飞艇，好在修灵服本身就有缓冲与避雷的作用，这才勉强落在了雨林内，可却亲眼目睹飞艇在雷磁暴中崩溃爆开。\r\n\r\n    之后的三天，王宝乐与众人分散在丛林中，食物的缺少，野兽的凶残，对未来的迷茫与恐惧，使得所有学子在这大变下，都或多或少的露出了一些性格里的本性，有人抱团，有人独行，有人果断，有人懦弱。\r\n\r\n    虽说弱肉强食这项法则，对于他们这些刚刚考入缥缈道院的学子而言，太突然了，可在这大变下，仿佛萌芽被激发，无论是贪婪还是凶残，无论是无私还是善良，都被凭空放大。\r\n\r\n    “无耻！”王宝乐心底嘀咕了一句，他三天前还真的以为这一切都是真实的，吓得他哪怕又遇到了死对头杜敏，也都强忍着，留在了对方所在的营地里。\r\n\r\n    直至如今三天过去，在吃不饱的状态下，他通过自己传音戒的体测功能，发现自己的体重竟然奇迹般的掉了六七斤的样子后，他震惊中狐疑起来。\r\n\r\n    实在是王宝乐的经历与众不同，他曾经为了减肥，一个个月几乎不吃不喝，疯狂运动，可也不知怎么回事，体重不但没减少，反而增加了三斤！\r\n\r\n    如今三天的时间，就掉了六七斤，这对他而言，根本就不可能！\r\n\r\n    尤其是他回忆起自己曾经在一些高官的自传里，看到有人唏嘘道院生涯时，曾隐晦的提起，似乎道院每隔一段时间，都会有所谓的新生考核。\r\n\r\n    若不是王宝乐钻研的彻底，也很难注意到这一点，如今这么一联想，眼前这一切，他虽不知缥缈道院如何做到如此逼真，但极有可能是百密一疏，以正常人的代谢来作为构建标准，而自己显然不是正常人……\r\n\r\n    他有五分把握，眼前这一切，应该是虚幻的。\r\n\r\n    而让他真正完全确定自己判断的证据，则是怀里那曾经被老医师退回来的……半块黑色面具！\r\n\r\n    想到这里，正在尿尿的王宝乐，不由得低头看了看自己的胸口，心底升起一丝说不出的怪异感。\r\n\r\n    实在是他清楚地记得在踏入修灵室前，他把这半块黑色面具随意的放在了怀里，之后遇险，也没空去理会，直至不久前，他无意中发现这面具虽看似如常，可实际上竟然伸手就能穿透，仿佛永远无法触及。\r\n\r\n    就好似这可以模拟万物的虚幻世界，难以解析其结构，重塑实质。\r\n\r\n    甚至随着时间的流逝，就连其外在形状，也都开始虚幻起来，同时浮现出一些模糊看不清的字迹。\r\n\r\n    虽难以看清，可这面具的变化，让王宝乐原本五成的把握，变成了十成！\r\n\r\n    而按照这样的思路去分析，如果这是一场以虚假的灾难形成的考核，就不难猜出其考核的方向。\r\n\r\n    “不可能是看谁强谁弱，毕竟大家都还没接触古武，那么这一次考核的目的，就只能是考察危机时刻的心性，或许还有考察对道院的信心？”王宝乐一边尿尿，一边脑子不断地转动，不时还打几个尿颤。\r\n\r\n    他面前的小花，越发凌乱时，王宝乐呼吸微微有些急促，他觉得自己很有必要抓住眼下这个机会，去给自己加分。\r\n\r\n    “就这么干了！”想到这里，王宝乐用力的抖了抖，刚要提上裤子，可忽然的，他看到了不远处的小河。\r\n\r\n    月光下，杜敏虽然站在那里，可王宝乐的目光里已经没有了她，只有一个正在清洗伤口的可爱少女，以及其胸前那亭亭玉立的……小白兔。\r\n\r\n    “居然有人形凶兽！”王宝乐眼睛猛地睁大，倒吸口气，心跳加速，可刚看了一眼，正在警惕四周的杜敏，似有所察觉，看去时，与王宝乐目光不由得就对望在了一起，一愣之后，神色大变，可还没等她尖叫，王宝乐就眼睛一瞪，一边提起裤子，一边抢先大吼。\r\n\r\n    “你看什么看，没见过男人尿尿啊！”\r\n\r\n    他话语一出，杜敏只觉得自己所有的话，都被王宝乐这一句，全部噎了回去，气的浑身发抖，她长这么大，只见过王宝乐一个如此厚颜无耻之人，不由得怒骂起来。\r\n\r\n    “死胖子，你也算男人！”\r\n\r\n    这句话一出，王宝乐险些气胖了一圈，他从小到大与这嘴毒的杜敏都是死对头，相互看对方不顺眼，偏偏二人又都在一个班级，如今又一起考入到了缥缈道院，此刻王宝乐深吸口气，哼了一声。\r\n\r\n    “死平板，胸都没我大，你也算女人！”\r\n\r\n    杜敏一听这话，差点喷出一口鲜血，额头青筋鼓起，正要冲过来时，王宝乐长叹一声。\r\n\r\n    “我这清白身躯，被你们看的清清楚楚，我以后怎么做人啊。”他一脸生无可恋，提着裤子，转身就跑，心底则是怦怦加速跳动，背后全是冷汗，暗道还好自己反应快，不然就危险了。\r\n\r\n    眼看王宝乐要逃，杜敏杏目怒张，飞速追来，其后小河里的可爱娇娥，则是在听到了二人的恶毒对骂后，一脸茫然，浑然不觉自己被王宝乐占了便宜，看到杜敏追出，她这才穿上衣服，脸红的迅速追来。\r\n\r\n    此刻随着杜敏的怒斥声，雨林的平静被打破，前方丛林内他们这一处临时营地的方向，立刻就有人群闻声快速赶来，堵住了王宝乐的路。\r\n\r\n    当首之人，是一个穿着白衣的青年，这青年拿着火把，身体高大，剑眉星目，于人群中很是显眼，四周更有不少学子将其簇拥，显然是以他为首。\r\n\r\n    此人正是王宝乐所在的这一处抱团的营地内，于这三天里，团结众人，展现出个人魅力的柳道斌。\r\n\r\n    “王宝乐，你干了什么！”柳道斌一眼就看到杜敏二女从远处带着怒气追来，而被她们追着的王宝乐，则是边走边系裤子……这诡异的一幕，让柳道斌愣了一下，他对杜敏早就有心思，此刻本能的对王宝乐就厌恶起来。\r\n\r\n    “我只是尿了个尿……”王宝乐话语还没等说完，忽然的从远处正跑来的杜敏二人那里，传出一声强烈的尖叫声。\r\n\r\n    在这声音出现的刹那，一股腥气眨眼间就弥漫此地，更有沙沙的声音好似潮水一般，急速如风暴的扩散开来。\r\n\r\n    王宝乐猛地转头，与此同时柳道斌以及其他学子神色一变，他们的目中看到在远处杜敏以及可爱少女的四周，丛林的地面上，树枝上，竟在这一刻，涌现出了数不清的蛇！\r\n\r\n    那些蛇花花绿绿，看起来充满毒性，且数量实在太多，远远一看如同蛇海，将杜敏二人死死的围困在内。\r\n\r\n    看着四周有那些数不清的毒蛇，二女神色大变，尤其是它们已然张开了满是毒牙的蛇口，毒液流下，发出嘶嘶的声音，腥气令人作呕。\r\n\r\n    柳道斌身体一震，没工夫理会王宝乐，直接就冲向杜敏，其后也有一些学子，眼睛赤红，飞快上前要去救援。\r\n\r\n    这一切发生的太快，就在众人欲上前救助的瞬间，突然的，远处的丛林地面上，传来一声震慑心神的婴啼，有一条手臂粗细的红线，哪怕是在黑夜，也依旧清晰无比，展开惊人的速度，正直奔此地。\r\n\r\n    时而飞跃出的身体，露出苍白的头颅，那已经不像是蛇头了，分明就是一个婴儿的面孔，只是目中露出的是让所有人都内心咯噔的狂暴。\r\n\r\n    “红骨白婴蛇！！”学子中有人认出，心神惶乱，失声惊呼，齐齐后退。\r\n\r\n    柳道斌更是头皮要炸开，汗毛耸立，心底狂颤，实在是这红骨白婴蛇名气太大，列入联邦灵元纪的千凶之列，此蛇身体虽脆弱，速度也并非极致，可它的毒性之大，沾染一丝就会瞬间化作血水，只留下一具红色的骨头，因而得名。\r\n\r\n    哪怕他心底爱慕杜敏，可若因此丧命，他本能觉得得不偿失，下意识后退，远远避开，生怕那红骨白婴蛇杀的兴起，将他们也一同葬身其口。\r\n\r\n    王宝乐这里，看到这一幕后，先吸了口气，但随即立刻意识到这一切实际上都是虚假的，顿时就轻松起来，眼睛一亮，暗道自己在老师面前表现的时机，出现了。\r\n\r\n    “反正都是假的，老子怕个鸟。”想到这里，王宝乐顿时挺起胸膛，望着那些逃回来的同学，目中露出深深的鄙视。\r\n\r\n    “虽然杜敏这个平板妹牙尖嘴利，长得又难看，总是利用职权刁难我，可我王宝乐是一个高尚的人，一个正直的人，一个不害怕牺牲的人，一个脱离了庸俗的人，一个有益于同学的人！”\r\n\r\n    “如此危险的环境，我王宝乐岂能后退，别人怕死不敢去，但……为了我的同学，我敢！”\r\n\r\n    这无耻的胖子，此刻都快被自己的言辞所感动，难道他真的忘了这里是虚假的世界了么？可他偏偏好像自己都忘记的样子，在那里陶醉起来，仿佛只有这里是真实的，才可以配得上他的英勇。\r\n\r\n    “板儿，今天就让你看看，什么叫男人！”\r\n\r\n    在这众人因恐惧倒退的瞬间，王宝乐非但没有后退，反倒是发出了一声大吼，他抿着嘴唇，抬起下巴，仿佛这一刻那圆圆的小脸，有一种如刀刻般的锐利，充满了男性的气息，龙行虎步，势如破竹一般冲入蛇群。\r\n\r\n    气势如虹，威武非凡的身影，好似代表了正义，直奔杜敏二女而去！\r\n\r\n    这一幕，顿时就让杜敏呆了一下，哪怕身处蛇群，也依旧有种强烈的不适应，但她身边的可爱娇娥，已经忍不住激动起来。\r\n\r\n    至于其他人，也都被王宝乐的大吼以及气势震慑，眼看着王宝乐在那红骨白婴蛇靠近杜敏二女的一瞬，蓦然临近，仿佛天神降临，一把抓住那人人敬畏的红骨白婴蛇，狠狠的扔向远处。\r\n\r\n    这一刻的他，浑身上下散发出威武霸气，如同圣人附体，一身正气散及八方，随后他没有半点停顿，一把抱住了激动的可爱娇娥，又一把将愣着的杜敏夹在腋下，直奔人群飞奔而回。\r\n\r\n    只是这四周蛇群太多，他在来回的过程里，屁股上还是被咬了好几口，当赶回来时，他的面色已经发黑，可咬牙支撑，直至将杜敏二女安全送回，这才脚下踉跄，失去了力量，倒了下来。\r\n\r\n    “好像有点冲动了……屁股好痛，证明真男人好辛苦啊。”王宝乐心底哀忿，眼看杜敏此刻依旧是傻了一样的望着自己，至于可爱娇娥则是双目都带着异样与感激，还有四周众人那一个个如见了鬼一般的神情，他虽眼皮有些沉，可心底还是升起一些得意。\r\n\r\n    只是此刻屁股的伤口由痛转麻，王宝乐赶紧一把抓住杜敏的小手。\r\n\r\n    “杜敏，我对你有救命之恩，我现在已经感觉不到屁股的存在了，我听说蛇毒如果被吸出来，是可以得救的，你帮帮我……”没等说完，王宝乐实在忍不住眩晕，脑袋一歪，眼看就要枕在杜敏的胸前，可他突然意识到了什么，强向改变方向，落在了可爱娇娥的小白兔上。\r\n\r\n    眼看这一幕，四周众人都神色古怪，杜敏更是在看到王宝乐就连昏迷，也都露出那嫌弃的样子，面色顿时黑了。\r\n\r\n    此时此刻，在联邦境内，远离池云雨林，距离缥缈道院越来越近的天空上，一艘红色的飞艇中，修灵室内，数百学子安静的沉睡其中，王宝乐也在里面，似有美梦，歪着脑袋，嘴角带着享受的笑容。\r\n\r\n    而在飞艇的主阁里，包括老医师在内，所有的老师，一个个都目瞪口呆，睁大眼睛，看着他们面前浮现的诸多水晶画面里的其中一个。\r\n\r\n    那画面内，正是池云雨林内，救下了杜敏后，昏迷的王宝乐。\r\n\r\n    “这小胖子叫什么名字！”\r\n\r\n    “虽是梦境迷阵，可却与真实完全一样，他在里面的表现，就是他真正的心性！”\r\n\r\n    “如此勇敢，如此为了救同学的无畏之意，这孩子是个百年难遇的好苗子啊！是我们道院最渴望获得的优秀学子！！”\r\n\r\n    好半晌，众老师纷纷惊呼，看向王宝乐时纷纷赞赏不已，更有一些老师已经心动，在考虑要不要直接将王宝乐拉拢过来，加入自己的学系内。\r\n\r\n    就连缥缈道院的掌院，那位老医师，也都有些傻眼，他心底迟疑，隐隐觉得有些不对头。\r\n\r\n    “难道我真的看走了眼？”沉吟间，他索性从学子档案里取出了王宝乐的那一份，低头看了起来。\r\n\r\n    -----------\r\n\r\n    又是5000字大章！\r\n\r\n    陪伴是最长情的告白，诸位大大们，能否守护三寸人间，让她从檐上的三寸雪，成为人间惊鸿客！\r\n', 1, 1, '2018-10-13'),
	(3, '书生孟浩', '<p>赵国是一个小国，如这南赡大地的其他小国一样，向往东土，向往大唐，向往长安，这是国主的倾慕，也是赵国文生的理想，一如都城内的唐楼，与皇宫齐高，似可以遥望千山万水外的东土长安。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;四月的季节，说不出冷，也自然没有难熬的热，轻微的风抚过大地，掠过了北漠羌笛，吹过了东土大唐，掀起一些尘土如雾，在黄昏的夕阳下，转了个弯儿，卷在南域边缘赵国的大青山，落在了此刻于这青山顶端，坐在那里的一个文生少年身上。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;少年有些瘦弱，手中拿着一个葫芦，穿着一身干净的蓝色文士长衫，看起来约莫十六七岁的样子，个字不高，皮肤有些黑，但清澈的双眼带着一抹聪颖，只是此刻皱起的眉头，使得聪颖内敛，神色中多了一抹茫然。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“又落榜了……”少年叹了口气，他叫孟浩，是这大青山下云杰县一个普通书生，早年双亲突然失踪，留下家财本就不殷，这几年读书不菲，到了如今已贫贫如洗。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“考了三年，这三年来整日看那些贤者书籍，已看的几欲作呕，莫非科举真的不是我孟浩未来的路？”孟浩自嘲，低头看了一眼手中的葫芦，神色有些黯淡。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“当官发财做个有钱人的理想已经越来越远，更不用说有钱之后去东土大唐……百无一用是书生。”孟浩苦笑，坐在这安静的山顶，看着手中的葫芦，神色的茫然越来越深，那茫然里带着对未来的恐惧，对自己人生的迷茫，不知晓自己以后能做些什么，也不知晓未来的路在哪里。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;会不会有个贵人看好了自己，是否还有个深闺小姐突然中意自己，又或者若干年后，自己还在不断的科举。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;这些问题没有答案，对一个十六七岁的少年来说，这样的迷茫，仿佛化作了一张噬人的大口，将他无形的吞噬，让他有些害怕。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“哪怕是县城里的教习先生，每月也只有几钱银子，甚至不如王伯的木匠铺子赚钱，早知如此头些年不去读书，和王老伯去学木匠手艺，想来日后总算能解决温饱，好过如今一无所有。”孟浩沉默。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“家里已经没有多少粮食了，银两也都花的所剩无几，还欠了周员外三两银子，以后……怎么办。”孟浩抬起头，看着天空，喃喃低语，天空很蓝，很大，遥远看不到尽头，仿佛如他看不到未来。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;许久之后，孟浩摇了摇头，从怀里取出一张纸条，认真的看了看，将纸条放进了葫芦里，站起身用力将葫芦扔下青山。<br>&nbsp;&nbsp;&nbsp;&nbsp;青山下有一条大河，河水寒冬不冻，传说通往东土大唐。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;山顶的孟浩，默默的看着山下大河中渐渐飘远的葫芦，没有眨眼，仿佛看到了自己的娘亲，看到了儿时的欢乐，那葫芦里带着他的理想，带着他对未来波澜壮阔的憧憬，越飘越远，不知未来的某日，会有谁捡到这个葫芦，看到里面的纸条。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;直至过去了数十息的时间，孟浩才收回了目光，将神色中的茫然隐藏起来，深深的呼吸了一口山顶的气息，目中露出坚定。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“不管如何，读书也好，做工也罢，总要……生活下去。”孟浩的性格本就这样，聪颖中带着坚强，若非如此，早年双亲外出后他也不可能一个人独自活到现在。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;孟浩抬头看了一眼天空，目中坚定之意更深，就要向山下走去。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;就在这时，忽然从那山崖下传来了微弱的声音，那声音似要被风吹散，落入孟浩耳中时微弱仿佛难以察觉。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“救命……救命……”<br></p>', 1, 2, '2019-03-12'),
	(4, '靠山宗', '<p>靠山宗，位于南赡大地南域边缘的赵国境内，曾是赵国四大宗门之首，就算是整个南域也都颇有其名，可如今没落，地位不比从前，也没有了曾经的辉煌，现今于赵国，只能算是末流。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;实际上靠山宗原本也不是叫这个名字，只不过在千年前出了一位轰动整个南域的修士，此人自号靠山老祖，更是强行将宗门之名改为靠山宗，横行霸道，几乎搜刮了赵国所有宗门之宝，风光一时无两。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;可如今千年岁月，物是人非，靠山老祖已失踪四百余年，若非生死未知，恐有后患，怕是早已被其他宗门吞并，现在的靠山宗已曰落西山，再加上赵国资源有限，被其他三宗排挤的就算是要招收杂役，也都需弟子外出绑来，更不用说光明正大的开宗广收门徒了。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;随着绿袍男子身后，孟浩行走在靠山宗的山峦小路，四周的一切如桃园之境，奇石怪树比比皆是，青山绿水间，一处处云雾内的奢华阁楼，仿佛瓦片都是用玉石铺成，看的孟浩感叹连连，只是身边那小胖子哭丧了一路，让孟浩觉得有些坏了此地的意境。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“完了完了，这次真的完了……”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“我要回家，家里有大馒头，家里有鱼肉……”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“该死的，该死的，我以后应该是继承家里的祖田，成为老爷，有个几房小妾，我不应该在这里做杂役……”那小胖子一边哆嗦，一边哭丧着脸喃喃低语，眼泪在眼圈里。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;他嘀咕了一路，直至过了半盏茶的时间，走在前方的绿袍男子传来冷漠的声音。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“再说一句废话，直接割了你的舌头。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;小胖子身子猛地哆嗦了一下，双眼露出强烈的恐惧，连忙把自己的嘴捂住，身子颤抖的越加剧烈。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;这一幕落在孟浩眼中，让他忽然觉得似乎事情不是自己想的那么美好，但他姓格坚毅，此刻深吸口气，倒也没有多言。<br>&nbsp;&nbsp;&nbsp;&nbsp;时间不长，在此山的半山腰处，孟浩看到了前方云雾翻滚间，露出了几排平屋，有七八个少年穿着一样的粗麻长衫，一个个疲惫的坐在屋舍外，也注意到了孟浩几人，但大都没有理睬。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;不远处，一块山石上，坐着一个穿着浅蓝色长衫的青年，此人面孔略长，如马脸般，衣衫则明显比那些少年华贵一些，神色带着冷淡，可在看到带着孟浩走来的绿袍男子后，他站起身恭恭敬敬的向着绿袍男子抱拳一拜。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“拜见师兄。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“这两人是新上山的杂役，你交代一下安排住处。”绿袍男子一脸不耐，说完转身看都不看孟浩二人，迈步离去。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;待他离去之后，马脸青年重新盘膝坐下，冷淡的扫了孟浩与小胖子一眼。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“这里是北区杂役处，靠山宗不养废物，你二人既来到了此地，做半甲子杂役，满期之曰方可下山，若中途逃走，荒山多兽，十死无生。领取杂役衫，从此之后就隔绝了凡尘，安心做个杂役。”马脸青年看着孟浩二人，冷淡开口。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;小胖子身子更为哆嗦，目中露出绝望，孟浩神色则显得很是镇定，目中隐藏着莫名的神采，使得马脸青年多注意了孟浩一眼，他在此地多年，见过不少被&nbsp; 抓来做杂役的少年，如眼前孟浩这样镇定的，着实不多。<br></p><br>&nbsp;&nbsp;&nbsp;&nbsp;“心姓不错，你等杂役并非必须满半甲子，平曰里也可修行，若能达到凝气一层，便不再是杂役，可晋升成为外宗弟子。”马脸青年淡淡开口，大袖一甩，立刻在孟浩与小胖子的面前，各自出现了一件粗麻长衫，上面放着拇指大小的木牌，刻着“杂”字。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;除此之外，还有一本小册，上面写着三个小字。<br>&nbsp;&nbsp;&nbsp;&nbsp;凝气卷。<br>&nbsp;&nbsp;&nbsp;&nbsp;这三个小字落入孟浩眼中，立刻让孟浩呼吸都有些急促起来，他死死的盯着那个小册子，脑海中瞬间浮现的，是之前那绿袍男子对冷面女子恭维的话语中提到的凝气七层。<br>&nbsp;&nbsp;&nbsp;&nbsp;“凝气一层可以成为外宗弟子，那抓我来的女人，她是凝气七层……什么是凝气，莫非这就是传说中的修仙之法。<br>&nbsp;&nbsp;&nbsp;&nbsp;这应该算是做杂役的工钱了吧，这等工钱虽说不是银两，但若能拿出去，必可卖到百金！”孟浩怦然心动，一把抓起长衫，将木牌与那小册子包裹在内。<br>&nbsp;&nbsp;&nbsp;&nbsp;“西首七房，是你二人的住处，从明天开始，你二人的工作是砍柴，每曰每人十木，做不到就不要吃饭了。”马脸青年说完，闭上了眼。<br>&nbsp;&nbsp;&nbsp;&nbsp;孟浩深吸口气，学着对方之前的样子，抱拳一拜后拉着小胖子，连忙向着屋舍处走去，此地仿佛是一个扩大了很多倍的四合院，按照一间间平屋外的门牌，找到了西首七房，推门后迈入进去。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;房间不大，除了两张小床外还有一张桌子，尽管简单，可却很是干净，在一张床上坐下后，那小胖子此刻再也坚持不住，哇哇大哭起来。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;他也就是十二三岁的年纪，此刻哭起来声音却极为响亮，就算在外面仿佛也可听到回音。<br>&nbsp;&nbsp;&nbsp;&nbsp;“我爹是财主，我应该也是财主，我不做杂役……”小胖子哭的极为伤心，身子哆嗦时肥肉也随着颤抖。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“别哭了，其实你想想这里也是不错的，我们这是给仙人打工，说出去得有多少人羡慕我们。”孟浩连忙去把房门关上，安慰起来。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“我不想打工，我家有钱，而且……我亲事都说完了，聘礼都给过去了，可怜我那未过门的貌美小娘子，这不是要守活寡么……”小胖子越哭越是伤心。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;孟浩神色有些古怪，暗道这小胖子人不大，居然都说了亲，自己这么大年纪，如今连女人手都没摸过，不由的感慨还是有钱好啊，这小胖子家里家财万贯，衣食无忧，而自己一穷二白，祖屋去年都卖掉，如今还欠下周员外一屁股债。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;不过想到欠债，孟浩乐了，自忖自己都来到这里了，周员外要是有本事就来找自己，若是没本事等自己出去后已是半甲子，估计对方……已经没了。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;想着想着，孟浩更觉得这里不错，不用花钱就可以解决温饱，甚至还没干活就得价值百金的工钱，尤其这里是仙人居住的地方，更是让孟浩觉得来到此地，对自己而言可以说是绝处逢生。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;被小胖子哭的心烦，孟浩索姓拿起粗麻衫内的小册子，盘膝坐在床上翻看起来，打开第一页，看完第一句话，孟浩有些目瞪口呆。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“人当有靠山，凡\n\n人若有一生富贵，修士若有一生无忧，入我靠山宗，老夫就是靠山。”这就是小册子里的开卷语，落款是靠山老祖。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;寥寥数十字，可却透出一股难言的霸气，更有赤裸裸的寻靠山的言论，让孟浩愣了一下，忽然觉得这句话大有道理。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“靠山宗，莫非这就是靠山宗的真意，做人要寻找靠山，找到靠山后一生富贵无忧。”孟浩越想越觉得这句话非常正确，想到自己若是有个大官做靠山，岂能连续落榜三次。感慨时对这从未见过的靠山老祖，隐隐有了尊敬，他觉得人生的某一扇大门，此刻随着这句话，已经缓缓打开。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“也就是说，我在这靠山宗，要用各种方法为自己寻找靠山，如此一来才可以在这里无忧。”孟浩双眼越来越亮，继续翻看手中的小册子，忘记了时间，忘记了身边小胖子的哭嚎。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;直至夜深，小胖子哭累了已经睡下，鼾声如雷回荡屋舍时，孟浩才恋恋不舍的合上了小册子，尽管神情有些疲惫，但他的目中神采却是极有神韵。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“这本书不是价值百金，这是价值千金！”孟浩喃喃自语，一生梦想当官发财成为有钱人的他，对于某种事物的评价若是能到千金，那么这件事物对他而言，已经是提升到了仅次于生命的地步。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;正激动中，小胖子的呼噜声嘎然而至，孟浩一愣看去时，看到小胖子闭着眼睛，居然从床上坐了起来，双手乱打，口中念念有词。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“打死你，抢我的馒头，我咬死你，你敢抢我的娘子。”说着说着，小胖子闭眼起身下床，在这房间里虎虎生风的打了一圈，不小心碰到了桌子，可让孟浩睁大了眼睛的是，这小胖子居然一口狠狠的咬在了桌子角上，留下一个深深的牙印后，这才又回到了床上继续睡觉，呼噜声再次传出。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;孟浩看了小胖子半天，确定了此人有梦游的习惯后又看了一眼桌子角，隐隐觉得这小胖子睡觉时不可招惹，小心翼翼的挪远了一些，低头望着小册子，神色继续激动。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“凝气九层，仙灵之路，为仙人打工，给出可以成为仙人的机会，这就是最大的工钱，我就不信若是自己成了仙人，还做不成有钱人！”孟浩紧紧的抓住小册子，眼中露出强烈的光芒，他仿佛看到了自己除了读书之外的另一条路。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;就在这时，突然屋舍的房门砰的一声，被人一脚踹开，一声冷哼随之传入房间。<br>&nbsp;&nbsp;&nbsp;&nbsp;－－－－－－－－－－－－－－－－－－－－－<br><br>&nbsp;&nbsp;&nbsp;&nbsp;看到书评区知道子右君家的孩子满月，耳根在这里恭喜柚子，祝孩子健健康康，人中龙凤！<br><br>&nbsp;&nbsp;&nbsp;&nbsp;另外，耳根注意到有读者察觉到了第一章的葫芦……没错，那就是一个古代的漂流瓶……里面的纸条写的什么，嘿嘿，你们猜猜看，猜中奖龙套~~&nbsp;&nbsp;<br>', 1, 2, '2019-03-12'),
	(5, '好同学，一切有我', '<p>当王宝乐醒来的时候，在这梦境迷阵内，已经过去了一天的时间，蛇群的毒没有众人想象中那么剧烈，随行的同学中有人擅长治疗蛇毒，也就使得王宝乐的美好愿望落空。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;好在随着他的苏醒，名叫周小雅的小白兔对他照顾无微不至，杜敏也罕见的没有与他针锋相对，这就让王宝乐心底舒坦，心里又开始琢磨自己的救人表现，必然会被老师们看到，想来自己这一次考核，应该能加分不少。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;他唯独郁闷的，就是在之后的几天里，团队众人穿梭丛林，寻找其他同学的路上，柳道斌也不知道吃错了什么药，也许是因为之前的事情愧疚，所以一路上遇到一些小危机，总是抢着带人出手，迅速化解，使得本就虚弱的王宝乐，没有丝毫表现的机会。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;偏偏又没有出现如蛇群那般大的事件，这就让王宝乐觉得自己一身通天的本领，却没有用武之地，满是郁闷中，只能看着柳道斌在那里不断刷考核分。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“这柳道斌再这么下去，说不定隐藏的考核分，就比我高了！”到了最后，王宝乐都焦急了，不过这种情绪没有持续太久，第二天深夜时，在一处一线天的山体下扎营的他们，听到了一声声凄厉的狼嚎。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;那声音仿佛可以穿透山石，让所有人都耳骨刺痛，蓦然惊醒，纷纷望去时，立刻看到在他们的前方，那无尽的丛林内亮起了一双双血红色的眼睛。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;月光下数不尽的凶狼，成扇形包围而来，这些狼群有的在地面飞奔，有的则是跳跃在树枝上，口中发出的狼嚎，目中露出的嗜血，让人望之色变！<br><br>&nbsp;&nbsp;&nbsp;&nbsp;这一幕，仿佛形成了压抑的狂风，直接就让柳道斌等人面色大变，冷汗淋漓，头皮发麻。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“快跑，有狼群！”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“是幽骨狼，数不清的幽骨狼！”。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;一旁的杜敏在经历了蛇群事件后，仿佛一下子就成长了不少，立刻就高呼，让众人进入一线天，利用那里的山堑阻挡狼群。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;柳道斌脸色变幻不定，最后狠狠一咬牙，面对群狼，并没有立刻撤退，而是召唤同学阻挡拖延时间。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;小白兔慌乱中扶着王宝乐，身体虽发抖，可却拉着他随人群跑向一线天，只是王宝乐这里，此刻早已急了。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;在王宝乐看来，之前一些小分让给柳道斌也就罢了，现在好不容易遇到这么大的机会，他岂能让柳道斌抢走，眼睛里瞬间好似有圣火点燃，他身体一下子重新威武，脚步猛地一顿。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“小雅学妹，你先走！”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;王宝乐说完，径直朝着柳道斌那里飞奔，一把抓住柳道斌，在对方还楞怔时，直接就将其扔去一线天的方向，口中还大吼。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“兄弟，你先撤，这里有我！”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;柳道斌整个人彻底懵了，没等反应过来，就看到王宝乐义无反顾的冲向狼群。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“大家快走，我来掩护！”这一刻的王宝乐，那种正义与神圣，再次爆发出来，远处的小白兔看向王宝乐时，心神再次被颤动。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;也有一些男同学，本已经进入一线天，可被王宝乐这里鼓舞，热血上涌，纷纷掉头，正要追随他的脚步，可却被红着眼的王宝乐一脚一个，全部踹了回去。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“好兄弟们，你们先走！”王宝乐正义凛然，大吼一声，可心底却在警惕，生怕别人和他抢分！<br><br>&nbsp;&nbsp;&nbsp;&nbsp;在他看来，这些狼群，哪怕样子再狰狞，可却都是考核分啊。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;至于那些被他踹回一线天的学子们，此刻一个个身体狂震，目中露出感动，实在是在他们看来，这一刻的王宝乐，那舍身为人的身影，高大威猛，不由得，有那么几人，浑身热血沸腾，竟又冲了上去。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;王宝乐顿时急了，赶紧上去又一个个推了回去，他生怕那几人再上来，索性一咬牙，直接在一线天的入口处，抬起双手，按在了岩壁上，用自己的身体形成了一面人肉之墙，口中更是焦急狂吼。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“我身中蛇毒，注定跑不掉了，不要管我，你们快走。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;这句话王宝乐说的真情流露，让那几人身躯震颤不已，也正是在这个时候，狼群忽然加速冲来，成群成片，嘶嚎慑神，嗜血之意弥漫八方，疯狂扑临，直奔王宝乐。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;这一幕，顿时就让一线天内正在后撤的众人，身心俱为震撼。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“王宝乐，你快回来！”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“天啊，他为了不成为我们的累赘，去用血肉阻挡狼群啊！”小白兔，杜敏以及所有一线天内的众学子，无不强烈触动，只觉得这一刻的王宝乐，那圆圆的身躯好似一座雄伟的大山，成为他们记忆里永恒的画面。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;柳道斌同样被打动，呼吸急促，他之前原本还对王宝乐有些不忿，可如今这不忿彻底消散，留下的只是深深的震撼。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;实在是这一刻的王宝乐，他举起双手支撑岩壁，化作人墙的身影，好似擎天！<br><br>&nbsp;&nbsp;&nbsp;&nbsp;这个动作做完后，王宝乐自己都被感动了，他觉得自己如果是老师，看到这一切后，必定也都会深有触动。可他还想着加更多的分，于是暗中拍起了学院的马屁，决然开口。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“能考入缥缈道院，是我的荣幸，即便死在这里，可我王宝乐生是道院的人，死是道院的魂！”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;王宝乐心底得意，对自己这句话非常满意，不信那些老师们不动容！<br><br>&nbsp;&nbsp;&nbsp;&nbsp;只是这得意没有持续太久，想着在考核里表现加分的王宝乐，他不知觉的忽略了一点，那就是……痛！<br><br>&nbsp;&nbsp;&nbsp;&nbsp;此地虽是虚幻梦境形成，可痛觉却是与真实没有丝毫区别，随着那些狼群的呼啸临近，随着狼口的疯狂撕咬，眨眼间在远处众人的目中，王宝乐的身影就已经被十多条凶狼淹没。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“痛啊！”王宝乐全身一颤，呼吸粗重，眼前看到的都是狼口，闻到的都是血腥，而那狼牙撕咬在血肉中的剧烈疼痛，更是使得他险些忘记了这里是虚假。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;可他的内心坚定，没有半点退意！<br><br>&nbsp;&nbsp;&nbsp;&nbsp;哪怕剧痛极致，哪怕全身上下血肉模糊，很多地方见白骨，哪怕他的意识也都模糊，耳边传来的撕咬声，狼嚎声，交杂在一起，好似死亡的丧钟。不过王宝乐从小到大，虽有不少缺点，可也有一样极为显明的特质！<br><br>&nbsp;&nbsp;&nbsp;&nbsp;那就是……执着！<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“好不容易遇到这种大分，不能浪费，我要一次性，将考核分加到爆！”王宝乐内心咆哮，正要多坚持一会，可就在这时，忽然的从远处正在哭泣撤退的学子身后，丛林内，有一道红色的身影，以惊人的速度，迎风而来！<br><br>&nbsp;&nbsp;&nbsp;&nbsp;那红衣身影是个十七八岁的短发少年，面容俊朗，眉目中更有一抹寒意，他穿着红色的劲装，背着一把大弓，身体好似老猿一般在树木间飞跃，在来临的过程中更是拿下大弓，连珠一般骤然射箭。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;瞬间九箭！<br><br>&nbsp;&nbsp;&nbsp;&nbsp;箭矢速度太快，发出尖锐的破空声，飞跃众人，穿梭在一线天内，直接就从王宝乐的头顶，腋下等处，呼啸而过，在凄厉的狼叫下，射中了九只凶狼！<br><br>&nbsp;&nbsp;&nbsp;&nbsp;箭无虚发，其内蕴含的巨大冲击更是将那九只凶狼的身体，狠狠地抛出！<br><br>&nbsp;&nbsp;&nbsp;&nbsp;这突如其来的变故，使得那些正在后退的学子全部一愣，就连王宝乐也都呆了一下，实在是那九只箭几乎是从其身体贴着飞过，吓了他一大跳。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;还没等他反应过来，在众人的目中，那红衣少年速度更快，仿佛身体内有惊人的爆发力，此刻人随箭走，冲入一线天，一个跳跃在了王宝乐的头顶半空，再射九箭！<br><br>&nbsp;&nbsp;&nbsp;&nbsp;砰砰之声惊人，随着九只凶狼的惨叫，四周其他狼群也都受惊，本能的后退了一下，借助这个机会，那红衣少年身体落下，直接扛住王宝乐的身体，急速后退。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;王宝乐身体一个激灵，顾不得疼痛，此刻眼中都是那逐渐拉远距离的狼群，顿时急了。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“兄弟，放我下来，我还可以再坚持一会啊！”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;听到王宝乐的话，红衣少年哪怕性格冷漠，可也为之动容，实在是这一刻的王宝乐血肉模糊，仿佛快要支离破碎。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“你已经做得很好了，之后的事情，交给我了！”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;听到这句话，王宝乐更急了，他感觉对方似乎抢走了自己的台词，正要开口，可那少年深吸口气，右手猛地抬起，能看到其右手的肌肉，居然在这一瞬膨胀起来，直接就庞大了数圈，触目惊心中将其手中的大弓，狠狠地抽在一旁的岩壁上，速度飞快，一连抽去十多下。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;这力量实在太大，轰鸣中山石出现裂缝，直接就崩溃坍塌了小半，化作无数碎石脱落，轰隆隆中将这一线天生生堵死！<br><br>&nbsp;&nbsp;&nbsp;&nbsp;王宝乐眼睛瞪大，看了看少年那粗壮的手臂，将之前要开口的话语，全部收了回去。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;这一切发生太快，都是电光石火间，此刻随着一线天坍塌，红衣少年一晃之下，就扶着王宝乐回归人群。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;眼看这一幕，不远处的众学子，全部心神狂震，柳道斌更是倒吸口气，失声开口。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“古武境第二层，封身！”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“还没到封身，我只是第一层大圆满。”红衣少年看了柳道斌一眼，把王宝乐放下，解释了一句。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“没到封身，也已有封身之威，这位同学，多谢救命之恩！”柳道斌赶紧上前抱拳一拜，其他同学也都纷纷如此，甚至有不少女生，看向红衣少年时都露出了崇敬，一时之间，红衣少年被众人簇拥。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;至于王宝乐，此刻躺在地上，郁闷的看着这一切，他知道对方救自己是好意，可还是觉得加分的机会失去了，不过也明白此事没有办法。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“古武境啊……”王宝乐心底感慨，联邦进入灵元纪后，虽说修行的时代到来，可对于绝大多数的民众而言，也只是能学到一门叫做养气诀的初始功法。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;此法能让人吸收灵气延年益寿，同时更能以自身为媒介，凝聚出灵石出售，所以广泛传播。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;至于真正的修行，门槛还是很高，难以直接踏入，这就需要一个基础，于是古武因此复苏。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;在经过整个联邦的归纳与推衍后，形成了三层古武境！<br><br>&nbsp;&nbsp;&nbsp;&nbsp;气血，封身，补脉！<br><br>&nbsp;&nbsp;&nbsp;&nbsp;只有达到第三层补脉圆满，才算是有资格，去争夺那鱼跃龙门般的道缘！<br><br>&nbsp;&nbsp;&nbsp;&nbsp;不过古武境的修炼之法，大都掌握在联邦各势力手中，对于绝大多数人来说，最正统的获得办法，就是考入四大道院，除此之外，就只能是投效各大势力或是世家。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“他和我差不多的年纪，十有八九，是个世家子……”王宝乐叹了口气，有种风头被人抢走的感觉，此刻身体的疼痛也强烈的浮现出来，忍不住惨哼了几声，引起了众人的关注，不少人都连忙过来。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;王宝乐眼看自己还是受重视的，心里好过了不少，不过他觉得自己太痛了，分数也差不多了，还是死了算了……于是深吸口气，声音带着颤抖传了出去。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“我要不行了，同学们，你们未来成为我缥缈道院的学子后，一定要……”王宝乐的情绪已经酝酿好了，随着话语的说出，正要慷慨激昂的爆发。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;可就在这时，那位红衣少年神色肃然，一步走到王宝乐身边，从怀里取出一个丹瓶，给王宝乐喂了下去。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“甘愿为道院牺牲之人，我陈子恒绝不会让他这么死去！好同学，你可以休息了，一切有我！”陈子恒的话语斩钉截铁，配合其战力，顿时就形成了强烈的感染力，让人信服。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;在这众人纷纷感激时，王宝乐傻了，呆呆的张着嘴望着陈子恒，他再次有种感觉，眼前这个家伙，抢走了自己的台词。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;有心去补救一番，可随着药效的扩散，王宝乐只觉得眼前一黑，虚弱的连话都说不出来了，只能悲愤的躺在那里，看着正在明亮的天空，心底只有一个念头。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“他一定和我一样，作弊了！”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;--------<br><br>&nbsp;&nbsp;&nbsp;&nbsp;感受到了大家的热情，今天正式发文！<br><br>&nbsp;&nbsp;&nbsp;&nbsp;求收藏，求推荐，今天你陪新书成长，明日她将陪你两年！&nbsp;&nbsp;<br></p>', 1, 1, '2019-03-12'),
	(6, ' 飘渺道院', '<p>在王宝乐悲愤的昏过去时，真实世界的天空上，红色的热气球飞艇，速度极快，已临近了缥缈城的势力范围。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;只是此刻，在这飞艇的主阁内，一片嘈杂。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“这王宝乐，我丹道系要了！”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“都别和我战武系抢人，他是我的！”几乎所有的老师，一个个都脸红脖子粗的，时而拍桌子时而争吵，为王宝乐进入道院后的学系喧嚷不休。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;这一切都是缘于王宝乐的舍身为人，对他们的震撼太大，那血肉模糊的身躯，让他们不能不动容，而那一句句话语，更是好似雷霆一般轰入他们的心神，尤其是最后一句，更是让他们心中掀起剧烈的波澜！&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“生是道院的人，死是道院的魂！”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;这句话透出的英勇与忠诚，是道院以及各个学系最渴望获得的种子学生，他们岂能让给别人。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;在这相互的争夺里，只见一个身体削瘦，留着山羊胡的中年老师，眼看自己无法争夺到，于是红着眼一把取出怀里的身份玉卡，灵力涌入，大声狂吼。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“我法兵系，用五年才有一次的权限，内定王宝乐成为我法兵系的特招学子，你们谁敢和我抢！”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;他话语一出，顿时手中的玉卡光芒闪耀，刹那间水晶上王宝乐的名字后面，直接就多了法兵系三个字。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;这一幕让众人纷纷吃惊，要知道这权限太珍贵了，一般来说学子进入道院后，都是老师们审核欲进入自己学系之人是否通过，只有少部分学子，他们才会主动给出橄榄枝。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;可无论如何，这种选择都是双向的，唯独……每一个学系都有的，五年里只能用一次的权限，这权限的作用就是直接内定某个学子成为自己的学系之人，且附带近乎奢华的待遇以及资源，同时更有一些特权，远超同伴，近乎衣钵。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;也正因为这样，所以这种权限大都是送给一些背景特殊，又或者优秀到了极致的学子，无比珍贵。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;王宝乐虽优秀，可在其他人看来，动用这样的权限，还是难免迟疑，此刻不由得都苦笑摇头。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;看到其他同僚的表情，山羊胡觉得自己这一次实在是太对了，暗道以这王宝乐的心性，好好培养后，他对法兵系的忠心，必定达到死心塌地的程度。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“资质再好，也有可能背叛，唯有心性与忠诚，千金难换，关键时刻唯有这样的弟子，才会挺身而出，不枉栽培！！”想到这里，山羊胡顿时得意起来，又看向一旁始终皱着眉头，盯着王宝乐资料若有所思的老医师。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“掌院，我们法兵系都用了权限内定王宝乐，您可别偏心啊。”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“放心，他是你们的人了，只要你不会后悔就好。”老医师低头继续看着面前的档案，淡淡开口，对于缥缈道院而言，每一届考入其内的学子，他们都会有一份对方从小到大，极为详细的资料，此刻他望着资料里的一句话，目中渐渐锐利。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“掌院，你……”山羊胡闻言一愣，其他老师也都怔了一下。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“曾经减肥一个月，不吃不喝，疯狂运动，可体重却不减反增……这种正常人身上不可能的事情，在他这里居然也会出现。”老医师冷笑，又翻出了梦境迷阵内的各个学子的体征，目光落在了王宝乐的从进入考核后，体重的变化数据上。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“如果老夫没有判断错误，这王宝乐早就知道了这是虚假世界，知晓了考核，他在作弊！”老医师抬起头，斩钉截铁道。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“不可能吧……”山羊胡倒吸口气，捂着胸口，只觉得眼前有些发黑。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“是否作弊，测试一下就知道了。”老医师望着水晶画面内的王宝乐，右手抬起操控迷阵，骤然一挥。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;顿时，迷阵世界内，刚刚劫后余生的众人，还没等喜悦散去，突然的就有一声震天的咆哮，从他们前方的丛林里，如同风暴一般，直接席卷。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;一颗颗大树直接就被音浪摧毁，纷纷爆开时，大地也都震颤起来，一头身体足有十丈大小的巨熊，直接就从地面爬了出来，仰天狂吼。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;这巨熊身躯腐烂了大半，可目中却有鬼火，仿佛不死之身，在出现后更是散发出足以让所有人都心神震颤的威压，如同丛林的霸主，使得无数鸟兽，都瑟瑟发抖。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“这……这不可能，天啊，那是……古蛮鬼熊！”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“能生撕古武第二层强者，与古武境大圆满不相上下的……古蛮鬼熊！！”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;柳道斌身体瞬间发软，小白兔、杜敏以及其他人也都一个个目中露出极致的恐惧，哪怕红衣少年，也都在这一瞬脸色突变。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;更为震慑人心的，是那鬼熊在咆哮后，竟直奔众人而来，每一步落下，大都都在震动，气势滔天。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“快跑！！”也不知谁喊了一声，众人本能的就急速散开，就连红衣少年也都面色苍白的放弃了出手，急速后退。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;唯有王宝乐这里刚从昏睡中被震醒，此刻在看到那残暴的巨熊后，眼睛猛地一亮，原本虚弱的身体，也都胸口急速起伏。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“居然还有附加分！！”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;激动中，王宝乐挣扎的在地上向着巨熊爬去，口中则是高呼。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“同学们快走，不要管我，我来帮你们拖延时间！”王宝乐说着，勉强捡起一块石头，向着来临的巨熊扔去。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“老熊，来吃我，只要我王宝乐有一口气在，就绝不允许你伤害我的同学！”王宝乐大吼，那些逃遁的学子，一个个都心底再次感动到了无以复加的程度，不少女生都哭了出来。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;眼看那巨熊磅礴的身体，冲向了王宝乐，似乎下一瞬就要将其生生撕开，此刻在飞艇的主阁内，老医师冷笑起来。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“看到了么，这小子的目中带着兴奋，你们谁见过有人死前，还如此表情的，生怕自己不死？”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;看着画面里的王宝乐，其他老师纷纷神色怪异，实在是若以正常的眼光去看，王宝乐那是英武，可若以怀疑的心态去观察，其破绽就有些明显了。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“这小子也太无耻了！”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“作弊也就罢了，居然还演的这么过分！”主阁内的老师们，也都有些看不下去了，至于山羊胡，此刻更是咬牙切齿，追悔莫及，心在滴血，只差捶胸顿足了。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“天啊，我的权限啊！！”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;到了最后，老医师右手再次一挥，顿时水晶内的所有画面，都立刻支离破碎，全部消散。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“行了，马上就要降落道院了，考核结束！”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;在梦境迷阵崩溃之时，王宝乐看到的最后画面，就是那巨熊遮盖了天空，随后与这片世界一起，化作了浑浊，直接漆黑。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;当他的意识重新恢复时，只觉得全身猛地一震，好似有一股大力推动，睁开眼后，发现已回到了飞艇的修灵室，耳边还有众人的哗然与不可思议的惊呼。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“这……我们不是在池云雨林么，怎么会这样！”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“是我自己做梦了，还是所有人都做梦了啊！”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;王宝乐眨了眨眼，赶紧装出一副茫然的样子，口中还大声呼喊。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“你们快走，不要管我，老熊，我和你拼了！”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;他这声音实在太大，又因他在迷阵内的英武，很难不引人注意，此刻随着喊出，顿时就有数不清的目光，直接就落在了王宝乐那里，尤其是他身边的同学，更是激动了。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“是王宝乐兄弟！”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“好兄弟！！”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“王宝乐，从此之后，你就是我大哥！”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;目光里，还有杜敏与小白兔，劫后余生的她们，此刻在看向王宝乐时，更有不同，尤其是小白兔，眼睛里泪汪汪的，似乎若非是穿着磁灵服，距离又远，她都要扑过去一样。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;这一切，让王宝乐心底得意极了，正琢磨着如何去收割众人的崇拜时，修灵室内，回荡出了威严的声音。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“诸位学子，你们的下方，就是道院所在，而刚才的一切，是我缥缈道院的新生考核，你们的成绩会计入学分……最后，欢迎加入缥缈道院！”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;或许是这威严的声音可以安定人心，又或许是听闻已临近道院，修灵室内的学子一个个从之前的梦境中缓了过来打起精神，纷纷转头顺着窗户向外看去。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;王宝乐虽有些遗憾也难掩心中期待，看向窗外时，立刻就看到在下方的大地上，赫然存在了一处巨大的湖泊，这湖泊好似一面镜子铺在大地上，折射出天空的颜色，美妙无比。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;湖泊中有三座岛屿，成一字型排列，能看到岛屿之间有不少舟船行驶，甚至随着靠近，还能看到岛屿上一处处充满古意的建筑以及无数的身影。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;尤其是最外围的岛屿，人数竟不下数万的样子，如同一个小城。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“我缥缈道院，曾名学院，始建于公元二三四八年，历经了联邦时代，参与了凶兽之战，又步入灵元纪，改学为道，迄今为止已有七百多年的历史，培养出数不清的天骄，英雄，为文明进程作出了显著的贡献，上一任的联邦总统，就是毕业于缥缈道院。”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“你们看到的这座湖，叫做青木湖，所谓青木年华，悠悠牧之，其意欢快，恰似少年！”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“至于三座岛屿，分别是道院核心的天行岛、真息道徒的上院岛以及你等学子的下院岛，各自传承我缥缈道院的上为青天换日月，下为黎民安太平的宗旨！”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;威严的声音回荡在修灵室内，这声音里蕴含了一股自豪，弥漫在众人心头，使得包括王宝乐在内的众人，无不自这一刻，被缥缈道院的气势与底蕴所震动。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;随着下院岛在众人眼中飞速的变大，能看到在这最大的岛屿上，赫然有十多座巍峨的山峰，好似十多把利剑，欲冲天而起。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;每一座山峰上，都存在了数不清的建筑，更有大字，哪怕在天空去看，也都格外清晰。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;法兵系、丹道系、战武系、阵纹系……&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;来不及看到全部，随着轰的一声，众人身体一震，这跨越万里，从凤凰城到达的飞艇，直接就降落在了缥缈道院的下院岛上！&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;缥缈道院，到了！&nbsp;&nbsp;&nbsp;<br></p>', 1, 1, '2019-03-12'),
	(7, '特招学子', '<p>缥缈道院极大，尤其是这下院岛更是磅礴，足以容纳十万人在内求学居住，此刻在下院岛的东南方，空港所在之处，正停放着数十艘巨大的热气球飞艇。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;正有数不清的学子，带着好奇与振奋，拎着行李从飞艇上走下，虽有嘈杂之音，可更多的却是带着欢喜的笑声。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;这些人，都是来自联邦不同地区，这一届考入缥缈道院的学子。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;在其中一艘飞艇舰板上，来自凤凰城的王宝乐等人，也都拿着行李，一个个很是兴奋，望着蓝天白云，望着远处各系的山峰，只觉得神清气爽，心神内充满了说不出的期待。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;只是相比于他们的朝气蓬勃，此刻从主阁内走出的老医师以及那些老师们，却是一个个都神色略有怪异，如今的他们对于这群从凤凰城来临的学子们，已经很是熟悉了。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;尤其熟悉的，就是王宝乐，所以不由得都多看了王宝乐几眼。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;这一幕立刻就被习惯察言观色的王宝乐注意到，别看他置身于人群中，可他始终关心自己的考核成绩，时刻留意老师所在的地方，这才看出了不对劲。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“他们怎么都看我……难道是我的考核成绩太过逆天？哈哈，一定是这样。”王宝乐顿时就激动了，只是在这激动里也有一些疑惑，原因是在那群老师里，有一个山羊胡，其目光落在自己身上时，竟仿佛带着一些悲愤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“什么情况……”王宝乐心下狐疑，觉得那山羊胡似乎有点问题的样子，还没等他详细琢磨，包括山羊胡在内的所有老师，就直奔他们走来。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“陈子恒，你来一下，我带你去报名。”随着靠近，老师中有人快走几步，向着红衣少年开口。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;陈子恒若有所思，点头走了过去后，被那位老师直接带走，能看到二人边走边说，那老师似在极力的推荐着什么的模样。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;王宝乐看到这里，眼睛猛地一亮，顿时就呼吸微微急促，有所猜测，暗道莫非考核里的成绩，在这一刻开始起作用了。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;于是心跳加速，带着期待，挺起胸口，生怕那些老师看不见自己。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“许留山，你和我走。”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“柳道斌，你来。”很快的，这些老师纷纷开口，喊出一个又一个名字，将他们全部叫了过去带走。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;这一幕，顿时就让众人一个个都开始心跳加速，他们也看出来了，这些被点名之人，显然是在考核里成绩不错的，被这些老师看重，这才提前带走，为他们所在的系，抛出橄榄枝。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;王宝乐心底得意，虽没有听到老师喊自己的名字，可他对考核成绩很自信，觉得越是后面的，应该就越是优秀的，甚至心底还对哪一个老师看重自己，有了很强烈的期待。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“要是所有老师都看中了我，我该怎么办，哎呦，好头痛，不知道该怎么选择了。”王宝乐心底陶然着，昂首挺胸，只是他等了半天，看到就连杜敏也都被喊走，身边的数百学子，此刻只剩约莫八成的样子，他有些懵了。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“不能吧……”王宝乐擦了擦额头的汗水，很难淡定。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;尤其是看到那些老师不断地带人离去，此刻所剩无几，直至所有老师都走的差不多了，就连老医师也都看了王宝乐一眼后离去，只有那一脸如别人欠了他钱般的山羊胡还在时，王宝乐只觉得眼前有些黑。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;就在这时，那一脸难看的山羊胡，胸膛急速起伏了几下，似乎很不情愿，又极为无奈，就好似自己选择的路，哪怕再难走，也都不得不走下去般，传出话语。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“王宝乐，还不过来！”这句话，似乎是从牙缝中挤出，在说完后，这山羊胡转身就下了飞艇。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;王宝乐顿时激动，只觉得这声音如同天籁，没时间去考虑对方的脸色，赶紧飞奔而去，很是殷勤的跟随在山羊胡的身后，似乎若对方有行李，他都会毫不犹豫的过去帮着拎起的样子。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;随着他们的离去，余下的弟子统一被人带着下船，这些表现没有特殊之处的学子们，会在接下来的几天内，各自选择各自的学系。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;此刻的下院岛空港外，山羊胡背着手，面色发暗，正大步前行，在他的前方此刻正有一些小型的飞艇停靠，有一些穿着青色院服的往届青年学子，正兴奋的等候在那里，往往看到有长得不错的女生出现，就立刻热情的跑过去嘘寒问暖，在看到山羊胡走来时，他们连忙毕恭毕敬。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“老师您慢点走，咱们是什么系啊。”山羊胡的身后，传来王宝乐气喘吁吁的声音，实在是这山羊胡自身本就是高手，走的太快，没有修炼古武的王宝乐，很难跟上。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;山羊胡心底正郁闷，从怀里拿出一枚紫色的玉佩，直接就扔给跑过来的王宝乐，哼了一声。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“自己去法兵系报道，我还有事，先走了。”说着，他走入一架小型飞艇内，飞速离去。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;接住玉佩的王宝乐一样郁闷，他也看出了对方的态度不对。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“难道我表现的太好，穿帮了……唉，法兵系是什么系？”王宝乐拍了拍额头，拿着玉佩站在那里，满心的苦恼，不自觉的从行李中取出一包零食，吃了起来。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;他觉得这山羊胡也太不靠谱了，此刻无奈之下，只能自己去打听，在这新生到来的日子，缥缈道院下院岛人数极多，空港更是人海一般，原本就炎热的天气，就更加的闷热起来。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;虽有湖风吹来，可带来的都是热浪，王宝乐站在那里从擦着汗，看见远处有人摆摊卖冰水，号称冰灵水，价格虽昂贵，可王宝乐是那种不会委屈自己的人，哪怕再贵也都跑过去买了几瓶，装在行李包里。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;一边喝着凉凉的冰灵水，王宝乐一边四处张望，看着四周热闹的空港，甚至还看到有人在直播新生入学的画面，依稀听到要礼物的声音。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;在打探与问询下，王宝乐没有耗费太多的时间，就对法兵系有了一些了解，心头顿时火热，坐在了前往法兵峰的小飞艇上。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;等到了法兵峰后，此地人数一样众多，有的是来参观以便备选，有的则是早已决断，来此递交入系申请。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;也有不少法兵系的志愿学长学姐，在这里负责接待，为一波波来临的新生带路，放眼看去，人头攒动，鼎沸无比。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“法兵系，看似炼器，可又有不同，能炼天地万物为宝！”随着人群前行，王宝乐听着前方一位马脸学姐激昂的介绍，格外认真，很符合他之前打听到的对法兵系的介绍，让他觉得听起来就很是威武。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“我缥缈道院的法兵系，在整个联邦中也都首屈一指，无论是法器，战器又或者民器，无不精通，且每一届毕业之人，在外都是炙手可热之辈。”走在前方的马脸学姐，一边带路，一边介绍，声音一直激昂，似对自己的学系很是自豪。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“你们方才在高空，应该注意到了我法兵峰的三处巨大的平台了吧，那里就是三大学堂，分别是灵石学、回纹学以及灵坯学！”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“与你们在家乡的基础学堂不同，道院的生活较为自由，每一个学系都会设有固定的学堂，无论新生老生，都可随时进去学习，至于其他时间则大都是自我修炼，每年虽有考核，但也并非特别严格，唯独上院大考才是关键。”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“若是五年内，始终无法考入上院，那么就只能离开道院了。”听到前方学姐说到考核，王宝乐更为留心，四周的众人，也都如此。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“不过你们也不必太过紧张，考入上院，对你们来说还是太遥远了，好了，这里就是新生送入系申请的地方。”眼看众人都望着自己，负责带路的学姐微微一笑，停身在了半山腰的一处十丈大小的石镜旁。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;这石镜充满古意，竖在那里散发沧桑，上面更有一道道好似符文般的脉络，看起来就很是不俗。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“将你们的申请卡在上面烙印一下就可下山了，最多三天的时间，下院会有通告，告知各大学系的录取名单。”马脸学姐说完擦了擦汗，有些口干舌燥，站在一旁，看着眼前这些学子，她心底感慨，唏嘘中觉得好似看到了当年的自己。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“也不知道这些人里，有多少能被录取，不过想来也不会很多，毕竟每一届，最多也只收四千人而已。”在这马脸学姐感叹时，王宝乐立刻留意到学姐擦汗的举动，赶紧小跑过去，从行李中取出一瓶凉凉的冰灵水，递到了学姐手中。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“学姐辛苦了，我代表我们所有新生，感谢学姐对我们的讲解，这大热天的，学姐你喝口水吧。”王宝乐表情憨厚，声音诚挚，使得马脸学姐不由得多打量了几下眼前的这个小胖子，顿时有了好感，实在是她迎接引导新生这么多次，这么体贴的人还是不多见的。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;其他同学也都纷纷侧目，毕竟王宝乐话语里并非只顾着其自己，而是代表他们所有人，这就让他们对王宝乐这里，也都印象不错。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;眼看自己一瓶冰灵水，就收获这么多的好感，王宝乐心头得意，觉得自己当官的潜质又高了一点。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;很快，四周的同学们一个个带着期待纷纷靠近石镜，取出一张白色的玉卡，放到了石镜上后，玉卡散出光芒，烙印完成。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;这种玉卡，他们每个人都有一张，是在到达下院岛后，由随行的老师发放，只是此刻的王宝乐，他看着众人手中的玉卡，有些傻眼。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“这什么玩意，我怎么没有……”王宝乐赶紧打探，知道来历后再次觉得那山羊胡太不靠谱了。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;直至所有人都放完了玉卡，只剩下王宝乐时，带路的学姐也不由得看了过去，关切的问道。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“学弟，有什么疑问么？”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“我的卡和别人有点不大一样……”王宝乐迟疑了一下，摸了摸自己怀里的紫色玉佩，小心翼翼的放在了石镜上。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;可就在其玉佩与石镜碰触的刹那，忽然的，整个玉佩突然爆发出强烈的紫色光芒，甚至就连这石镜也都一下子光芒万丈，更有轰隆隆的巨响惊天而起，回荡整个法兵峰。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;更有气浪扩散开来，四周所有学子，一个个都心神狂震，骇然倒退。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“什么情况！”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“怎么回事！！”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;就连王宝乐也都吓了一跳。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;这还不算完呢，接下来更为惊人的，是在这光芒滔天中，法兵峰顶，甚至都有浩瀚庄严的钟鸣，悠悠而起，好似在通告整个法兵系！&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;铛、铛、铛……&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;一时之间，法兵峰所有区域，无论是正在上山的学子，还是学堂内之人，又或者山顶诸多建筑内，正在自我修炼的所有人……无不抬头。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;石镜旁，马脸学姐此刻只觉得脑海嗡鸣，猛地睁大了眼，目中露出无法置信，失声惊呼。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“特招学子！！”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“你说啥？”王宝乐更是有些懵。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;与此同时，在这钟鸣回荡中，于法兵峰的山顶，有一处充满灵气的大殿，山羊胡正坐在那里看着一本古籍，原本慢慢平复的心，又变得烦躁起来。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“这小王八蛋，怎么来的这么快！”他心底烦闷，一想到自己五年的权限就这么的没了，就觉得肠子都要悔青了。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;若没人来打扰他也就罢了，偏偏此刻随着钟鸣回荡，顿时就有几道身影从大殿外飞奔而来，正是法兵系的其他老师。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“张有德道友，听说你这次给我们法兵系特招了一个绝顶好苗子啊！”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“哈哈，老张啊老张，我就知道你一向眼力卓著，那好苗子在哪儿？快叫来让我看看。”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;随着众老师带着兴奋纷纷进来，山羊胡听着他们的话语，脸色惨黑，有一种自己买的狗屎，含着泪也要吃完的感觉，只能硬挤出笑容。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“没错，那是一个……好苗子！我有个法器要炼，先走了……”说着，山羊胡赶紧离去，生怕再留下去，自己就忍不住一掌拍死那个好苗子！&nbsp;<br></p>', 1, 1, '2019-03-12'),
	(8, '他叫白小纯', '<p>&nbsp;帽儿山，位于东林山脉中，山下有一个村子，民风淳朴，以耕田为生，与世隔绝。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;清晨，村庄的大门前，整个村子里的乡亲，正为一个十五六岁少年送别，这少年瘦弱，但却白白净净，看起来很是乖巧，衣着尽管是寻常的青衫，可却洗的泛白，穿在这少年的身上，与他目中的纯净搭配在一起，透出一股子灵动。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他叫白小纯。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“父老乡亲们，我要去修仙了，可我舍不得你们啊。”少年满脸不舍，原本就乖巧的样子，此刻看起来更为纯朴。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;四周的乡亲，面面相觑，顿时摆出难舍之色。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“小纯，你爹娘走的早，你是个……好孩子！！难道你不想长生了么，成为仙人就可以长生，能活的很久很久，走吧，雏鹰长大，总有飞出去的那一天。”人群内走出一个头发花白的老者，说道好孩子三个字时，他顿了一下。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“在外面遇到任何事情，都要坚持下去，走出村子，就不要回来，因为你的路在前方！”老人神色慈祥，拍了拍少年的肩膀。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“长生……”白小纯身体一震，目中慢慢坚定起来，在老者以及四周乡亲鼓励的目光下，他重重的点了点头，深深的看了一眼四周的乡亲，转身迈着大步，渐渐走出了村子。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;眼看少年的身影远去，村中的众人，一个个都激动起来，目中的难舍刹那就被喜悦代替，那之前满脸慈祥的老者，此刻也在颤抖，眼中流下泪水。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“苍天有眼，这白鼠狼，他终于……终于走了，是谁告诉他在附近看到仙人的，你为村子立下了大功！”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“这白鼠狼终于肯离开了，可怜我家的几只鸡，就因为这白鼠狼怕鸡打鸣，不知用了什么方法，唆使一群孩子吃鸡肉，把全村的鸡都给吃的干干净净……”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“今天过年了！”欢呼之声，立刻在这不大的村子里，沸腾而起，甚至有人拿出了锣鼓，高兴的敲打起来。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;村子外，白小纯还没等走远，他就听到了身后村子内，传出了敲锣打鼓的声音，还夹着欢呼。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;白小纯脚步一顿，神色有些古怪，干咳一声，伴随着耳边传来的锣鼓，白小纯顺着山路，走上了帽儿山。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这帽儿山虽不高，却灌木杂多，虽是清晨，可看起来也是黑压压一片，很是安静。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“听二狗说，他前几天在这里被一头野猪追赶时，看到天上有仙人飞过……”白小纯走在山路上，心脏怦怦跳动时，忽然一旁的灌林中传来阵阵哗哗声，似野猪一样，这声音来的突然，让本就紧张的白小纯，顿时背后发凉。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“谁，谁在那里！”白小纯右手快速从行囊中拿出四把斧头，六把柴刀，还觉得不放心，又从怀里取出了一小根黑色的香，死死的抓住。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“别出来，千万别出来，我有斧头，有柴刀，手里的香还可以召唤天雷，能引仙人降临，你敢出来，就霹死你！”白小纯哆嗦的大喊，连滚带爬的夹着那些武器，赶紧顺着山路跑去，沿途叮当乱响，斧头柴刀掉了一地。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;或许是真的被他给吓住了，很快的哗哗声就消失，没有什么野兽跑出来，白小纯面色苍白，擦了擦冷汗，有心放弃继续上山，可一想到手中这根香是他爹娘去世前留给他的，据说是祖上曾偶然的救下一个落魄的仙人，那仙人离去时留下这根香作为报答，曾言会收下白家血脉一人为弟子，只要点燃，仙人就会到来。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;可至今为止，这根香他点过十多次，始终不见仙人到来，让白小纯开始怀疑仙人是不是真的会来，这一次之所以下定决心，一方面是香所剩不多，另一方面是他听村子里人说，头几天在这看到有仙人从天上飞过。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;所以他这才到来，想着距离仙人近一些，或许仙人就察觉到了也说不定。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;踌躇一番，白小纯咬牙继续，好在此山不高，不久他气喘吁吁的到了山顶，站在那里，他遥望山下的村庄，神色颇为感慨，又低头看着手中的只有指甲盖大小的黑香，此香似乎被燃烧了好多次，所剩不多。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“三年了，爹娘保佑我，这次一定要成功！”白小纯深吸口气，小心的将香点燃，立刻四周狂风顿起，天空更是眨眼间乌云密布，一道道闪电划过，还有震耳欲聋的雷鸣在白小纯耳边直接炸开。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;声音之大，气势之强，让白小纯身体哆嗦，有种随时会被雷霹死的感觉，下意识的就想要吐口唾沫将那根香灭掉，但却挣扎忍住。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“三年了，我点这根香点了十二次，这是第十三次，这次一定要忍住，小纯不怕，应该不会被劈死……”白小纯想起了这三年的经历，不算这次，点了十二次，每次都是这样的雷鸣闪电，仙人也没有到来，吓的本就怕死的他每次都吐口唾沫将其熄灭，说来也怪，这根香看似不凡，可实际上一样是浇水就灭。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在白小纯这里心惊肉跳，艰难的于那雷声中等待时，距离这里不远处的天空上，有一道长虹正急速的呼啸而来。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;长虹内是一个中年男子，这男子衣着华丽，仙风道骨，可偏偏风尘仆仆，甚至仔细去看，可以看到他神色内深深的疲惫。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“我倒要看看，到底是个什么样的人，竟然点根香点了三年！”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一想到自己这三年的经历，中年男子就气恼，三年前他察觉有人点燃自己还是凝气时送出的香药，想起了当年在凡俗中的一段人情。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这才飞出寻来，原本按照他的打算，很快就会回来，可没成想，刚寻着香气过去，还没等多远，那气息就瞬间消失，断了联系。若是一次也就罢了，这三年，气息出现了十多次。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;使得他这里，多次在寻找时中断，就这样来来回回，折腾了三年……&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;此刻他遥遥的看到了帽儿山，看到了山顶上白小纯，气不打一处来，一瞬飞出，直接就站在了山顶，大手一挥，那根所剩不多的香，直接熄灭。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;雷声刹那消失，白小纯愣了一下，抬头一看，看到了自己的身边多了一个中年男子。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“仙人？”白小纯小心翼翼的开口，有些拿不准，背后偷偷捡起一把斧头。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“本座李青候，你是白家后人？”中年修士目光如电，无视白小纯身后的斧子，打量了白小纯一番，觉得眼前此子眉清目秀，依稀与当年的故人相似，资质也不错，心底的恼意，也不由缓了一些。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“晚辈正是白家后人，白小纯。”白小纯眨了眨眼，小声说道，虽然心中有些畏惧，但还是挺了挺腰板。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“我问你，点一根香，为什么点了三年！”中年修士淡淡开口，问出了他这三年里，最想要知道的问题。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;白小纯听到这个问题，脑筋飞速转动，然后脸上摆出惆怅，遥望山下的村庄。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“晚辈是一个重情重义的人，舍不得那些乡亲们，每一次我点燃香，他们也都不舍得我离去，如今山下的他们，还在因为我的离去而悲伤呢。”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;中年修士一愣，这个缘由，是他之前没想到的，目中的恼色又少了一些，单单从话语上看，此子的本性还是不错的。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;可当他的目光落在山下的村子时，他的神识随之扫过，听到了村子里的敲锣打鼓以及那一句句欢呼白鼠狼离去的话语，面色立刻难看起来，有些头疼，看着眼前这个外表乖巧纯朴，人畜无害的白小纯，已心底明朗对方实际上一肚子坏水。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“说实话！”中年修士一瞪眼，声音如同雷声一样，白小纯吓得一个哆嗦。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“这不怨我啊，你那什么破香啊，每次点燃都会打雷，好几次都差点霹死我，我躲过了十三次，已经很不容易了。”白小纯可怜兮兮的说道。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;中年修士看着白小纯，半晌无语。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“既然你这么害怕，为什么还要强行去点香十多次？”中年修士缓缓开口。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“我怕死啊，修仙不是能长生么，我想长生啊。”白小纯委屈的说道。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;中年修士再次无语，不过觉得此子总算执念可嘉，扔到门派里磨炼一番，或可在性子上改变一二。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;于是略一思索，大袖一甩卷着白小纯化作一道长虹，直奔天边而去。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“跟我走吧。”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“去哪？这也太高了吧……”白小纯看到自己在天上飞，下面是万丈深渊，立刻脸色苍白，斧头一扔，死死的抱住仙人的大腿。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;中年修士看了眼自己的腿，无奈开口。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“灵溪宗。”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;兄弟姐妹们，阔别2个月，你们想不想我啊，我非常想你们！&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这本书，我做了详细的大纲，每次回顾大纲里的情节，都很兴奋，有种燃烧的感觉，我非常满意，明天，正式更新，依旧是中午一章，晚上一章！&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;很兴奋，我们已沉寂了数月，如今归来，要……再战起点！&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;新书期，兄弟姐妹，别忘了收藏与推荐啊，收藏与推荐至关重要！&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;求收藏！！求推荐！！&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;让众人知晓，我们……归来了！&nbsp;&nbsp;&nbsp;<br></p>', 1, 3, '2019-03-12'),
	(9, '火灶房', '<p>灵溪宗，位于东林洲内，属于通天河的下游支脉所在，立足通天河南北两岸，至今已有万年历史，震慑四方。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;八座云雾缭绕的惊天山峰，横在通天河上，其中北岸有四座山峰，南岸三座，至于中间的通天河上，赫然有一座最为磅礴的山峰。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;此山从中段开始就白雪皑皑，竟看不清尽头，只能看到下半部的山体被掏空，使得金色的河水奔腾而过，如同一座山桥。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;此刻，灵溪宗南岸外，一道长虹疾驰而来，其内中年修士李青候带着白小纯，没入第三峰下的杂役区域，隐隐还可听到长虹内白小纯的惨叫传出。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;白小纯觉得自己要被吓死了，一路飞行，他看到了无数大山，好几次都觉得自己要抓不住对方的大腿。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;眼下面前一花，当清晰时，已到了一处阁楼外，落在了地上后，他双腿颤抖，看着四周与村子里完全不同的世界。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;前方的阁楼旁，竖着一块大石，上面写着龙飞凤舞的三个大字。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;杂役处。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;大石旁坐着一个麻脸女子，眼看李青候到来，立刻起身拜见。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“将此子送火灶房去。”李青候留下一句话，没有理会白小纯，转身化作长虹远去。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;麻脸女子听到火灶房三字后一怔，目光扫了白小纯一眼，给了白小纯一个宗门杂役的布袋，面无表情的交代一番，便带着白小纯走出阁楼，一路庭院林立，阁楼无数，青石铺路，还有花草清香，如同仙境，看的白小纯心驰荡漾，心底的紧张与忐忑也少了几分。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“好地方啊，这里可比村子里好多了啊。”白小纯目中露出期待，随着走去，越是向前，四周的美景就越发的美奂绝伦，甚至他还看到一些样子秀美的女子时而路过，让白小纯对于这里，一下子就喜欢的不得了。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;片刻后，白小纯更高兴了，尤其是前方尽头，他看到了一处七层的阁楼，通体晶莹剔透，甚至天空还有仙鹤飞过。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“师姐，我们到了吧？”白小纯顿时激动的问道。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“恩，就在那。”麻脸女子依旧面无表情，淡淡开口，一指旁侧的小路。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;白小纯顺着对方所指，满怀期待的看去时，整个人僵住，揉了揉眼睛仔细去看，只见那条小路上，地面多处碎裂，四周更是破破烂烂，几件草房似随时可以坍塌，甚至还有一些怪味从那里飘出……&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;白小纯欲哭无泪，抱着最后一丝希望，问了麻脸女子一句。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“师姐，你指错了吧……”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“没有。”麻脸女子淡淡开口，当先走上这条小路，白小纯听后，觉得一切美好瞬间坍塌，苦着脸跟了过去。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;没走多远，他就看到这条破破烂烂的小路尽头，有几口大黑锅窜来窜去，仔细一看，那每一口大黑锅下面，都有一个大胖子，脑满肠肥，似乎一挤都可以流油，不是一般的胖，尤其是里面一个最胖的家伙，跟个肉山似的，白小纯都担心能不能爆了。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;那几个胖子的四周，有几百口大锅，这些胖子正在添水放米。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;察觉有人到来，尤其是看到了麻脸女子，那肉山立刻一脸惊喜，拎着大勺，横着就跑了过来，地面都颤了，一身肥膘抖动出无数波澜，白小纯目瞪口呆，下意识的要在身边找斧头。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“今早小生听到喜鹊在叫，原来是姐姐你来了，莫非姐姐你已回心转意，觉得我有几分才气，趁着今天良辰，要与小生结成道侣。”肉山目中露出色眯眯的光芒，激动的边跑边喊。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“我送此子加入你们火灶房，人已带到，告辞！”麻脸女子在看到肉山后，面色极为难看，还有几分恼怒，赶紧后退。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;白小纯倒吸口气，那麻脸女子一路上他就留意了，那相貌简直就是鬼斧神工，眼前这大胖子什么口味，居然这样也能一脸色相。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;还没等白小纯想完，那肉山就呼的一声，出现在了他的面前，直接就将阳光遮盖，把白小纯笼罩在了阴影下。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;白小纯抬头看着面前这庞大无比，身上的肉还在颤动的胖子，努力咽了口唾沫，这么胖的人，他还是头一次看到。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;肉山满脸幽怨的将目光从远处麻脸女子离去的方向收回，扫了眼白小纯。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“嗬呦，居然来新人了，能把原本安排好的许宝财挤下去，不简单啊。”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“师兄，在下……在下白小纯……”白小纯觉得对方魁梧的身体，让自己压力太大，下意识的退后几步。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“白小纯？恩……皮肤白，小巧玲珑，模样还很清纯，不错不错，你的名字起的很符合我的口味嘛。”肉山眼睛一亮，拍下了白小纯的肩膀，一下子差点把白小纯直接拍倒。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“不知师兄大名是？”白小纯倒吸口气，翻了个白眼，鄙夷的看了眼肉山，心底琢磨着也拿对方的名字玩一玩。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“我叫张大胖，那个是黄二胖，还有黑三胖……”肉山嘿嘿一笑。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;白小纯听到这几个名字，大感人如其名，立刻没了玩一玩的想法。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“至于你，以后就叫白九……小师弟，你太瘦了！这样出去会丢我们火灶坊的脸啊，不过也没关系，放心好了，最多一年，你也会胖的，以后你就叫白九胖。”张大胖一拍胸口，肥肉乱颤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;听到白九胖这三个字，白小纯脸都挤出苦水了。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“既然你已经是九师弟了，那就不是外人了，咱们火灶房向来有背锅的传统，看到我背后这这口锅了吧，它是锅中之王，铁精打造，刻着地火阵法，用这口锅煮出的灵米，味道超出寻常的锅太多太多。你也要去选一口，以后背在身上，那才威风。”张大胖拍了下背后的大黑锅，吹嘘的开口。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“师兄，背锅的事，我能不能算了……”白小纯瞄了眼张大胖背后的锅，顿时有种火灶房的人，都是背锅的感觉，脑海里想了一下自己背一口大黑锅的样子，连忙说道。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“那怎么行，背锅是我们火灶房的传统，你以后在宗门内，别人只要看到你背着锅，知道你是火灶房的人，就不敢欺负你，咱们火灶房可是很有来头的！”张大胖向白小纯眨了眨眼，不由分说，拎着白小纯就来到草屋后面，那里密密麻麻叠放着数千口大锅，其中绝大多数都落下厚厚一层灰，显然很久都没人过来。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“九师弟，你选一口，我们去煮饭了，不然饭糊了，那些外门弟子又要嚷嚷了。”张大胖喊了一声，转身与其他几个胖子，又开始在那上百个锅旁窜来窜去。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;白小纯唉声叹气，看着那一口口锅，正琢磨选哪一个时，忽然看到了在角落里，放着一口被压在下面的锅。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这口锅有些特别，不是圆的，而是椭圆形，看起来不像是锅，反倒像是一个龟壳，隐隐可见似乎还有一些黯淡的纹路。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“咦？”白小纯眼睛一亮，快步走了过去，蹲下身子仔细看了看后，将其搬了出来，仔细看后，目中露出满意。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他自幼就喜欢乌龟，因为乌龟代表长寿，而他之所以来修仙，就是为了长生，如今一看此锅像龟壳，在他认为，这是很吉利的，是好兆头。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;将这口锅搬出去后，张大胖远远的看到，拿着大勺就跑了过来。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“九师弟你怎么选这口啊，这锅放在那里不知多少年了，没人用过，因为像龟壳，所以也从来没人选背着它在身上，这个……九师弟你确定？”张大胖拍了拍自己的肚子，好心的劝说。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“确定，我就要这口锅了。”白小纯越看这口锅越喜欢，坚定道。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;张大胖又劝说一番，眼看白小纯执意如此，便古怪的看了看他，不再多说，为白小纯安排了在这火灶房居住的草屋后，就又忙碌去了。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;此刻天色已到黄昏，白小纯在草屋内，将那口龟形的锅仔细的看了看，发现这口锅的背面，有几十条纹路，只是黯淡，若不细看，很难发现。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他顿时认为这口锅不凡，将其小心的放在了灶上，这才打量居住的屋舍，这房屋很简单，一张小床，一处桌椅，墙上挂着一面日常所需的铜镜，在他环顾房间时，身后那口平淡无奇的锅上，有一道紫光，一闪而逝！&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;对于白小纯来说，这一天发生了很多事情，如今虽然来到了梦寐以求的仙人世界，可他心里终究是有些茫然。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;片刻后，他深吸口气，目中露出期望。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“我要长生！”白小纯坐在一旁取出杂役处麻脸女子给予的口袋。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;里面有一枚丹药，一把木剑，一根燃香，再就是杂役的衣服与令牌，最后则是一本竹书，书上有几个小字。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“紫气驭鼎功，凝气篇。”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;黄昏时分，火灶房内张大胖等人忙碌时，屋舍内的白小纯正看着竹书，眼中露出期待，他来到这里是为了长生，而长生的大门，此刻就在他的手中，深呼吸几次后，白小纯打开竹书看了起来。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;片刻后，白小纯眼中露出兴奋之芒，这竹书上有三幅图，按照上面的说法，修行分为凝气与筑基两个境界，而这紫气驭鼎功分为十层，分别对应凝气的十层。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;且每修到一层，就可以驭驾外物为己用，当到了第三层后，可以驾驭重量为小半个鼎的物体，到了第六层，则是大半个鼎，而到了第九层，则是一整尊鼎，至于最终的大圆满，则是可以驾驭重量为两尊鼎的物体。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;只不过这竹书上的功法，只有前三层，余下的没有记录，且若要修炼，还需按照特定的呼吸以及动作，才可以修行这紫气驭鼎功。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;白小纯打起精神，调整呼吸，闭目摆出竹书上第一幅图的动作，只坚持了三个呼吸，就全身酸痛的惨叫一声，无法坚持下去，且那种呼吸方式，也让他觉得气不够用。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“太难了，上面说这修炼这第一幅图，可以感受到体内有一丝气在隐隐游走，可我这里除了难受，什么都没有感觉到。”白小纯有些苦恼，可为了长生，咬牙再次尝试，就这样磕磕绊绊，直至到了傍晚，他始终没有感受到体内的气。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他不知道，即便是资质绝佳之人，若没有外力，单纯去修行这紫气驭鼎功的第一层，也需要至少一个月的时间，而他这里才几个时辰，根本就不可能有气感。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;此刻全身酸痛，白小纯伸了个懒腰，正要去洗把脸，突然的，从门外传来阵阵吵闹之声，白小纯把头伸出窗外，立刻看到一个面黄肌瘦的青年，一脸铁青的站在火灶房院子的大门外。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“是谁顶替了我许宝财的名额，给我滚出来！”&nbsp;<br></p>', 1, 3, '2019-03-12'),
	(10, '六句真言', '<p>&nbsp; 白小纯这么一伸头，面黄肌瘦的青年立刻就看到，目光落在白小纯的脸上，气势汹汹。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“就是你顶替了我的名额！”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“不是我！”白小纯缩头已来不及了，赶紧装出一副无辜的样子。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“胡说，你这么瘦，头这么小，分明就是新来的！”许宝财握紧了拳头，怒视白小纯。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“这真的和我没关系啊。”白小纯眼看对方的怒意似要炸了一样，觉得委屈，小声说道。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“我不管，三天之后，宗门南坡，你我决一死战，若你赢了，这口气许某忍了，若你输了，这个名额就归我了。”许宝财大声开口，从怀里扔出一张血书，直接扔在了白小纯面前的窗台上，上面密密麻麻写着无数的血色的杀字。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;白小纯看着那张血书，看着上面那么多血色的杀字，只觉得杀气扑面，心底发毛，尤其是听到对方说要决一死战，更是倒吸口气。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“师兄，多大点事啊，用自己的血，写了这么多个字……得多疼啊。”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“多大的事？啊，我这些年省吃俭用，攒了七年的灵石，七年啊，整整七年！！孝敬给执事，这才换来一个进入火灶房的资格，却被你插了一脚，我与你势不两立，三天后，不是你死就是我活！”许宝财歇斯底里，咬牙切齿。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“我才不去呢。”白小纯赶紧用指尖夹起血书，扔出窗台。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“你！”许宝财刚要发火，只感觉地面一颤，身边已多了一坐肉山，不知何时，张大胖已站在了那里，正冷眼打量许宝财。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“九胖，去和你二师兄一起刷碗，至于你，别在这里大呼小叫的，一边玩去。”张大胖一挥手，掀起阵阵风声。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;许宝财面色变化，连连退后几步，想要说些什么，可看到张大胖后又忍住，最后怨毒的看了眼白小纯，这才悻悻的离去。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;白小纯想了想，觉得对方离去时的目光太阴毒，稳妥起见，决定自己还是不要随意出火灶房为好，留在这里，对方应该不敢进来。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一晃数日，白小纯渐渐适应了火灶房的工作，夜晚时便修行紫气驭鼎功，可惜进展缓慢，始终无法坚持超过四息，让白小纯很是苦恼。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这一夜，他正修行时，突然的，听到外面传来火灶房内的那些胖子师兄兴奋的声音。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“关门了，关门了，黄二胖，快去关门！”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“黑三胖，快去查看一下四周有没有偷看的！”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;白小纯一愣，这次他学聪明了，不从窗户去看，而是顺着门缝看去，只见外面几个胖子灵活无比，在院子里健步如飞，神神秘秘，一片忙碌。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;很快的，火灶房的大门就被关的密密实实，四周更是不知谁展开了什么手段，居然起了一层稀薄的雾气，使得那几个胖子的身影，更为神秘起来。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;白小纯看了半天，直至看到那几个胖子不在窜来窜去，而是神秘在一处草屋前围在一起，哪怕隔着雾气，他也能看清张大胖威武的身影，似乎在那里低声说着什么，他觉得隐秘的事，自己还是少知道为妙，于是退后一些，努力做出自己没看到的姿态。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;可就在这时，张大胖的声音传来。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“九胖，你都看到了，还不快赶紧过来。”声音不算大，似刻意的压了下来。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;白小纯眨了眨眼，脸上露出乖巧的样子，一副人畜无害般，走了出去。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;刚一靠近，张大胖一把抓来，就将白小纯带到了身边，与身边几个胖子围在一起的白小纯，立刻就闻到了一股与众不同的气味，吸入鼻孔内，化作了无数暖流，融入全身。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;再看其他人，也都是神色露出舒爽之意，白小纯精神一振，看到了在张大胖的手中，拿着的一块婴儿头颅大小的灵芝，这灵芝晶莹剔透，一看就不是凡品之物。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“九师弟，来，吃一口。”张大胖看了白小纯一眼，将手里的灵芝递了过去，憨声道。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“啊？”白小纯看了看灵芝，又看了看身边几个胖子师兄，眼看白小纯迟疑，张大胖顿时生气了，一副你若不吃，咱们没完的模样。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;不仅是他如此，四周黄二胖，黑三胖等人，也都这般，盯着白小纯。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;白小纯咽了口唾沫，这种把如此价值不菲珍贵非凡的灵芝，当成鸡腿一样送给自己，非逼着自己吃一口，如果不吃就翻脸的好事，他做梦的时候遇到过，现实里还是头一遭。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;白小纯心脏怦怦跳动，一咬牙，接过灵芝，狠狠地咬下一大口，那灵芝肉入口就化，融入全身后，阵阵比之前强烈了无数倍的舒爽感，让白小纯脸都涨红了。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“好，吃了这孙长老点名要用来入汤的百年灵芝，咱们就真的是自己人了。”张大胖神色露出满意，也咔嚓一口，吃下了一小块，扔给了下一个胖子，很快的，众人就咔嚓咔嚓的，将这灵芝吃掉了一圈，看向白小纯时，也都露出自己人的笑容。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;白小纯呵呵一笑，顿时明白这就是同流合污了，而这几位师兄都吃成这么胖还没事，想来这种吃法是安全的，难怪那个许宝财要给自己下战书，写那么多杀字……&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“师兄，这灵芝真好吃，吃的我浑身发热。”白小纯舔了舔嘴唇，眼巴巴的看向张大胖。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;张大胖听到这句话，眼睛一亮，哈哈大笑，无比豪爽的从怀里掏出一块黄精，递给白小纯。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“师弟，现在知道这里好了吧，师兄之前没骗你吧，吃，以后管饱！”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;白小纯眼中冒光，接住咬下一大口，刚吃完，张大胖又拿出一块地宝，这地宝金黄，香气四溢。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这一次不用张大胖说话，白小纯连忙咬了下去，满口酸甜，浑身舒爽时，张大胖又拿出一枚红色的灵果，这灵果气味甜腻，里面还有一丝气在旋转。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;于是在接下来的时间里，什么灵芝，药材，灵果，地宝，白小纯全部都吃了一口，其他几个胖子也都如此，直至吃的白小纯眼前眩晕，如醉了一样，全身涨热，甚至头顶都冒出了白烟，他觉得自己的身体都胖了一圈。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;随着他不断的吃下，张大胖等人的目光越发的柔和，到了最后，都拍着肚子笑了起来，笑声里带着一副同流合污之感。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;白小纯醉晕晕的，放开了手脚，一巴掌拍在张大胖的肚子上，一只脚踏在旁边，一样大笑起来。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“这杂役处别的房啊，为了获得一个外门弟子的名额，都打破了头，而我们，为了丢掉一个外门弟子的名额，也都打破了头啊，谁也不愿去啊，谁去当外门弟子啊，在这里多好。”张大胖越看白小纯越觉得对脾气，得意的说道，又拿出一根人参，这人参头上数不清的节环，须子之多更是密密麻麻，一看就有不少年份。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“九师弟，我们每个人修为早就足够成为外门弟子了，可我们得藏着啊，你看，这是一根百年人参，外门弟子为了吃一口，打破了头啊，你看咱这。”张大胖直接掰下一条须子，扔在嘴里，嘎吱嘎吱的咽了下去后，将这根人参递给了白小纯。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“师兄，我饱了……这次真的吃不下了……”白小纯双眼迷离，他是真的撑着了，正开口时，张大胖拔下一条须子直接塞到他的嘴里。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“九师弟你这太瘦了，这样出去，宗门里哪个姑娘会喜欢，咱们宗喜欢的都是师兄我们这样威武饱满的，来，吃……我们火灶坊有副对联，叫做宁在火灶饿死，不去外门争锋。”张大胖打了个饱嗝，一边拿出一摞空碗，一边指着身边的草屋，那里挂着一副对联。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“对，对，我们大家都在这里饿死，恩……都饿死。”看着这幅对联，白小纯拍了拍肚子，也打了个饱嗝。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;张大胖等人闻言都大笑起来，觉得这白小纯越来越可爱。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“今天高兴，九师弟我告诉你一个学问，我们火灶房吃东西，是有讲究的，有一句口诀，九师弟你要记住，灵株吃边角，主杆不能碰，切肉下狠刀，剔骨留三分，灵粥多掺水，琼浆小半杯。”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“这六句真言，是多少年来先烈前辈总结的，你只要按照这个去吃，保证不出事，行了，都散了吧，今天的宵夜结束，那些外门弟子还在等着喝汤呢。”张大胖一边说着，一边向一个个碗中倒米汤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;白小纯迷迷糊糊的，满脑子都是那六句真言，看了眼正在倒米汤的张大胖等人，又看了看一口口碗，一个嗝打出后，蹲下身拿起一个空碗，仔细看了看后，咧嘴笑了起来。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“师兄，这个碗不太好啊。”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;张大胖等人听了后，看向白小纯，露出诧异的神情。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“你们瞧这个碗，此碗看起来不大，可实际上很能装，咱们为什么不让它看起来很大，实际上装的很少呢？比如说这碗底……厚一点？”白小纯一副乖巧的模样，笑眯眯的说道。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;张大胖愣了，有种好似被雷霆轰击的感觉，身体的肥肉慢慢颤抖起来，双眼冒光，其他几个胖子，也都呼吸急促，一个个全身肥肉都在哆嗦。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;啪的一声，张大胖猛地一拍大腿，仰天大笑起来。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“好，好，好，这可是能名垂千古，造福我火灶房无数后辈的好主意啊，没想到九师弟你看起来这么的乖巧本分，肚子里居然这么有货啊，哈哈，你天生就是干火灶房的料！”&nbsp;<br></p>', 1, 3, '2019-03-12'),
	(11, ' 炼灵', '<p>众人大喜，看向白小纯时，已是喜欢到了极点，觉得这白小纯不但可爱，肚子里坏水还不少，于是张大胖做主，奖励给白小纯一粒灵米，塞在了白小纯的手中。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;白小纯笑的很开心，迷迷糊糊的回到了房间里，还没等爬上床，体内积累的无数天才地宝的灵气，就爆发开来，脑袋一晕，直接就倒在了地上，呼呼大睡。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这一觉甜美非凡，第二天清晨时，白小纯睁开眼，精神振奋，低头时发现自己胖了一圈，全身黏糊糊的，贴着一层黑色的污垢，赶紧出去清洗一番，张大胖等人正在忙碌门中弟子的早饭，看到白小纯的样子，都笑了起来。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“九师弟，那些污垢是你体内的杂质，去掉后你修行时会顺利很多，这几天不用你帮手，过几天在干活。”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“那粒灵米是好东西，记得快点吃了，若放久了不好。”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;白小纯神清气爽，点头回应了一声后，回到房间目光落在那口龟形的锅上，索性扛着出去刷洗一番，带回房间放在灶上，将那粒灵米拿在手中看了看，此米小拇指大小，晶莹光滑，散出阵阵香气。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“仙人吃的东西，果然都不凡啊。”白小纯感慨一番，将火灶内的几块木头点燃，刚一燃烧，一股炙热顿时扑面，让白小纯眼前一焦，赶紧后退，望着火灶内的火，啧啧称奇。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“这火也不一般啊，不但燃的快，比村子里的火温度也高了很多。”白小纯又看了眼火灶内的木头，觉得应该是此木不俗。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;就在这时，随着火焰的燃起，白小纯惊奇的看到，那口龟形锅上的第一条纹路，竟由下向上，开始变的明亮，很快这一条纹路，就从头到尾，全部亮起。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;白小纯愣了一下，忽然一拍大腿。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“我就说么，这是个宝贝，一定比大师兄的那口锅好。”白小纯越发觉得此锅不凡，赶紧把灵米扔在锅中。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;坐在一旁一边等着，他一边拿起紫气驭鼎功的竹书，按照里面第一幅图的动作与呼吸，开始修炼。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;几乎是刚一修炼，白小纯就睁大了眼，他发现这个昨天摆出来很困难的姿势，此刻居然极为顺畅，没有丝毫难受之感，甚至那种呼吸的方法，也都不再出现窒息，反倒是有种很舒服的感觉。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;尤其是坚持的时间，他分明记得之前最多也就是三四个呼吸，可眼下已过去了七八个呼吸，竟没有丝毫酸痛。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;忍着振奋，白小纯让自己平静下来，直至坚持到了三十息后，在他感觉身体出现微弱酸痛时，突然的，从其体内竟出现了一缕气，这气冰凉，飞速在体内游走，虽然没有完整的游走一圈就消散，可依旧让白小纯激动的跳了起来。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“有气了，哈哈，有气了！”白小纯狂喜，在房屋里走来走去，也想到了定是昨晚吃下的那些天材地宝的原因，心底觉得吃的少了。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“难怪张师兄说宁在火灶饿死，不去外门争锋，这等好事，外门弟子都不会有。”白小纯赶紧坐下，再次修炼。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这一次，他按照紫气驭鼎功第一层的呼吸方法与第一幅图的动作，整整坚持到了六十息，在达到六十息的瞬间，他的体内一股比之前还要大了一倍的气脉顿时滋生出来，如同涓流一样，在他的体内快速的游走。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;白小纯有了经验，连忙按照第一幅图上的标示，默默想着体内的几处路线。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;很快，他体内的气脉涓流就按照白小纯所想，顺着路线而行，随着白小纯还在坚持摆出第一幅图的动作，他甚至察觉到身体内还有一丝丝凉气从全身各个位置钻出，如同水滴一样，融入那条气脉涓流内，使得涓流越来越大。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;到了最后，竟化作了一条小溪般，直至完整的游走了一圈后，白小纯全身一震，脑海如拨开云雾一样，传来轰的一声。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一股前所未有的轻灵之感，立刻就在他的身体上浮现出来，一团团污垢更是顺着汗毛孔不断地泌出。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;而他体内的那条小溪，也没有如以往那样消散，而是始终存在，自行的缓慢游走全身，白小纯睁开眼，目中更为清澈，灵动之意多了不少。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;甚至身体也都明显感觉轻快很多。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“气脉常在，就是这紫气驭鼎功修炼到第一层的表现，也代表了达到那什么凝气第一层！”白小纯喜不自禁，跑出去又清洗了一番。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;张大胖等人看到后，露出一副彼此都懂的笑容，对于白小纯这么快修成第一层，虽有惊讶，但却明白缘故。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;重新回到屋舍，白小纯深吸几口气后，拿起一旁的竹书，仔细的看了看。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“这紫气驭鼎功第一层修成后，就可以驾驭一些物体，这可是仙人的法术啊，可以隔空摄物啊。”白小纯眼中冒光，按照上面的方法，双手掐出简单的印决，向着旁边的桌子一指，立刻他就感觉体内的那条小溪，如脱缰的野马直奔自己右手食指而去，更是脱离指尖。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;仿佛形成了一条无形的丝线，与那桌子连接在一起，可惜刚一连接，此线立刻不稳，啪的一声碎裂了。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;白小纯面色微微苍白，好半晌才恢复过来，仔细的想了想后，放弃了桌子，而是将口袋内的木剑取出，这木剑不知是什么木头制成，重量虽不如桌子，但也有些沉重，他右手抬起一指。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;木剑顿时震动了一下，竟缓缓地漂浮起来，但只升起了一寸，就又掉了下来。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;白小纯也不气馁，兴奋的多次尝试，木剑也从开始的升起一寸高度就掉了，变成了十寸，二十寸，三十寸……到了黄昏时，他的房间内那把木剑，已能直线的漂浮而去，速度虽然不快，也难以转弯，但却不会像最早时那样轻易摔落。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“从此我白小纯就是仙人了！”白小纯站在那里，一副傲然的样子，左手背着身后，右手抬起向前挥舞，那把木剑摇摇晃晃的飞来飞去。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;直至体内气息不稳时，白小纯才收回木剑，正要继续修行，忽然闻到了阵阵香气从一旁的锅中传出，他抬头深吸一口，立刻食欲大动，这一天他忙于修行，倒也忘了锅内还煮着灵米，上前打开锅盖。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在打开盖的刹那，一股浓郁的香气从锅内的灵米上散出，只是在那灵米上，不知为何，出现了一道刺目的银纹！&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这银纹很明显，仔细一看，甚至有种摄人心神之感，但随着时间流逝，渐渐成为了暗银色，白小纯眯起眼睛，想了想后将那粒灵米取出，拿在手中查看一番。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“这纹有些眼熟……”白小纯目中露出思索，低头看了眼火灶，发现里面的火早已熄灭，就连木头也都成为了灰烬，而那口锅上的一条亮纹，也重新黯淡了。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他立刻认出，这灵米上的银纹，竟与锅背的纹，一模一样。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;压下心中的疑惑，安全起见，白小纯没有将此米吃下，而是放在了布袋里，思索片刻后，便走出屋舍，与张大胖等人一起干活。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;时间流逝，一晃过去了半个月，这半个月来，白小纯修行又停顿下来，精进缓慢，不过他也打探出了别人煮灵米时，不会出现什么银纹。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;好奇之余，他越发觉得自己那粒米不对劲，尤其是对那口锅，觉得更为古怪，终于在几天后，随着黑三胖外出火灶房去采购所需时，去了一趟四海房，那里是他打探出的，杂役可以前往知晓修行常识的地方。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;从四海房回来后，白小纯的心脏强烈的跳动，他强忍着惊喜，直至回到了房间，立刻就将那粒灵米取出，仔细的看着上面的银纹，目中慢慢露出不可思议之色。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“仙人修行，有三炼不可缺少，分别是炼药，炼器以及……炼灵！”白小纯想着自己在四海房查看到的典籍中描述炼灵的图片，对比灵米上的银纹，越看越像。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“炼灵！”好半晌，他长长的呼出一口气。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;炼灵，是一种以特殊的方法，为物品强行注入天地之力的手段，如同代替天道行使造物之法，掠夺天地之力加持强化，无论是丹药香药还是法宝，都可以炼灵，故而遭天地所不允，所以存在一定的几率，一旦成功则可使得物品威力大增，而若失败，则会让物品直接在天地之力下成为废品。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;且炼灵最惊人的，是可以叠加炼化，甚至若能成功炼灵十次，可以让物品出现翻天覆地的开天之变。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;而越是珍贵之物，叠加炼灵后就越是恐怖。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;只不过越到后面，成功的几率就越小，即便是一些炼灵大师，也都不敢轻易尝试，毕竟一旦失败的代价，难以承受。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“典籍上曾说，我灵溪宗的护宗至宝，就是一件莫大机缘下，炼灵了十次的天角剑！”白小纯觉得有些口干，咽下一口唾沫，目中已露出骇然，更有迷茫，下意识的看了看那口龟纹锅上的数十条黯淡的纹路，心脏跳动的速度，仿佛要从胸口里蹦出来一样。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他此刻已确定，灵米之所以出现炼灵纹，一切的原因，就是这口锅！&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;踌躇一番，白小纯咬牙，若不解开这么谜团，他会睡不着觉，但也知道这口锅若真不俗，那么这等隐秘，万万不可让第二人知晓。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;于是等到了深夜，这才小心翼翼的来到锅旁，深吸口气后，患得患失的将那把被他操控的木剑取出，按照当日扔下灵米的样子，扔到了锅中。&nbsp;&nbsp;&nbsp;<br></p>', 1, 3, '2019-03-12'),
	(12, '离乡', '<p>铁柱坐在村内的小路边，望着蔚蓝的天空，神情发呆，铁柱不是他的本名，而是从小因为身体瘦弱，父亲怕养不活，于是按照习俗称呼的小名。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;他的本名叫做王林，王姓在四周几个村落内算是大姓，祖上木匠出身，尤其是在县城，王氏家族也算颇有名气，拥有数个专门卖木制品的店铺。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;铁柱的父亲是家族内庶出的次子，不能接管家族要务，而是在成婚后离开县城，在此地村庄定居。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;由于有一手精湛的木匠活儿，铁柱家境也算小康，吃穿不愁，就算是在村子里，也多受人尊敬。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;铁柱从小就极为聪明，喜欢读书，想法很多，几乎是村子内公认的神童，父亲每次听到别人夸奖，脸上的皱纹都会绽开，露出开怀的微笑。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;母亲更是对他关心有佳，可以说从小到大，铁柱都是生活在父母的慈爱之中，他知道父母对他期望很高，别人家的孩子在他这个年纪都已经下地干农活了，可他却在家读书。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;书读的多了，想法自然也就随之而来，对于山村外的世界，他充满向往。抬头望了望小路的尽头，铁柱叹息一声，合上手中书本，起身向家中走去。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;父亲坐在院子里，手中拿着烟袋，深深的吸了一口后，对推门进来的铁柱说道：“铁柱，书读的怎么样了？”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;铁柱应付了几句，父亲磕了磕烟袋，起身说道：“铁柱啊，你要好好读书，明年就是县里大考了，你以后有没有出息，可就全看这次了，可别跟我一样，这辈子就呆在村子里，唉。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“行了，你天天叨叨，要我说，我们家铁柱一定能考上。”铁柱的母亲，端着饭菜放在院内桌子上，招呼他们父子二人过来吃饭。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;铁柱哦了一声，坐下后胡乱的拔了几口，母亲慈爱的望着儿子，把不多的几块肉，给他夹过去。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“爹，四叔快来了吧？”铁柱抬头说道。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“算算时间，差不多就是这几天了，你四叔比爹有出息，唉，孩儿他娘，给老四准备的山菜都包好了吧？”父亲一提起老四，脸上露出唏嘘之色。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;母亲点头，感慨道：“铁柱啊，你四叔是个好人，这几年幸亏他帮助，你爹做的木雕才能卖出价钱，你以后要是有出息了，别忘记报答你四叔。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;正说着，忽然门外传来马声，接着马车轱辘压地的哗哗声随之而来，一个爽朗的声音在外面响起。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“二哥，开门喽。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;铁柱惊喜，立刻跑过去把院子大门推开，只见一个精壮的中年汉子，目光炯炯的站在门外，他看到铁柱后哈哈一笑，摸了摸他的脑袋，笑道：“铁柱，这才半年没看到，个子又长高了。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;铁柱父母连忙站起，他父亲笑道：“老四，我约莫时间，你也快来了，快进来，铁柱，还不去给你四叔拿凳子去。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;铁柱高兴的应了一声，急忙跑回屋子，拿出一个板凳放在饭桌旁，用袖子认真的擦了擦，希冀的望着中年汉子。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;中年汉子冲他眨了眨眼睛，打趣道：“铁柱，这次怎么这么勤快啊，我记得上次我来的时候，你可没这样啊。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;铁柱父亲瞪了铁柱一眼，笑骂道：“这小兔崽子，刚才就在叨叨你是不是快来了。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;中年汉子看到铁柱小脸微红，笑道：“铁柱，你四叔可没忘记答应你的东西。”说完，从怀来拿出两本线状书，放在桌子上。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;铁柱兴奋的欢呼一声，拿起书翻看一下，喜不自禁。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;铁柱他娘慈祥的望了眼自己儿子，对中年汉子说道：“老四，你哥平时总念叨你，这次多住几天吧。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;中年汉子摇头道：“二嫂，家族这段日子事情多，我明儿一早就要赶回去，等这段时间忙完了，我再来看你们。”说完，他歉意的看了看自己二哥。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;铁柱父亲叹息，说道：“老四别听你嫂子的，明儿把货物装好，家族的事儿最重要，咱们以后再聚也一样。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;中年汉子望着铁柱父亲，说道：“二哥，铁柱今年十五岁了吧？”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;铁柱父亲点头，感慨道：“过了年，这小兔崽子就十六了，唉，一晃十多年过去，真快。”说着，他眼露溺爱之色，望着自己的儿子。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;中年汉子沉吟少许，面色一肃，说道：“二哥，二嫂，和你们说件事儿，恒岳派今年收取弟子，家族有三个推荐名额，分到我这里有一个。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;铁柱父亲一怔，失色道：“恒岳派？可是那个全都是仙人的恒岳派？”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;中年汉子一笑，点头说道：“二哥，就是那个仙人门派，咱们家族在附近也算望族，有推荐资格，我家小子你也知道，读书不行，舞刀弄剑倒是擅长，我琢磨着仙人不大会收我家小子，这名额珍贵，我看铁柱从小就聪明，喜好读书，说不定能行。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;铁柱母亲喜极，急忙说道：“老四，这……这……”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;中年汉子摸了摸铁柱脑袋，说道：“二哥，二嫂，我看这事就这么定了吧，让铁柱去试试，要是真被收取了，那就是他的福分。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;铁柱迷惑的望着父母与四叔，他有些听不懂他们说些什么，仙人？什么叫做仙人？铁柱犹豫了一下，轻声询问。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;中年汉子面色严肃，望着铁柱，说道：“铁柱啊，仙人就是可以在天上飞来飞去，他们每一个人都神通广大，不是我们凡人可以琢磨的。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;铁柱懵懂间，对仙人有了一丝好奇。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;铁柱父亲激动起身，拉着铁柱他娘对中年汉子就要鞠躬，中年汉子连忙把他们扶起，诚恳的说道：“二哥，你这是做什么。我娘死的早，要不是二娘小时候照顾我，我也不可能有今天，铁柱是我侄儿，我这么做是应该的。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;铁柱父亲感慨流泪，重重的拍了拍中年汉子，点了点头，随后疾言厉色的对铁柱说道：“王林你记住，以后无论如何，都不要忘记你四叔对咱们家的恩惠，否则我就没你这个儿子！”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;铁柱内心一颤，他虽然对仙人还是懵懂，但却从父母的表情中看出他们对此事极为重视，于是跪在四叔面前，重重的磕了几个响头。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;中年汉子拉起铁柱，赞赏的说道：“好孩子，你这几天收拾一下，我月底来接你！”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;旁晚，铁柱早早的睡下，耳边还能听到院子里父亲与四叔的声音，父亲今天很高兴，很少喝酒的他，说什么也要和四叔喝上几杯。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“仙人？到底是什么？”铁柱内心很兴奋，他幼小的心灵隐约知道，这将是自己的一个机会，一个可以去外面世界的机会！<br><br>&nbsp;&nbsp;&nbsp;&nbsp;第二天一早，四叔离开了，铁柱的父母拉着他一直送到村口，在回来的路上，铁柱清晰的看到，父亲似乎一下子年轻了很多，看向他的眼神，也充满了期望。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;这种期望的眼神，要比之前让他去县里大考时，浓重很多。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;小山村内根本没有秘密可言，就是一只狗生了几个崽子也能在瞬间传遍全村，很快全村的人都从铁柱母亲嘴里知道了这个消息，纷纷来访，每个人的眼神看向铁柱时都不同，有羡慕，有嫉妒……<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“王家生了个好娃，人家被恒岳派收为弟子了。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“铁柱这孩子我从小看他长大，这孩子打小就聪明，这次又成为恒岳派弟子，以后定有大出息。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“铁柱有本事，以后出息了可别忘记咱们村，多回来看看啊。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;诸如此类的话，纷纷涌入铁柱耳中，渐渐的说成了他已经成为恒岳派弟子一般，父母每次听到，都笑的合不上嘴，脸上的皱纹似乎都少了很多。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;每当铁柱独自走在村内，所有看到他的村民，都热情的拉着他问来问去，更有甚者甚至当着自己孩子面，把铁柱当成榜样，训导一番。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;半个月的时间很快过去，铁柱成为恒岳派弟子的消息迅速传开，周围十里八乡的村民，都陆续的前来道贺，他们的主要目的是看一看铁柱。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;每个人来时，都会准备礼物，铁柱父母推脱不掉，也就收下了。不过在这些人离开时，他们都会为其准备一份重重的回礼，按照铁柱父亲的话说，咱家的娃儿以后那是高高在上的仙人，不能让他欠下这么多人情债，所有来访的村民，咱都给他回礼。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;此时，王氏家族的族人，也渐渐知道老四把自己娃的名额让给铁柱的事情，纷纷陆续前来道喜。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;对于自己家族的族人，铁柱父亲极为重视，这些人里有很多是之前瞧不起他的，更有多年前把他逼出家族的，现在这些人一个个前来他家，让他感觉一下子多年的憋屈一扫而空。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;他和铁柱他娘商量一下，准备好好招待一番，于是花了大价钱，请村里的教书先生写下请帖，送往族内。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;教书先生说什么也不收钱，只是要求铁柱必须要承认，他从小就在自己这里念书，对此铁柱没啥说的，这本就是事实嘛。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;请帖送到后，王氏家族族内大部分亲戚都来庆祝，由于来人太多，铁柱他爹把招待地点选在了村子中心的广场，摆下数百桌宴席。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;村子的居民自行帮着招待，相互交谈之时无不对铁柱赞叹有佳，夸奖不已。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;至于铁柱他爹，则是带着老婆孩子亲自在村口迎接，为铁柱一一介绍亲戚身份。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“这是你三祖父，当初爹离开家族，你三祖父暗中帮衬了不少，铁柱，你以后要记的报答。”铁柱父亲搀扶着一个白发苍苍的老人，对王林说道。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;铁柱连忙乖巧的应诺，老人望着铁柱，感慨道：“老二啊，时间真快，你家娃娃都这么大了，你这娃很好，比你有出息。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;铁柱父亲满面红光，笑道：“三祖父，铁柱这孩子从小就聪明，指定是比我强啊。您老慢点走，孩儿他娘，扶着三祖父过去。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;铁柱母亲连忙上前，搀扶老人走向宴席。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;看到老人走了后，铁柱他爹哼了一声，对铁柱说道：“这老家伙，当初看不上你爹，非要把我逼走，现在铁柱你出息了，又来道喜，这亲戚啊，就是这样。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;铁柱懵懂的点了点头，问道：“爹，四叔今天来么？”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;铁柱父亲摇头道：“你四叔传来信儿，他在外地回不来，等月底接你时才能赶回。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;这时，又有马车奔行而来，在村口停下后，走出一个五十多岁的老者，他望了望铁柱父亲，轻叹一声，说道：“老二，恭喜。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;铁柱父亲面色复杂，许久后说道：“大哥……”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;老者目光一扫，望着铁柱，微笑道：“老二，这就是你家小子吧，不错，这次说不定真能被选上。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;铁柱父亲眉头一皱，随后舒展开，说道：“铁柱这孩子没啥优点，就是打小聪明，愿意读书，这次指定是会被选上。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“那可不一定，仙人门派收取弟子，要求非常严格，讲究有没有仙缘，我看这小子傻头傻脑的，去了也是白搭。”一个傲慢不逊的声音悠悠传来，从马车上走下一个十六七的少年。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;少年相貌俊美，剑眉星目，面如冠玉，眼中露出轻蔑之色。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;铁柱父亲怒目而视，王林则深深的看了对方一眼，没有说话。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;老者面色一肃，喝道：“王卓，怎么这么没有礼貌，这是你二叔，这是你弟弟王林，还不见礼。”说完，他又对铁柱父亲说道：“犬子说话难听，老二你别介意，不过……”说道这里，他话锋一转，又道：“不过老二，这仙人收徒，可不是那么简单的事情，需要讲究缘分，这次是恒岳派道虚上仙看重了犬子，这才对我王氏家族感兴趣，给了包括犬子在内的三个名额。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;铁柱父亲哼了一声，说道：“你家娃既然能行，我家娃，一定也会被选中！”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;少年耻笑，根本就不在乎老者的喝斥，蔑视道：“你就是二叔吧，我劝你还是别往好了想，这修仙体制不说万中无一也差不多，这傻小子不可能和本少爷比，本少是被仙师内定的弟子，他能比么？”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;老者脸上得意之色一闪而过，又训斥了几句，对铁柱父亲一抱拳，带着少年走向宴席。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“铁柱，不要有压力，要是真没被选上也不算啥，明年去县里大考也是一样。”铁柱父亲憋了半天气，许久后才缓舒出来，语重心长的说道。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;王林目光坚定，低声道：“爹，你放心，我一定能被选中！”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;铁柱他爹温和的拍了拍儿子肩膀，眼神中露出期望的目光。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;又陆续迎接了不少亲戚，铁柱父亲最后带着他回到宴席，此时高朋满座，场面热闹非凡，众人相互庆祝，熙熙攘攘。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“族内的亲戚们，父老乡亲们，我王天水没啥文化，不会说什么词儿，但是我今天高兴，我家娃这次有机会被恒岳派选中弟子，是我这辈子最高兴的事儿，我多了也不说了，感谢各位来我这儿道喜，谢谢了！”铁柱父亲大声说完，端起酒杯一干二净。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“老二，你家娃娃从小就聪明伶俐，一定能被选中，和王卓那孩子一样，成为仙人。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“二哥，你有铁柱这孩子，这辈子算不白活了，以后等着享福就行了。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“铁柱，给你爹争口气，这次无论如何也要进入恒岳派。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;场面一时间大为锦繁，庆祝声四起，不过暗地里，也有很多人抱着看热闹的心态，比如王卓的父亲，就是如此，他表面上恭喜，内心却从来都瞧不起这个二弟，对他的孩子就更是如此，他望了望自己家的小子，又看了看铁柱，内心颇不以为然，心道老四这次把名额让出，虽然出乎自己预料，但只要那些仙人不是瞎子，就不可能选中铁柱。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;人生百态，此间众人一一可见，铁柱父亲拉着铁柱，挨个桌子敬酒，为他介绍一个又一个陌生的亲戚。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;这一天，铁柱的父亲喝了很多酒，他从来没有这么风光过，一直到很晚，亲戚陆续离开。临走时，少年王卓依然还是那副瞧不起的表情，趁人不注意在铁柱耳边轻声道：“傻小子，你不会被选中的，你没有那块料。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;说完，他轻蔑的一笑，随着他父亲离开了。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;回到家中，铁柱躺在床上，内心暗自决定，无论如何，也要被选中！<br><br>&nbsp;&nbsp;&nbsp;&nbsp;半个月匆匆而过，这一天，铁柱四叔赶着马车来了。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;铁柱父母连忙把他迎入房内，中年汉子洗了把脸，匆匆说道：“二哥，二嫂，这次不能待时间长，我接上铁柱马上离开，明天一早恒岳派就要来接人了。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;铁柱父亲一怔，脸上露出一丝不舍之意，果断的说道：“行，铁柱，跟着你四叔走吧，你……你若是被选中，就踏踏实实的在恒岳派，若是……若是没被选中，不要有任何负担，回到家里来。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;铁柱不舍的望着父母，重重的点了点头，他母亲从房间拿出个包裹，爱怜的说道：“铁柱，出去要听四叔话，不要惹事，外面不比家里，遇事多忍让，娘给你准备了几件新衣服，还有你最爱吃的烤甘薯，娘会想你的，要是没被选中，就回来……”铁柱他娘说着说着，眼泪就流了下来。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;铁柱从小到大，都没有出过村子，这次，是他第一次离开。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;四叔在一旁感慨，说道：“铁柱，给你爹娘争口气，一定要被选中。二哥，二嫂，家族几天后会有大型欢庆席，今天着急，我明儿来接你们一起过去，家族的三个被推荐上去的娃娃，那时就有结果了。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;说完，他拉着铁柱匆匆上了马车，抽了马匹一鞭子，扬长而去。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;铁柱的爹娘，一直望着飞快消失的马车，泪流不止。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“他爹，铁柱从来没离开过咱们身边，这次他出去会不会受欺负。”铁柱他娘咬着下唇，眼中露出不舍。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“娃娃长大了，自有他们的福气……”铁柱父亲拿起烟袋，深深的吸了一口，脸上的皱纹，一下子又多了不少。&nbsp;&nbsp;<br></p>', 1, 4, '2019-03-12'),
	(13, '仙人', '<p>马车飞快的在小路上驰骋，王林的身子随着地面的坑洼起伏晃动，他抱着怀里的包裹，内心跌宕，带着父母对他的期望，离开了居住十五年的山村。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;此地距离县城有不短的路程，王林渐渐睡下，也不知过了多久，他被人轻轻推动，睁开眼睛一看，四叔面带微笑的望着他，打趣道：“铁柱，第一次离开家门，有什么感想啊。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;王林察觉到马车已经停下，憨笑道：“没啥感想，就是有些害怕，不知道能不能被仙人收入。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;四叔哈哈一笑，拍了拍铁柱肩膀，说道：“行了，别想那么多了，到地方了，这是四叔家，你先去休息，等早上我带去你家族。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;下了马车后，展现在王林面前的是一片瓦房，跟着四叔来到一处房间，王林坐在床上却怎么也没有睡意，他脑中闪过一幕幕爹娘、乡亲、亲戚的言语，内心轻叹，对于被仙人收入弟子的念头，更重了。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;时间一点点过去，不大一会天色微亮，日头渐渐升起，王林尽管一夜没怎么休息，但精神却很充沛，带着一丝忐忑，他随着四叔来到了王氏家族的大宅子。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;这是王林第一次看到这么大的房子，眼花缭乱，四叔边走边叹道：“铁柱，这次一定要给你爹争口气，可别叫亲戚们笑话。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;王林内心更加紧张，紧咬下唇，点了点头。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;不大一会儿，四叔把他领到宅子正中的大院子里，铁柱他爹的大哥，那个老者站在院中，看到铁柱后略点头，说道：“铁柱，一会仙人来了，可别大惊小怪，一切事情就跟着你哥哥王卓学，他怎么做，你就怎么做，知道么！”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;最后几个字，老者语气严厉。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;王林沉默不语，四下一看，发现除了王卓之外，还有一个少年，少年皮肤略黑，虎头虎脑，眼中更是透出一丝机灵，他衣内鼓鼓，好似揣了什么东西。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;他看到铁柱望向自己，冲他做了个鬼脸，跑过来问道：“你就是二叔家的铁柱哥吧，我叫王浩。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;王林轻笑，点了点头。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;老者看到铁柱居然不理会自己，心底微恼，正要喝斥。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;就在这时，忽然天上云彩飘扬，一道剑光闪电般破空而来，剑光消散后，地面上站立一白衣青年，青年目光炯炯有神，散发出飘逸不凡的气质，他神态冰冷，目光在铁柱等三少年身上一扫，尤其是在机灵少年身上鼓鼓之物处看了眼，冷声道：“王家的三个名额，就是他们？”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“这就是仙人？”对方一眼之下，王林只感觉身体一凉，心脏止不住的狂跳，小脸面无血色，呆呆的望着对方。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;再看那个一脸机灵之色的少年，也是双手放在裤边，毕恭毕敬，眼中露出狂热之色。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;唯有王卓，漫不经心的看了对方一眼，鼻子里轻哼一声。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;王卓的父亲连忙上前，满脸恭敬之色，寒蝉若惊道：“上仙，这三人正是王家推荐的族人。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;青年点头，不耐烦的说道：“谁是王卓？”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;老者面上一喜，连忙拉着王卓说道：“上仙，这就是犬子王卓。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;青年深深的看了王卓一眼，面色微缓，点头道：“王师弟果然一表人才，难怪能被道虚师叔看中。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;王卓得意的看了看铁柱与另外那个机灵少年，自傲的说道：“那是自然，本少的修仙灵根，道虚仙人可是夸奖不已。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;青年眉头一皱，但很快就舒展开，似笑非笑的望了王卓一眼，袖子一卷，带着三个少年腾云驾雾化作长虹，瞬间消失在原地。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;四叔抬头望着天空，喃喃自语道：“铁柱，一定要被选中啊！”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;王林感觉身子一轻，剧烈的罡风吹的脸上生痛，仔细一看，立刻震惊的发现自己居然被夹在青年的腋下，正在空中飞快前行，地面的村庄变成一个个巴掌大的黑点，飞快的向后移动。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;就这么一小会儿，他的眼睛就已经被风吹的通红，眼泪哗哗流下。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“你们三个不想眼睛瞎掉，就闭眼。”青年冰冷的声音传来。王林内心一颤，连忙闭上双眼，不敢再看，内心对于修仙，更加的向往了。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;时间不长，王林能感觉到青年略有气喘，速度也明显降了下来，接着眼前一花，青年迅速落下，在落地的一刻，青年松开手臂，三个少年摔落在地。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;好在摔落的力道不重，三人连忙爬起，展现在王林面前的，是一处宛如世外桃源般的仙境，青山绿水，鸟语花香。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;正前方，一座高耸入云的山峰，千岩竞秀，云雾缭绕，看不清细貌，时而传来一两声兽鸣。一条扭曲的石阶小径，自山峰蜿蜒而下，宛若画中景色，山明水秀。一种隔世之感悠然而生。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;远远向上看去，山峰顶端有座大殿，虽说被云雾遮掩，但阵阵七彩光芒闪烁，让人一看不由升起膜拜之意。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;大殿旁，一座横生而出的长条石桥仿若弯月般，延伸至虚空云雾之中，与另一座山峰相连。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;如此洞天福地，自然就是恒岳派的山门所在，恒岳派是赵国为数不多的修真门派之一，500年前曾一度统领整个赵国修真界，拥有数位元婴期老怪，风光无限，可惜随着时间的流逝，中间历经数场变故，时至今日，昔日的苍天大派，已经萎缩到只能勉强在修真界站稳脚跟的地步。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;不过尽管如此，恒岳派对于四周万里之内的凡人来说，依然还是可望而不可及。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“张师弟，这三个可是王家推荐的少年？”一个身穿黑衣的中年人，带着一丝仙风道骨之气，从山峰下飘然而至。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;青年脸上露出恭敬之色，说道：“三师兄，这三人正是王家推荐。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;中年人目光一扫，在王卓的身上重点看了几眼，含笑道：“掌门知道你修炼到了关键处，让我来进行这次测试，你去修炼吧。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;青年应诺，身体一动，顺着山峰小径，眨眼间就消失无影。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;王林呆呆的望着眼前的一幕，心潮澎湃，忽感有人拉自己衣服，扭头一看，正是那一脸机灵之色少年，少年眼中狂热之色更浓，低声道：“这就是仙人居住的地方，他奶奶的，我王浩说什么也在被选中！”说完，他摸了摸自己衣内鼓鼓之物。&nbsp;&nbsp;<br></p>', 1, 4, '2019-03-12'),
	(14, '测试', '<p>&nbsp;王卓对于眼前的景象，也是怔在当场，许久后才缓过神，内心傲气不知觉的收敛了很多。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;此时，又有几道长虹剑光临至，每道剑光消散后，都会现出恒岳派弟子，在他们的身边，毫无例外跟着几个十五六岁的少年。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;这些人有男有女，在落下的一刻均都是如王林三人般，呆呆的望着眼前的景物，脸上表情不一。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;带众人来此的恒岳派弟子零散的站在不远处，相互交谈打量这些少年。又等了一会，最终所有被推荐到此地的少年都来齐，此时黑衣中年人扫了一眼众人，声音不含任何感情，说道：“你们之中，只有极少的几个会被选中成为我恒岳派弟子。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;众少年寒蝉若惊，王林更是内心忐忑，他数了下，测试的一共48人。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“修仙，首重天资，接下来第一项测试，就是看你们灵根是否充足。现在我点到谁，谁就到我近前来。”中年人面无表情，随意的点了一个少年。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;少年双腿微颤，小心翼翼的走了过去，中年人手按在少年头顶，淡然道：“不合格，到左面站好。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;少年似乎一下子失去了所有的力气，神情黯淡，眼带茫然的走到左边，沉默不语。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;接着又一个少年被点中，忐忑不安的上前。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“不合格。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“不合格。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“不合格。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;连续十多个人，均都不合格，中年人的右边，到现在为止一个人都没有。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;王卓被点中，他脸上傲气尽收，面色有些苍白，走上前。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;中年人手放在王卓头上后，忽然面露喜色，语气温和，说道：“你叫什么名字？”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;王卓连忙恭敬道：“回禀上仙，小子王卓。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;中年人点头，笑道：“原来你就是道虚师叔提到过的王卓，恩，到右边站好。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;王卓大喜，在所有人的羡慕目光中，走到右边，眼中傲气又现，轻蔑的望着众人，神情不可一世。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“妈的，他是走狗屎运了。”王浩撇了撇嘴，对王林低声道。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;王林内心更加紧张，他眼前浮现父母期待的目光，握紧了拳头。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“不错，你也去右边站好。”中年人惊喜的声音传来，在他面前是一个少女。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;时间过去不长，大部分少年都被测试完，可站在中年人右边的，只有两人。接下来，王浩被点中。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;王浩一路小跑，站在中年人面前，还没等对方测试，立刻跪在地上重重的磕了几个响头，大声道：“上仙仙福永享，寿与天齐，小子王浩，您测试这么多人，一定很辛苦，要不您休息一会？我不着急，没事。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;中年人哑然失笑，测试了这么多人，均都是一脸紧张，惟独眼前这个少年虎头虎脑，最为机灵，居然还拍起了马屁。按在王浩头上，他摇头说道：“资质差些，不……”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;王浩一听说自己资质不行，内心立刻跌至低谷，还没等对方说完，立刻从怀里拿出一个玉盒，双手交上，机灵的说道：“上仙，这是家父偶然间在山中得到，尝试多次都打不开，小子特地从家里带来，献给上仙您。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;中年人轻笑摇头，正要拒绝，但一扫玉盒，忽然瞳孔一阵收缩，抓起仔细一看，顿时面露喜色，说道：“不错，居然是一株有300年的灵芝，看这玉盒样子，应是被修真者封印在内，难怪你父亲打不开。”他语气一顿，略微沉吟，含笑道：“我身边缺个炼丹的小童，我看你比较机灵，可愿意做我小童？”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;王浩立即惊喜起来，跌宕起伏的差异，让他激动不已，大声说道：“愿意，上仙，我愿意。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;中年人含笑点头，说道：“做我药童，也不会亏待你，可与其他弟子一样修炼门内仙术，你站在右边吧。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;王浩内心兴奋，跑到右边，得意洋洋的瞪了王卓一眼。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;所有的失败者，均都面露惺惺之色，一个个催头丧气，更有甚者，已经泪流满面，哭泣不已。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;中年人眉头一皱，喝道：“哭泣者，直接送走！”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;站在不远处的恒岳派弟子，立即走出几人，抓起几个啼哭者，漫不经心的踏着剑光迅速消失。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;中年人随手一指，点中王林。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;王林深吸口气，紧张万分的走到中年人身边，他脑中一片空白，心里默默祈祷，眼前禁不住回想起父母期望的目光。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“我一定能被选中！”王林坚定的想到。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;对方手一按，面无表情吐出三个让王林如坠冰窟的字。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“不合格！”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;王林不知道自己是怎么走到左面队伍中，他只感觉耳边仿佛春雷般轰轰作响，回荡起伏间，尽是缭绕那三个字。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;不大一会，所有人都被测试完，站在右边的，只有三个人，这三个人，在失败者眼中，犹如天之骄子一般，高大无比。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;王卓更是轻蔑的望着王林，目中讥讽之意表露无疑。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“修仙，资质虽然重要，但毅力更为关键，你们这些资质平凡的少年，若是具备毅力，倒也可以成为记名弟子！这第二项测试，就是毅力！”中年人面无表情，顿了一下，又道：“顺着此处阶梯上去，达到顶端者，算合格，若第三天仍未完成，就是失败，失败者会被送回你们各自的家族。如果半途坚持不住或者遇到危险，就大声呼喊放弃，自会有人把你们带走。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;说完，中年人含笑望着右边的三人，说道：“你们随我去见掌门，到时候会安排师父，王浩，你就不要去了，随我去丹房，熟悉一下操作。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;中年人吩咐完，便带着三个天之骄子踏上山峰，消失匿迹。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;王林深吸口气，目光坚定，毫不犹豫踏步向石阶走去，进行第二项测试，毅力。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;除去三个天之骄子以及六个因哭泣被送走的少年外，还剩下39人。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;这39人中有催头丧气者，也有神情坚毅者，更有犹豫害怕者，彼此前后不一，渐渐踏上阶梯，走出各自不同的未来。&nbsp;&nbsp;<br></p>', 1, 4, '2019-03-12'),
	(15, '无情', '<p>石阶陡峭不平，两旁更是险境连连，稍有不慎就会失足掉落。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;走了不到半天，王林就感觉双腿如灌铅，挥汗如雨，上气不接下气般难以迈步，当初在山脚下看，这条石阶小径不长，可实际走在上面，却发现这小径好似没有尽头般，让人从心底不由产生绝望的思绪。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;在他的前面有十几个身体强壮的少年，也均都气喘吁吁，缓缓上爬。到现在为止，还没有一个放弃的。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;王林咬牙坚持，他知道这是自己最后一次机会，父母期望的眼神一直徘徊心中，就在这时，在他身后一个少年忽然脚下一颤，踩空，身体从一旁迅速落下，惊恐的尖叫顿时彻响。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“我放弃，救命！”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;所有人都停下脚步，不约而同的向下望去，只见一道乌光闪烁，一个恒岳派弟子不知从什么地方冲出，抓住身处半空的少年，身子轻轻的落在山脚下。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;王林面色苍白，沉默不语，小心翼翼的继续向上爬去，时间匆匆而过，两天后，他前面的十几个少年，已经看不到身影。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;王林不知道这些同伴到底有多少放弃的，他只知道，自己绝对不能放弃，尽管双腿双脚已经摸出血泡，钻心的刺痛时刻从破碎的血泡中传来，但他仍然坚持用手攀爬前行。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“稚子心坚，奈何大道无情，徒劳，徒劳罢了……”一声悠悠的叹息，远远的从山峰顶端飘落而下，一个面色蜡黄的中年人，身子轻飘飘的从石阶落下，在一个个少年身边飞快走过，面带感慨之色。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;在路过王林身边时，中年人略顿一下，眼前此子是他看到的第六个少年，但绝对是最狼狈的一个，全身鲜血淋淋，衣服已经被血液浸透，膝盖，脚指血肉模糊，完全是用着双手在一点点攀爬，中年人轻叹一声，问道：“孩子，你叫什么名字？”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;王林神情已经模糊，他心里只有一个念头，那就是死也要爬到山顶，对于中年人的问话，他根本就没听到，在他的眼里，除了这条石阶小径之外，别无他物。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;中年人注视王林双眼，对他的心念之坚颇为动容，手在王林头上一按，随后摇头自语道：“毅力绝佳，可惜资质太过普通，无缘，无缘啊……”他深深的看了眼王林，继续向石阶下走去。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;第二天夜晚，王林双手血肉模糊，他爬过的石阶，留下一道深深的血迹，对于这些，王林已经不知道了，他现在全凭一股意念在催动身体，气息已至弥留之际。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;在第三天日出的一刻，他模糊间似看到了石阶的尽头，可惜此时一个无情的声音如雷鸣般传来，震入心间。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“时间已到，只有三人合格，其余……失败！”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;王林惨笑一声，身子一歪，倒在石阶上，昏迷不醒。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;三天前测试天资的黑衣中年人，站在山顶，望着距离此处不到五十米的王林，眼露无情之色。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;这时几个恒岳派弟子迅速从山顶向下走去，一路上把所有还在坚持的少年带到山上，统一喂食药物。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“师兄，39个测试者，放弃25人，除了合格的三人外，还有11人。”一个恒岳派女弟子，冷声回报，她当年就经历过这样残酷的测试，最终靠着自小练习武术的底子，在毅力上勉强合格，才成为记名弟子，用了近十年的时间努力，到现在仍然未成为真正的弟子。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;黑衣中年人神态冰冷，略点头，目光一扫均都昏迷的11个少年，淡淡的开口道：“合格的三人，带他们去见杂务处，安排日后的工作。放弃的25人，送回各自家族。至于这坚持到最后的11人，待他们苏醒后，统一送到剑灵阁，看看有没有人与剑灵有缘，若是没有，依然还是送回各自家族。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;说完，中年人看都不看这些少年一眼，拂袖而走。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;三天后，剑灵阁内，包括王林在内的11个少年，均都面色苍白的站在一旁，王林身上的伤势已经痊愈，但心灵的伤口却撕裂更大，阵阵钻心的痛，止不住的吞噬他的身心。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;这剑灵测试，并非黑衣中年人主持，而是一个身穿白衣的陌生青年，不过毫无例外的，都是面色冰冷，望向他们的眼光，犹如看蝼蚁般，无情。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“这是最后测试，能走进这房间者，合格。”青年言辞简短，脸上更是露出不耐烦之色。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;王林目光所及，是一处普通至极的房舍，房舍正中大门打开，向里望去，只见一把把长短不一的古剑，摆放在内。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;少年们一个个按照顺序，向房间走去，第一个少年刚刚走近房屋五米处，就面露挣扎之色，被一股无形的力道推动退出几米。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“不合格，下一个！”青年淡淡的说道。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;王林是第七个，之前的六人全部都是在五米处无法前进，他苦涩一笑，提起心底剩余不多的期望，向前走去。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;五米处，轻松踏过，王林一呆，内心期望迅速攀升，口干舌燥，心脏狂跳，又继续踏出一米，此时仍未感觉任何不适。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;青年“咦”的一声，目光闪动，露出一丝感兴趣的神色，面容微缓，说道：“别犹豫，继续向里走，若是能走进房屋，获得剑灵认同，哪怕你之前两次测试都不合格，也会被收为真正的弟子！”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;其他十个少年，均都面露羡慕之色，这种羡慕之中，还藏有深深的嫉妒。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;王林内心紧张，父母期待的目光再次用入脑海，又踏出一米，此时距离大门，还有三米。王林忐忑，再次踏出一步。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;就在此时，一股庞大的力道忽然出现，疯狂的涌向王林，他身体不受控制的飞快退后，一直到十多米处，才停了下来。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;站在一旁的其余少年，均都眼露嘲讽之色，在他们看来，王林就应该和自己一样，不可能有任何机会。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;惨笑一声，王林内心撕裂的伤口更大了，父母期待的目光渐渐从脑海中消散掉。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;青年神情又如之前般冰冷，淡淡说道：“不合格，下一个。”&nbsp;&nbsp;<br></p>', 1, 4, '2019-03-12'),
	(16, '回程', '<p>十一个人，最终没有一个合格，除了王林外，还有一个少女也同样踏入到两米处，但结果依然。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;当天测试完，众少年就被遣散到山下，被恒岳派弟子一一送走，送王林回家族的，还是带他们来此地的那个张姓青年，在他的身边，跟着王卓与王浩。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“王卓师弟，恭喜你成为道虚师叔的弟子，以后前途不可限量。”青年含笑，抱拳道。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;王卓脸上露出不可一世的表情，傲然道：“那是自然，师父已经说了，只待我这次回家处理完俗事，便传我修仙法术。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;王浩在一旁撇嘴，说道：“我从小就看不上你那副小人得志的表情，不就是有师父么，算什么大不了的，小爷还会炼丹呢。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;王卓轻蔑的看了眼王浩，没理会他，而是目光转动，望着一旁沉默不语的王林，轻笑道：“铁柱，怎么样，之前我就和你说了，你没那块料，你和你爹都不信，现在知道结果了吧。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;王林抬头，淡淡的看了眼王卓，对恒岳派青年说道：“上仙，我父母都在等我，还请您快些把我送回去吧。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;王卓看见王林居然不理会自己，再次冷笑道：“土包子，这辈子就待在村子里像你爹一样当木匠吧。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;青年脸中露出似笑非笑的表情，看了看眼前的三个少年，也没说话，袖子一卷，带着三人消失在恒岳派山门。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;回去的路上，罡风依旧，可王林的心绪，却是天地之别，来时期望，回时绝望。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;时间不长，王氏家族大宅子目光可及，王林睁开双眼，远远望去只见大宅子内人头怂恿，热闹似锦，一桌桌档次远远超过他爹前几日招待亲戚的宴席，把整个大院子都铺满。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;王氏家族所有人，甚至连那些在外地采购木料的族人也都回来，齐聚一堂相互庆祝，觥筹交错，熙来攘往。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;这次宴席的主角是三个人，王天水的大哥，王天水，以及他的三弟，在这三个人身边，所有的亲戚都相互上前庆祝，场面热闹非凡。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;他们眼中毫无例外都是露出羡慕之色，口中更是阿谀奉承，尽挑好词，尤其是对王林的父亲，更是磋叹不已，唏嘘往事。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“二哥，这次你家小子一定会被选中，以后你就不用干木匠活了，家里谁看见你不得叫一声二爷啊。”王林父亲的六弟，一个大腹便便的中年人，大声赞道。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“老二，当年我就看出你这辈子绝对不一般，怎么样，让我看对了吧，你这辈子应在铁柱身上了，铁柱成为仙人，你这当爹的，那可就了不得了。”王天水的五叔，眯个小眼睛，坐在王林父亲身边，举杯阿谀说道。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“二哥，你家铁柱和我家小子，这次都能被选中，咱哥俩有十几年没见了，这次说什么也要不醉不归。”王浩的父亲，王天水的三弟，端着酒杯来到面前，一脸的笑意，大声说道。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;铁柱父亲望着四周这些之前瞧不起他的亲戚们，颇感扬眉吐气，风光无限，多年的阴郁一扫而空，不过却始终有一块大石压在心头。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“铁柱啊，一定要被选中！”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“二嫂，你跟着我二哥这辈子算是享福了，有了铁柱这孩子，以后咱们十里八村的，谁不认识你啊。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“就是，二嫂，你家娃娃可比我家小子有本事，铁柱这孩子从小就明聪，真是个好孩子啊。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“铁柱娘，咱们虽说是同族，但现今同族通婚的多了去，我家闺女也到了年纪，铁柱这孩子仪表堂堂，我从小就喜欢，不如咱们两家结个亲家？”与铁柱父亲一样，铁柱母亲身边也围绕着一群女眷，相互热情的唠着乡间家常。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;铁柱的大哥，冷眼望着这一切，内心暗道等着吧，等仙人把孩子送回来，就知道结果了，到时候若是铁柱没被选中，看老二你如何收场。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;想到这里，他面上哈哈一笑，端起酒杯与身边夸奖他家孩子的亲戚，应承了几句。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;场面沸沸扬扬，繁闹不已。就在这时，忽然一道长虹从天际划过，落在大院子中，露出四人。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;四周瞬间安静下来，所有的王氏族人都兢兢战战，不敢说话。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;恒岳派青年目光一扫，暗自感叹，他当日被恒岳派收为弟子时，家乡人也是这样庆祝，一时之间颇为感慨，回头深深的看了眼王林，他知道这少年接下来要面对的事情，是成年人都无法承受的。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“大道无情……”青年摇头，身体一动，架起剑光离开此地。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“修道者不能有俗世牵挂，你们各自处理好，三天后，我再来接你们。”远远的，传来他的声音。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;铁柱父亲的大哥，一看到仙人离开，立即走出激动的望着自己儿子，问道：“道虚上仙可收你为徒？”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;王卓一脸自得之色，傲道：“那是自然，师父说我十年内，将会成为恒岳派弟子中的翘楚。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;王卓父亲大喜，重重的拍了拍儿子的肩膀，开怀道：“好！王卓你以后就是仙人了，咱们王家，有仙人了，哈哈。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;王浩的父亲，此时也是一脸焦急之色，盯着王浩，正要询问，王浩打了个哈欠，得意道：“爹，不用问了，你儿子已经是恒岳派弟子了。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;王浩父亲狂喜，拿起酒杯狠狠的喝了一口，王卓目露轻蔑之色，阴阳怪调的说道：“三叔，你生了个好儿子，把我们王家的脸都丢光了，当着所有人面大拍仙人马屁，最后又送礼，这才勉强获得一个药童的资格。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;王浩眉毛一挑，讥讽道：“我乐意，怎么的，到时候看看谁的仙法厉害就知道谁给王家丢脸了。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;铁柱的父亲，一直望着自家娃，他从铁柱脸上看到一丝没落，内心一颤，升起一股不妙的预感。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“铁柱，你……你怎么样？”铁柱他娘，满怀期望的问道。&nbsp;&nbsp;<br></p>', 1, 4, '2019-03-12'),
	(17, '苏铭', '<p>&nbsp;山，是青山。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;一片连绵不绝的大山，如龙脊延伸，弥漫在这苍茫的大地上，其内草木众多，更有鸟兽之声不断回旋。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;远远一看，山脉上凸起的部分组成了五座山峰，如同人的五指抬起，要抓向天空一样，其中一座山峰中段，一块凹进去的大石上，有一个少年人正靠在阴凉处，身边放着一个编篓，里面堆积着一些药草，散出阵阵药香，缭绕四周。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;那少年眉清目秀，只是身子单薄，看起来略有羸弱之意，穿着一件由兽皮缝制的小衫，脖子上挂着一圈白色的月牙兽骨，头有些杂乱，被其随意的用草绳束着。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;他坐在那里，手中拿着一卷十多张兽皮粘在一起的革书，在那里津津有味，摇头读着。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“蛮族有祖，开天造人，遗留万代至今……持蛮者称蛮士，入地移山倒海……有蛮纹通天，可摘取日月星辰……”少年读着读着，叹了口气。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“没有蛮体，如何成蛮……蛮士……蛮士……苏铭，你也就只能采些草药，成为部落里的凡医罢了，想成为修蛮的蛮士，遥遥无期。”少年自嘲，放下了手中的兽皮卷，看着远处的天地，起呆来。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;这卷兽皮，他已经看了不知道多少遍，不说倒背如流，也相差无几。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“天似圆，地若方，如无边，仿无际……”苏铭喃喃中，脑子里幻想着那书卷里的世界，不知不觉的，天色渐暗，可以看到远处天地尽头处似有乌云若隐若现。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;吹来的山风，也带着了一股潮气，落在那山上的诸多草木树叶上，出哗啦哗啦的声响。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;在看到那天边乌云的一瞬，苏铭忽然精神一振。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“阿公推算的果然准确，今天真有乌龙涎！”苏铭双眼露出明亮之芒，他快的站起身，将手中兽皮卷塞入怀里，左手一把抓起身边的编篓背在背上，身子一晃，极为灵巧的抓着一旁的草绳索，向着山顶爬去。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;远远一看，这少年羸弱的身子，却是爆出了极为坚韧的力量，若猿猴一般，几个起跃之间，就攀出了十多丈远。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;天地间的乌云，滚滚而来，更有轰鸣之声回旋，仿若天怒降临在这片山脉之上，那乌云连接天地，漆黑一片，转眼就越来越近了。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;苏铭此刻攀爬更快，几乎就是在这乌云扩散而来的一瞬，他已然攀临到了距离山顶约数十丈的位置，那里有一大块凸起的怪石，似天然而成，其内中空，有无数拳头大小的洞口，这怪石整体的样子，若一条盘踞在此地与山峰融合的巨蟒之。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;那怪石下，还有如獠牙的锥形，看起来触目惊心，颇为奇异，且因其是从山体上凸出的部位，几乎若悬空一般，很难攀爬过去，除非可以升空飞行。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;苏铭左手抓着绳索，右手从背后的编篓里取出一个小瓶放在嘴上咬着，慢慢挪动身子，向着那怪石相反的方向，移动了数丈，使得其左手抓着的绳索也倾斜起来后，五指扣住山壁，身子紧贴，抬头看着天空的乌云，目光炯炯，一动不动。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;片刻后，乌云盖顶，雷霆之声轰轰而起，震耳欲聋中，狂风肆虐，吹动这山脉似都欲要拔地而起一般，苏铭在这狂风中，扣住山壁的五指已然白，但却纹丝未动，看向天空的双眼内，露出了坚韧。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;那狂风越来越大，吹动这山脉草木，声响如巨兽之吼，更是将这山脉上的无数枯枝烂叶大都卷起，使得漫天遍地，全部都是草木树叶在急飞舞。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;甚至还有一些大块的枯木与小兽，也都被生生卷起，旋转中被远远抛开，出的凄厉惨叫，也被隐藏在了那风声之中。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;在这狂风中苏铭坚持了不多时，整个天空已然被乌云完全遮盖，雷鸣下，豆大的雨滴倾盆而落，整个天地在这一瞬间，似成为了被水帘遮盖的世界。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;雨水哗哗，不断地落下，越来越大，苏铭依旧死死的抓着被雨水浸湿的绳索，贴着山壁，任由雨水淋透了全身，仍然一动不动，盯着那如蟒蛇之兽的怪石下，一处凸起的若獠牙之岩。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;也不知过去了多久，那雨依旧渐增，天地一片雨雾蒙蒙，那被苏铭盯着的獠牙之岩，却是慢慢在雨水的冲洗下，泌出了黑色的液体。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;那黑液与雨水融合，连成了一条水线，向着下方流淌。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;苏铭看着这一幕，双目露出喜色，但却依旧不动，直至那泌出的黑液渐渐稀少，最终赫然化作了金色的一瞬间，苏铭双目一凝，没有丝毫迟疑，猛的松开了扣住山石的右手，身子滑落时他右手立刻从嘴边取下了那小瓶。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;他左手抓着的绳索本就倾斜，此刻随着其右手松开，整个人如同荡起一样，随着那绳索以极快的度直奔那凸起的獠牙之岩而去。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;因之前那绳索倾斜的弧度极大，且他把握的位置又颇为准确，此刻几乎是雷霆轰鸣间，苏铭已然随着绳索来到了若悬空的獠牙之岩旁边，其左手抓着绳索，右手拿着小土瓶，临近的刹那，快的放在那獠牙之岩下，借着那绳索回荡到了极限后一顿的时间，生生装满了大半瓶金色的液体。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;但就在这时，却听一声声尖锐的嘶鸣蓦然而起，约有四五条足有手臂粗细，半丈之长的黑色蜈蚣，赫然从那怪石上诸多的拳头大小的洞口内钻出，狰狞的直奔那荡在半空的苏铭扑去。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;苏铭没有丝毫意外，几乎就是在那些蜈蚣出现的一瞬，他立刻松开了抓着绳索的左手，身子以极快的度立刻下坠，避开了那些蜈蚣的扑击。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“小红！”苏铭整个人在半空急下坠，那狂风如刀子让其身子瞬间有了僵硬，尽管避开了那些蜈蚣，可如今一旦落下，将会立刻成为肉泥一般。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;可他却没有惊慌，只见一道红影从旁边的山崖上抓着一道绳索一冲而出，直奔苏铭下坠的身子而来，瞬息临近，一把拉住了苏铭，那红影是一只红色的小猴，此刻呲牙咧嘴，但眼中却是露出灵动。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;一人一猴，随着绳索落在了山崖壁上，也就是之前那苏铭读书的地方后，那苏铭眼中露出紧张，立刻收起始终抓着的小土瓶。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“小红，我们要快跑，这次我抢的乌龙涎太多了！咦，你手里抓着什么东西？”苏铭说着，看到了那小猴爪子里握着一小块黑黝黝的碎片。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;那小猴立刻神色露出警惕，爪子放在身后，连连嘶吼了几声，苏铭见时间紧迫，不再多言，而是立刻向前快走几步，身子一跃而下，抓着一条条绳索，与那小猴一起飞向下而去。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;在他们身后，嘶鸣之声滔天而起，却见那数只黑色的蜈蚣顺着岩壁快追来，如多道黑线滑落，紧追不断。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;那红色的小猴连连呲牙，不断的向着身边的苏铭嘶叫，红影闪闪间，时而回头看向后方追来的那些蜈蚣，目中露出害怕与愤愤之色。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“又不是第一次这般逃跑，那些乌龙不敢下山，你就别装了，老规矩，这些乌龙涎分你一小半。”苏铭急逃遁中，话语却显得懒洋洋的，果然此话一出口，那小猴立刻眉开眼笑，显然之前的一切都是装出来的。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;这一人一猴，对于这山脉极为熟悉，再加上那些蜈蚣不知为何，此山有些地方似不敢直接闯过，而是绕行而去，如此一来，那苏铭与小猴逃遁度尽管没有那些蜈蚣快，但他们却是时而直接向下一跳，往往都可以抓到一条绳索，几次之后，便下了山峰，钻入丛林内，不见踪影了。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;那些蜈蚣果然不敢下山，嘶吼了许久，这才带着不甘，重新爬上山顶去了。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;乌云来的快，去也同样匆匆，数个时辰后，这片山脉就恢复了正常，那乌云滚滚，向着远处蔓延离去。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;丛林边缘，苏铭与小猴走了出来，此刻天色已然渐黑，可以看到远处的天边有些微弱的火光，那里正是苏铭所在的部落。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“分给你的小半都被你喝了，你还想要？”走出丛林，苏铭全身湿漉漉的，但却不以为意，而是似笑非笑的看着那一直跟在身后，眼巴巴望着自己的小猴。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;这小猴极为通灵，是他在三年前进入此山时无意中现，彼此还生过一些矛盾，最终却是成为了好友。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;小猴眨了眨眼，挠了挠脸，露出一丝挣扎犹豫，但很快它就把手中方才抓着的那一块漆黑的碎片递给苏铭，连连嘶吼了几句，表达出了要换那乌龙涎的意思。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“好吧，再给你一些，不过这破石头我不要，你留着吧。”苏铭笑了起来，从编篓里取下那小土瓶，递给了小猴。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;这小猴连忙抓过来，喝了一口，脸上露出陶醉之意，身子更是晃悠了几下后，打出了一个酒嗝，把手中的那黑色石头碎片连同小瓶都扔给苏铭后，晃着身子几个闪跃，就消失在了丛林内。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;苏铭看着只剩下小半的土瓶，微微一笑，也不介意就放回了背后的编篓内，打量起那块漆黑的石头碎片来&nbsp;&nbsp;<br></p>', 1, 5, '2019-03-12'),
	(18, '蛮启', '<p>这是一块普通的石头碎片，只有婴儿巴掌大小，样子不规则，其上除了有一些似天然而成的纹络外，还有一个中空的小孔，似一个挂件。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;但整体看起来也没有什么出奇的地方，很寻常的样子。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;唯一有些奇异的，则是当苏铭拿着碎片时，感觉其上温暖，似有一股暖意流入身体，让人很是舒服。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“咦？”苏铭仔细看去，但看了半响，也没看出什么端倪。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“听阿公提过，据说很早的时候这里曾经是火蛮之地，如此看来，此物说不定具备了一些火蛮之力，所以才会让人感觉温暖，倒也不错。”苏铭解开脖子上挂着的月牙骨圈，将这碎片套入进去后，重新挂在了脖子上，那碎片贴着胸口，暖意更浓了一些。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“回家了！”少年迈开大步，向着远处那一片灯火之地，快速的跑了过去，此刻的他没有注意到，那怀里贴着胸口的石头碎片，有一抹微弱的光芒一闪而逝。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;随着临近，在苏铭的眼前，那微弱的灯火渐渐清晰，可以看到那是一处被诸多巨木组建的围墙环绕而成的部落。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;范围不大，约莫只有数百人居住的样子，但在苏铭眼中，这里却是让他感觉温馨。隐隐的，还有阵阵热闹的欢声传了出来，可以从那一排排巨木围墙的缝隙内，看到部落的中心处，有一团巨大的篝火，四周有诸多的族人，更有一些部落里的女子，正对篝火起舞。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;部落的大门，同样是巨木组成，当打开时，往往是被几根绳索吊起，如今紧闭，其上更有几个身子极为魁梧的大汉，穿着兽皮衣衫，皮肤极为粗糙，脖子上挂着森森白骨串，耳朵上更有骨环，充满了彪悍之意，目光炯炯的盯着四周，当看到远处跑来的苏铭时，这几个大汉都咧嘴笑了起来。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“拉苏，阿公找了你一天，你怎么才回来。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“方才下雨，是不是又去抢乌龙涎去了。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“阿公找我？把绳索扔下来，这次收获还不错。”苏铭快跑几步，来到了那大门下方，得意的拍了拍背后的编篓，大声喊了起来。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;随着一根编制的绳索从高处垂下，苏铭一把抓住，身子灵活的攀爬，几个呼吸间就爬上了大门顶端，看到了那几个守夜的族人后笑了笑，顺着一旁的阶梯快走了下去。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“这小娃娃身子灵活，胆子更是不小，多年前就敢独自一人上乌龙山去采药，看来日后部落里的凡医，一定是他了。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“可惜，他不具备蛮体，否则的话，说不定可以成为阿公一样的蛮医。”几个大汉看着远去的苏铭，微微轻叹。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;苏铭进入到了部落里，一路跑去，四周的草木宅子内，往往有人看到他后，都会善意的喊一声拉苏。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;拉苏这个名字，并非是只说他，而是指部落里一切没有到进行第二次蛮启的孩童统称。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;苏铭跑的很快，没过多久，便来到了部落的中心，看到了大量的族人环绕篝火四周，正在欢声笑语。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;篝火外，摆放着一圈耐烧的木栏，上面有许多被烧烤的冒油的大块肉，散发出阵阵香气。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;更有几个族中少女，看到跑来的苏铭后，扫了一眼，便不太在意了。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;对于这个部落来说，眉清目秀的苏铭，与其他族人有身躯上的差距，几乎每一个族人都要比他魁梧不少。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;好不容易才挤了出来，抓起一块香气四溢的烤肉，一边咬着，一边向着前方快步跑去。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;人群中，正前方的位置，坐着一个唯一穿着不是兽皮，而是粗麻衣衫的老者，这老者头发编着很多小辫，看起来似极为苍老，但双目却是颇有神韵，让人看之一眼，似就要被摄取进去一样。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;他身份应是极为尊贵，正低声说着什么，四周有几个族人陪伴，边听边点头，神色极为恭敬。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;看到远处跑来的苏铭，这老者脸上露出微笑，点了点头，示意苏铭坐在一旁，又继续和那几个族人交谈起来。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;那几个族人同样看到了苏铭，脸上纷纷露出笑容。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“我乌山部虽是小部落，但毕竟是正统的乌山传承，这一次风圳部落蛮公大寿，且我与其当年有一段交情，不能失礼。”那老者缓缓开口。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“可惜数百年前我乌山部落分裂，如今只延续下三脉，否则的话，我乌山部也是中型部落，统领附近八方时，那风圳部也是附属，可眼下……唉。”说话之人，是一个约四旬左右的男子，他是乌山部的族长，其身子魁梧，似充满了惊人的爆发力，脖子上的骨圈赫然有九个手指粗细的牙齿。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;尤其是他的脸上，更是有一道若隐若现的纹，那纹的样子极为狰狞，好似鬼神，只不过很是模糊，似始终无法完全凝固。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;苏铭看着那纹，眼中露出羡慕，通过那卷兽皮，使得苏铭知道，那是尚未成型的蛮纹，整个部落里，如今没有人具备画下蛮纹使其凝固的实力。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;就连阿公，也只是处于凝聚蛮血的第九层而已。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;但就算是这样，也使得阿公成为了乌山附近的诸多部落里的极强者，能与之比较的，唯有另外两脉分离而出的往昔同族部落，黑山部与乌龙部。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“过去之事，提之何用，没有开尘境的强者守护，是无法成为中型部落的，当年乌山部的两位开尘境先祖死亡，才是最终分裂的根源所在。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;老夫修行至今，始终无法突破这凝血第九层，达不到第十层，更达不到那极限的十一层，无法画下完全凝固的蛮纹，难以开尘……”粗麻衣衫的老者轻叹，缓缓说道。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“罢了，你们下去吧，准备礼物，明日……山痕，你身为乌山猎队魁首，便由你带队启程。”那老者站起身，看来一眼站在那乌山部落族长旁边的一个中年汉子后，转身离开了这里，向着远处走去。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;那中年汉子神色平静，听闻此话立刻躬身称是。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;苏铭连忙跟在阿公身后，一同离开了这篝火处的繁闹之地。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;一路老者没有说话，只是默默地走着，待那身后的欢语也渐渐远去后，他来到了一间草木搭建的屋舍外，走了进去。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;这屋舍不大，其内颇为简易，进屋后，这老者盘膝坐在了一旁，看了跟进来的苏铭一眼。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“又去采乌龙涎了？”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;此刻与老者独处，苏铭对这从小看着自己长大的阿公，很是尊敬，将背后的编篓放下，他拿起那小土瓶，递给了老者。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“以你的灵活，那几只乌龙是无法伤你的，不过还是少去为好……那里毕竟也属于黑山部与乌龙部的范围。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;这乌龙涎对我无用，你自己留下滋养身体好了。”老者看着苏铭，神色带着慈祥。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;苏铭点了点头，将这小瓶收起，这些年来，此物他喝下了很多，也正是因此，才具备了如此灵活的身体。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;更是多年来，阿公时常给他熬制一些药汤，使得他尽管不具备修蛮的蛮体，可却也比寻常族人要强上不少。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“还有三天，就是族里你们这代拉苏蛮启的时候了，我记得你也已经到了十六岁……需要去参拜蛮像了。”老者望着苏铭，缓缓说道。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“我乌山部落的蛮像，是传承于当年真正的乌山部，尽管不是主像，还无法与中型部落的蛮像比较，但在附近来说，却是极为强大。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;苏铭又沉默了片刻，点了点头。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“这几天不要外出，好好休息一下，三天后和他们一起去蛮启。”老者说着，慢慢闭上了双眼。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;苏铭站了半响，拎着编篓默默的离开，走向距离这里不远，那属于他的草木屋舍。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;他永远也不会忘记，在七岁那年，他与部落里所有同年龄的孩童一起环绕蛮像，去进行第一次蛮启的情景。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;身为蛮族之人，一生需要经历两次如启蒙一般的仪式，这就是蛮启，一次是七岁，一次是十六岁。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;同时，部落里的阿公也会在这个时候，借蛮像之力，选择出具备蛮体之人。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;轻叹一声，苏铭内心泛起苦涩，他渴望成为修蛮的蛮士，那兽皮卷里的一幕幕描述，让他从小就痴迷，只是现实的残酷，在他七岁那年第一次参拜蛮像时，清晰的显露出，他不具备蛮体，没有修蛮的资格。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;蛮，为世界之本，只有成为蛮士，才可以笑傲九天之上，才可以成为真正的强者！<br><br>&nbsp;&nbsp;&nbsp;&nbsp;从那兽皮卷上，苏铭小时候就知道，这个世界上有很多部落，大大小小，每一个部落都有各自不同的蛮像，这是一个部落的根本，也是让后代成为蛮的必须之物。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;参悟蛮像，若能获得感应，则可自然而然的获得修蛮的传承，不需人教，可自行修炼。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;可一旦七岁与十六岁时都失败，则表示一切永远无法被改变，苏铭的内心一直迟疑，没有看到答案时他期待，可如今距离最后一次参悟只有三天，他却害怕起来。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“这一次……能成么……”苏铭默默的回到了自己的屋舍，坐在一旁，发起呆来。&nbsp;&nbsp;<br></p>', 1, 5, '2019-03-12'),
	(19, '刺幽', '<p>深夜，苏铭躺在属于自己的屋舍内，看着漆黑的四周，久久无法入睡。阿公的话语始终缭绕在耳边，让他的脑海中不断地浮现出九年前的一幕幕。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;长叹一声，苏铭坐起身来，沉默的推开屋舍的木门，一阵清凉的风吹起他杂乱的头发，那风很凉，似随着月色而来，洒落大地。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;四周很安静，只有远处那乌山上时而传来一两声微弱的嘶鸣，部落里大都漆黑，只有那中心的篝火，还有一些零散的火苗，再就是四周的巨木围墙上，有一些火把，在这深夜里，隐隐有燃烧的啪啪声存在。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;苏铭抬头看着天空，天空月明星稀，很是璀璨，那星河似永恒一样，让苏铭的眼中，慢慢露出了迷茫。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“族里的人对我都很好……可我的样子明显与他们不太一样……或许，这与参拜蛮像失败有关吧……<br><br>&nbsp;&nbsp;&nbsp;&nbsp;不具备蛮体，就无法修蛮，只能一辈子留在这里，无法走出去，看不到兽皮革书上描述的世界……”苏铭默默地坐了下来，靠着屋舍，看着天空，眼中的迷茫更浓了。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“蛮族有祖，开天造人，遗留万代至今……持蛮者，飞天入地移山倒海……有蛮纹通天，可摘取日月星辰……”这个深夜里，乌山部落内，一个少年看着天空，喃喃着……<br><br>&nbsp;&nbsp;&nbsp;&nbsp;此刻的他，没有注意到，其脖子上挂着的那个黑色的碎片，再次有一抹微弱的光芒一闪而逝……<br><br>&nbsp;&nbsp;&nbsp;&nbsp;时间一晃，很快就到了第三天。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;作为乌山部落这一代的拉苏蛮启之日，这一天从清晨之时，整个部落就沸沸扬扬，几乎所有的族人全部走出，带着自家的拉苏，凝聚在部落中心的广场上。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;蛮启的仪式往往需要一整天的时间，尤其是这在拉苏十六岁时的蛮启，如同成年礼一样，甚至在今天这个时候，完成了蛮启的拉苏彼此还可以选择伴侣。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;阵阵带着奇异节奏的鼓声从部落内回旋，随着鼓声的出现，一个个拉苏从人群内走出，站在正中心的位置。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;这一次完成蛮启的拉苏约有三十多人，其中绝大部分都是少年，他们一个个尽管年纪不大，可身体却是极为粗壮，透出彪悍之意。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;即便是那些少女，也是如此，这样一来，人群中的苏铭，就显得格外鲜明，他的眉清目秀，更是与四周格格不入。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;虽说如此，但这里的人早就接受了苏铭的存在，尽管他的样子在他们看去有些不同，可却没有排斥，而是将他融入进来，成为了部落里的一员。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;将这些准备蛮启的拉苏们环绕后，所有的乌山部族人，纷纷跳起了原始的舞蹈，以舞祭天，用身体来表达出对天地的敬畏与祭祀。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“苏铭，听族里人说，你前几天也去了乌龙山，还取了乌龙涎回来。”在外围族人欢声歌舞时，苏拉身边传来一个憨厚的声音。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;那是一个同样年纪的少年，皮肤粗糙，身体颇为强壮，几乎整整大了苏铭一大圈，其双眼很是明亮，此刻正看着苏铭，憨笑起来。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;望着说话的少年，苏铭脸上露出微笑，这少年叫做雷辰，是他不多的族中好友。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“取了一些回来，昨天去找你，你阿爸说你与猎队上了山，等晚些蛮启结束，到我哪里分些拿走。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;那叫做雷辰的少年双眼一亮，连忙上前几步，憨笑起来。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“本来能早些回来，途中遇到一只貂鹿，你之前说需要貂鹿血下药，我就跟了上去，昨天夜里回来的。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;苏铭知道，对方说的轻描淡写，可实际上那貂鹿极难杀死，更颇为危险，此刻内心有了温暖。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;二人正说着，四周的欢歌之声渐渐平静，人群散开，只见那乌山部的阿公，穿着粗麻衣衫，手中拿着一根通体漆黑骨杖，在几个族人簇拥下，走了过来，站在了这些少年前方。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;他的出现，让四周立刻完全安静，这些少年更是目露敬畏，显然对这阿公很是害怕。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“祭祀蛮祖！”阿公目光炯炯，扫过众人，于苏铭身上略有停顿，话语间，其右手抬起那漆黑的骨杖一挥，立刻就见从不远处的人群内，匆匆走来了数十个大汉，身上都扛着一只只被绑住的野兽。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;那些野兽都还活着，此刻发出了凄厉的嘶吼，不断地挣扎，可却于事无补。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;一共四十九只不同样子的野兽，在不久之后被全部抬了上来，环绕在那些少年四周，那阵阵嘶吼的声音回荡，凝聚在一起，竟隐隐有了一股冲击灵魂的穿透之力，只不过它们身边都有乌山部的族人存在，死死的按住，不让它们挣脱开来。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;那站在这些野兽身边的族人大汉，没有丝毫迟疑，全部同时低头，左手多出了一把锋利的石刃，直接刺入这些野兽的颈脖，将它们的头颅生生割下。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;甚至那嘶吼之声，更是在这一瞬，随着头颅的砍掉，达到了极致，似可惊天动地，让这准备蛮启的三十多个拉苏中的数人，露出害怕恐惧之色。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;苏铭面色苍白，但却咬牙忍着，余光扫过身边的雷辰，却见对方眼中露出森光，竟有一副嗜血的感觉，似对此已然习惯，隐隐有些享受，与之前的憨厚，判若两人。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;更有大量的鲜血蓦然喷发出来，如同血泉一样，散发出腥臊之气，向着那三十多个拉苏洒下，落在了他们的头发上，身体上，脚下的大地上。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“你们是幸运的，因为如今没有部落的交战，可同样，你们也是不幸的……”阿公看着眼前这些少年，轻声开口。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“在我小时候，十六岁的蛮启之礼，是需要去砍下一个敌对部落之人的头颅，饮下对方的鲜血来完成蛮启。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;相比现在，你们幸运……可不幸的是，你们只看见了兽血，而没有去触摸敌人的头颅……”阿公喃喃，深深的看了一眼前方的拉苏们，拿着骨杖的右手抬起，向着前方一指。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;与此同时，他左手抬起五指握拳后猛的松开，立刻一股强大的气息从他身体内蓦然散开，这股气息卷动四周，形成了一股狂风呼啸，弥漫了整个乌山部落。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;只见在阿公的脸上，有一道道纹络幻化，那纹络交错在一起，赫然形成了一个如同蟒首之类的图腾。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;那蟒首活灵活现，如同真实存在，幻化在阿公的面部，似仰天咆哮，发出了无声的嘶吼，尽管双耳听不到，但此地乌山部所有族人，包括那强壮的族长，都不由得身子颤抖，退后几步。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“乌蟒纹……这是阿公的蛮纹……”苏铭呆呆的望着阿公，看着其面部的纹络，内心被震撼取代，他上一次看到这一幕，是九年前，如今再次看到，却是感触比当年要强上数倍之多。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“阿公一人之力，就可屠灭整个部落，如此强大的他，仅仅是凝血境第九层……不知那开尘境之人，又要强大到什么样的程度……<br><br>&nbsp;&nbsp;&nbsp;&nbsp;更不用说开尘之后的祭骨境……兽皮卷上曾说，祭骨境的强者，就算是中型部落都极为罕见，唯有那些难以想象的大型部落，才拥有不多的祭骨境之蛮。”苏铭心神震动，内心对于成为蛮士，更为渴望。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“以此地鲜血，兽躯，请乌山蛮像！”阿公如雷鸣般的声音，打断了苏铭的思绪，却见随着阿公的话语传出，立刻四周的那些野兽尸体，全部爆开，血肉与大地上的血液，连同那些少年身上的血水全部被一股无形之力吸撤，在半空中凝聚在一起，形成了一个巨大的血肉团。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“蛮启！”阿公身边的那粗壮的大汉，乌山部的族长此刻一声大喝。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;包括苏铭在内的所有拉苏，全部毫不犹豫的咬破舌尖，喷出一口鲜血，他们的鲜血急速升空，被那血肉团吸收后，一声惊天轰鸣中，那血肉团赫然化作了一个黑色的雕像。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;那是一个半身为人，半身为兽的狰狞雕像，充满了一股野蛮原始的气息，它一只手抓着一条长龙，另一只手拿着一把巨大的长枪，双目露出疯狂与嗜血。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;它的出现，就连天空都一下子有了黯淡，仿若被它的威严生生压下。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“乌山蛮像……”苏铭心脏怦怦、怦怦的跳动，好似要爆开一样，但就在这时，他脖子上的那碎片，隐有暖流散出，融入他的身体内，使得那种难受的感觉，烟消云散。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;这种感觉让苏铭一怔，下意识的正要低头去看，就在这时，阿公的声音回荡开来。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“依次上前，入蛮像内参拜！”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;话音一落，立刻就有一个少年快走几步，踏入那蛮像正下方后，整个人瞬间消失，没过多久，在其消失的地方，这少年一脸黯淡的幻化出来，退后到了一旁，不言不语。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“下一个！”说话之人，是那乌山部的族长，这大汉神情严肃，目光在这些拉苏身上扫过。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;一个个蛮启的拉苏，陆续的走上前去，消失后不久，便再次出现，直至一个女子踏入蛮像后，那蛮像散发出了赤色的光芒一闪。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;顿时整个部落的族人纷纷精神一振，就连那阿公也是凝神望去，却见那赤色的光芒连续闪烁了九下后，那少女的身影幻化而出。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“拥有蛮体！！”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“蛮像九次光芒，这表示具备蛮体！”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;那少女现身后，一脸惊喜。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“你叫做乌拉是吧，很好，到我身边来。”阿公脸上露出微笑，看着那少女，点了点头。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;看着那少女走向阿公，苏铭沉默，咬牙之下一步迈出，向着那蛮像走去，他的走出，立刻引起了四周族人的注意。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;对于这个与他们有着明显区别的少年，乌山部的族人，大都带着善意的怜悯，他们的目光凝聚在苏铭身上，直至他站在了那蛮像下方。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;苏铭深吸口气，看了一眼不远处望着自己的阿公，闭上了双眼，在其双目闭合的刹那，他感受到一股说不出的力量笼罩全身，似融入淤泥中一样，睁开双眼时，四周的一切已然完全大变。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;这里不是乌山部落，而是一处不大的空间，四周一片漆黑，唯有正前方漂浮着一个黑色的雕像，散发出赤色的光芒。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;那雕像的样子，与他在外面看到的蛮像，一模一样，散发出野蛮原始的气息。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;望着那蛮像，苏铭沉默片刻，向着那蛮像深深一拜。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;一拜许久，苏铭脸上露出苦涩，他知道，若具备蛮体，只需一拜就可让那蛮像散发出赤红光芒，可眼下的这一切，与九年前一样，没有丝毫的不同。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“我此生无法成为蛮士……”苏铭咬着下唇，长叹一声，转身就要离开这里。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;但就在他转身的一瞬间，他忽然全身一震，猛的回头看向那蛮像，呆了一下！<br><br>&nbsp;&nbsp;&nbsp;&nbsp;与此同时，他看到了自己胸口处，那被他忽视的碎片，散发出了刺目的幽光……&nbsp;&nbsp;<br></p>', 1, 5, '2019-03-12'),
	(20, '异变', '<p>&nbsp;乌山下，乌山部落内，此刻所有的族人几乎都在部落的中心，看着那群蛮启的拉苏。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;但就在这时，突然那漂浮在半空的巨大蛮像，其身蓦然一震，似颤抖一样，更有轰鸣之声骤然而起了，那声音来的太过突然，让这四周的所有族人均都一愣。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;阿公双目精光一闪，猛的上前几步，他没有去看那蛮像，而是立刻抬头看向天空，看着看着，他的眼中露出了凝重。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;此刻，更多的族人也发现了异常，纷纷抬头看向天幕。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;只见那天空上，有一道道黑气凭空出现，从四面八方疯狂的凝聚，很快就形成了一个巨大的漩涡，那漩涡之大，似占据了此地大半个天空，笼罩整个乌山八方，即便是距离很远，也可以看到此地的异变。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;那漩涡形成后，缓缓地转动，发出了轰隆隆如雷鸣般的巨响，回荡四周。更是在那漩涡内，有无数弧形的电光游走，闪电扫过，与雷鸣交错。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“蛮祖显灵！？”乌山部落内的那些族人，不知是谁喊了出来，立刻纷纷跪拜在地，神色带着敬畏与恐惧，向着天空膜拜起来。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;站立的人，唯有阿公与其身旁几个部落里的首领，除了阿公外，这些首领大汉也是神色露出惊慌。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;那天空上的漩涡转动速度越来越快，片刻后，似有狂风横扫大地，使得这乌山附近八方被完全的笼罩在内。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;那漂浮在半空的蛮像，此刻更是颤抖的剧烈，仿佛在那漩涡下，无法承受一样。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;此时此刻，在那乌山的另一边，有一处与乌山部落大小类似的部落，此部落名为黑山，如今这黑山部落里，所有的族人纷纷骇然，在他们部落的半空，一个十丈大小的蛮像赫然漂浮。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;那蛮像全身漆黑，完全不具备人形，看起来好似一只蜥蜴，此刻不断地颤抖，似要崩溃一样。在那蛮像下，有一个穿着黑色的粗麻长衫，枯瘦如骷髅般的老者，神色阴沉，不知在想些什么。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;同样的一幕，在这乌山附近的所有部落内，全部同时出现，甚至更远一些地方的部落，也均都如此。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;没有人知道，到底发生了什么，这漩涡又为何会出现，就连那乌山部落的阿公，也都忽略了方才走入蛮像体内参拜的苏铭。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;乌山部落蛮像内，那幽光一闪之间，赫然将这四周的所有空间全部占据，将此地弥漫在了那诡异的光芒中，更是在苏铭的目瞪口呆下，他看到不远处那之前需要参拜的蛮像通体一震，好似从静止沉睡中复活了一般，竟让他有一种似此像具备了血肉的错觉。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;他甚至能看到，那原本狰狞的蛮像，此刻在这幽光中，剧烈的颤抖起来，仿佛无法承受这光芒的笼罩。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;这蛮像半人半兽，左手抓着巨龙，右手拿着长枪，可如今却是在那颤抖中，那股原始野蛮的气息赫然改变，露出了一丝让苏铭不知是否还是错觉的惊恐。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;苏铭脑子里一片空白，他不知道发生了什么事情，不知道为什么会这样，他站在那里，怔住了。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;他的全身同样被那颈脖碎片的幽光弥漫，似与此地空间融合在了一起，随着那幽光越来越明亮，仅仅是片刻的时间，这蛮像的世界里，已经完全成为了幽色。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;苏铭只感觉脑子轰的一声，好似有什么壁障被一股无形的力量冲破，让他身子颤抖间，脑海内出现了一副奇异的画面。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;那是一片苍茫的大地，他似站在了半空向下俯视，在他的目光里，大地上出现了密密麻麻近百万的人群，一眼看不到边际，仿若没有尽头一样。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“这是……哪里……”苏铭喃喃，这一幕，让他心神震撼，脑海中一片空白。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;这些人群分成两个阵营，此刻全部跪在地上，向着天空膜拜，更有阵阵苍凉的鼓声回荡天地。形成了说不出的一股音浪，可触动灵魂一样，让一切听到者，都会心神一震。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;半空中，苏铭的四周，他看到了有近百个巨大的蛮像，那一个个蛮像都各自不同，散发出原始野蛮的气息，身躯看起来似有血肉，如具备了生命。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;他们此刻同样单膝跪下，向着天空膜拜！<br><br>&nbsp;&nbsp;&nbsp;&nbsp;苏铭下意识的抬起头，他看到了……<br><br>&nbsp;&nbsp;&nbsp;&nbsp;在那天空的至高之处，有两个顶天立地之人，那两个人看不清样子，但苏铭只是看了一眼，就有种如看见了天威一般的错觉，如自己成为了蝼蚁。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;他们，如真正的神灵！<br><br>&nbsp;&nbsp;&nbsp;&nbsp;其中一人，苏铭只能看到那一头紫色的长发，此人右手抬起，向着天空蓦然一挥，立刻天地色变，转眼间朗朗乾坤瞬息成为了黑暗，无数星辰闪烁，在此人挥手间，那些星辰好似被牵引吸撤，竟从天空而来，转眼凝聚在那紫发之人身旁，赫然形成了一条星辰银河。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;那银河被此人右手一指，发出轰隆滔天之音，直奔对面其敌而去，远远一看，这一幕如同天崩星河塌陷，似以整个天的力量降临在一个人身上。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;轰鸣间，那紫衣男子忽然低头，其目光横扫，蓦然与苏铭竟有了对望！<br><br>&nbsp;&nbsp;&nbsp;&nbsp;苏铭脑海轰的一声，整个人好似被一股大力推动，直接从这看似虚幻的世界里被生生轰出。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;苏铭身躯一颤，眼前一片漆黑，许久，许久，当他恢复了神智后，发现自己依旧还是在那蛮像的体内空间，四周没有幽光，仿佛一切都是他的错觉。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;苏铭呼吸急促，全身被汗水淋湿，他猛的低头看向自己脖子上挂着的那碎片，这碎片黑黝黝的，除了有微弱的暖意外，没有丝毫不寻常的地方。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“是幻觉么……还是这蛮像的记忆……方才是……如兽皮革书描述，摘天之星辰么……”许久，苏铭才从之前的那一幕画面里完全清醒过来，目中有了迷茫，脑子里很乱，沉默了片刻后，起身再次向那蛮像躬身一拜，便准备离开这里。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;只是，他这一拜才刚刚开始，立刻就有咔咔声从前方隐隐传出，只见那蛮像的脸部，出现了一道细微的裂缝，那裂缝随着苏铭的一拜的动作，还有蔓延的趋势。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;似无法承受苏铭的一拜，若完整的参拜下去，仿佛这蛮像将会崩溃一样，这诡异的一幕，让苏铭倒吸口气，他脑中对于之前的一切，此刻再无丝毫怀疑，那绝不是错觉！<br><br>&nbsp;&nbsp;&nbsp;&nbsp;更是在那蛮像出现裂缝的一瞬间，似有阵阵喃喃之语在他脑海内回荡，那喃呢的声音，让苏铭眼中露出了狂喜之色，这声音，正是他梦寐以求的，修蛮者凝血境的修行之法！<br><br>&nbsp;&nbsp;&nbsp;&nbsp;修蛮之术，无法口口相传，唯一的方法就是蛮像传承，一个蛮像的存在，对于部落来说极为重要，这关系到其存亡延续。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;当那喃呢的声音消失后，苏铭的身体也消失在了这蛮像体内的世界，出现在了乌山部落中，他刚一出现，立刻就看到四周包括阿公在内的所有族人，全部抬头看着天空，其内心一动，随之看去。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;那巨大的漩涡还再旋转，闷闷的嗡鸣回荡。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“苏铭，到我身边来。”阿公的声音在苏铭耳边响起，苏铭内心忐忑，他隐隐觉得这天空的异常，应该是与自己脖子上的碎片有关，但此事却不敢说出，忐忑中走向阿公，站在其身后。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;没过多久，那天空的异象渐渐散去，慢慢恢复如常，没有人去问苏铭是否参拜成功，毕竟没有光芒散出，则表示着失败。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;直至天空恢复，余下的拉苏再次进行参拜，当全部结束时，此番蛮启，共有两个孩子被察出在十六岁时具备了蛮体。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;这两个孩子被阿公带走，将会成为部落里的重要族人，传授一些蛮修的经验。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;至于其他的拉苏，则是带着失落，各自散去，苏铭一路沉默，但内心却是怦怦跳动越加快速，他想要把这一切告诉给阿公，可是却隐隐觉得此事太大，尤其是蛮像竟出现了裂缝。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;犹豫中，苏铭走向自己的屋舍。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;那穿着麻布长衫的阿公，在远处看了苏铭的背影一眼，露出了一丝疑惑。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;匆匆回到了屋舍，苏铭坐在木床上，低头望着自己胸口的那黑色的样子不规则的碎片，双目露出迷茫，半响，他正要取下脖子上的挂件，但却犹豫了一下，起身推动木框挡在了屋门处，这样一来若有外人进来，他也可以有时间准备。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;弄完这些，他这才重新坐下，把那石头碎片拿在手中，仔细的观察起来。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“这石头碎片到底是什么……它是小红找到的，估计是那天风太大了，把一些被腐叶吹起，使得此物露出，被小红捡到……”苏铭内心怦怦加速跳动，他隐隐觉得自己获得了一样未知的宝物。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“蛮像在它面前都要出现裂缝……不知道小红在什么位置捡的，还有没有了……”苏铭舔了舔嘴唇，双眼露出激动之意。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“我本来没有蛮体，无法得到修蛮的传承，但此物让我如今获得了修行之法！”苏铭深吸口气，压下激动，把全部精力都放在了这石头碎片上。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;也不知过去了多久，他不觉中，有困意来袭，抓着那碎片，倒在了木床上，睡了过去。&nbsp;&nbsp;<br></p>', 1, 5, '2019-03-12'),
	(21, '梦呢', '<p>&nbsp; “哥哥……”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“哥哥……”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;一个柔弱的声音，带着一种特殊的感情，回荡在属于苏铭的梦中。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“哥哥……你能听到么……哥哥……”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“哥哥……我在等你……”那声音带着疲惫，似永恒的在那里自言自语的呼唤，越来越微弱，喃呢中慢慢的消失无声。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;随着声音的虚弱，梦中的苏铭似有了钻心的疼痛，仿若有什么最为重要的事物，正随那声音远去了一样，这种感觉让他猛的惊醒。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;苏铭全身一阵冰凉，已被汗水淋湿，其面色苍白，在急促的呼吸中看向四周，眼前熟悉的一切让他渐渐心绪平静下来。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;此刻外面已经是深夜，远远的有一些鸟兽的嘶吼隐隐传来，除此之外，一片寂静，苏铭默默的坐在木床上，盯着自己手中的那碎片，神色渐渐迟疑起来。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“刚才的梦境很是奇怪……还有我之前明明没有什么困意，可观察这碎片时却不知不觉的睡着了……那个梦境……那个声音……”苏铭脸上露出迷茫，他很少做梦，如之前那样的梦境，更是从未有过，尤其是那个女子的声音，他不知为何，似很熟悉的样子。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“一切，必定有此物有关！”苏铭低头借着月光，仔细的看着手中的碎片，皱起了眉头。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“此物到底是什么……”他犹豫了一下后，索性咬破指尖，按照兽皮卷上描述，天地间的一切宝物，大都需要自身滴血后才可开启。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;苏铭从出生到现在，没见过什么宝贝，这碎片是唯一的一个，此刻挤出指尖鲜血，滴在那碎片上后，便目不转睛的期盼起来。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;可过了许久，那碎片还是没有什么变化，其上的鲜血也没有被吸收的迹象。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;苏铭挠了挠头，骨子里泛起了一股倔劲，起身采取了各种各样的方法，如牙咬，用力去掰，甚至用水浸泡，但最终那碎片还是依旧，没有丝毫不同。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;眼看天色渐亮，苏铭拿着这碎片，发起呆来，时间流逝，当远处的天边完全明亮起来，初阳升空的一瞬间，苏铭忽然脑中灵光一闪。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“此物最早放在身上，有暖意传出，或许……这就是它的作用！”苏铭毫不犹豫，立刻把这碎片再次穿在挂件上，套在脖子上，使得那碎片紧贴胸口。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;阵阵暖意散来，融入苏铭体内，好似流转了一整圈的样子，使得他全身泛起舒服的感觉，深吸口气，苏铭脑海中浮现出了从蛮像那里获得的修蛮者修行之法。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;蛮，为世界之本，此番苏铭获得的，便是修蛮的第一个境界，凝血境的修行之法。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;苏铭从那兽皮卷上知晓，在古老的岁月前，蛮祖开天造人，那个时候，人人皆为神灵，但如今无数岁月变迁，作为神话传承的蛮族，也渐渐不复传说，而是沦为了平凡。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;至于修蛮的法诀，也是从那个时候流传下来，只不过被加以改变，更为适合现在的蛮族修行，其中那第一个境界，凝血境，有十一层划分，其作用便是将修行者血脉内那甚微的蛮祖传承之血激发出来，让其凝聚。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;所谓的蛮体，实际上就是用蛮像的强大，来找出体内蛮祖传承之血较多的一些族人，唯有他们，才可以在蛮的道路上，走的更远一些。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;寻常的族人，体内蛮祖传承之血因极为稀少，故而不被认可，也就没有了成为蛮的资格，也就不会在参拜蛮像时，获得那冥冥中的存在给予的修行之法。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;但苏铭，却是极为特殊，他实际上依旧是不具备蛮体，可却因那奇异的碎片，得到了那很难口口相传，在绝大部分中小型部落唯有蛮像传承才可得到的修行之法。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“凝聚体内蛮血，从而让蛮血苏醒，最终画下属于自己的蛮纹，踏入开尘之境！”苏铭喃喃，双眼露出明亮之芒。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;他盘膝坐在那里，深吸口气后，缓缓闭上了双眼，按照那脑海中无法说出的修行之法，慢慢的沉浸在内。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;时间一点点的流逝，很快那天边的初阳已然升起老高，部落里的炊烟渐起渐散，有了热闹。猎队的族人，在几个首领带队，整装之后于亲人的守望下，走出了部落，他们要去狩猎足够的食物。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;一些年纪不大只有四五岁的拉苏，则光着身子，在部落里跑来跑去，玩耍的很是快乐，那阵阵童音的欢笑声，会让听到的族人，脸上露出微笑。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;除此之外，在阿公那里，正在与那两个成功具备蛮体的族人，讲着一些修蛮的经验，蛮士，对于一个部落来说是最重要的力量。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;乌山部落如今，随着老一辈的一一故去，眼下全部都算上，也只有二十二个蛮士而已。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;没有人注意到，这个清晨，苏铭的屋舍房门紧闭，在其内的他，盘膝中全身有微弱的血光浮现，那血光仔细看，可以隐隐看到是他身体皮肤下的一条血线，那血线正缓缓的闪烁红芒，透出妖异之感。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;如今在苏铭的身体上，浮现的血线只有一条，且还是若隐若现，始终无法完全表露出来。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;许久，苏铭睁开双眼，深深的呼出一口浊气。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“在凝血境内，出现的血线越多，则表示能达到开尘的几乎就越大一些，不过开尘境实在是太难，按照兽皮卷上描述，唯有开尘，才可勉强称蛮，可开尘境，那是可以让一个小型部落有成为中型部落资格的强者！<br><br>&nbsp;&nbsp;&nbsp;&nbsp;阿公体内蛮血已经凝聚了大半，可依旧开尘无望，四周的各个部落里，也从未听说有谁达到了开尘境。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;苏铭喃喃，开尘境对他来说太过遥远，他如今最在意的，就是自己能否成功修炼成凝血境的第一层。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;凝血境的第一层，需要浮现出三条血线。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;若是具备了蛮体，刚开始修行就可在很快的时间内，凝聚出三条以上的血线，直接踏过这凝血境的第一层，不会如苏铭这样连一条也若隐若现。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;虽说刚开始并不顺利，但苏铭却没有沮丧，对他来说，只要可以修行，则代表有了希望。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;且在他凝聚体内血脉时，他明显的感觉到，那胸口处的碎片散发的暖意更多了一些，这更让苏铭精神一振，他隐隐觉得，自己应该把握了这件宝贝的关键。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;时间一晃，便是七天，这七天里，苏铭几乎足不出户，甚至连饥饿的时候都不算太多，这一点让他疑惑了半响，毕竟那兽皮卷上曾说过，修蛮者，尤其是在凝血境时，因运转了体内血脉，故而会食量大增，可以让身体迅速增长的同时，锻造出更多的鲜血来供修行之用。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;可在苏铭身上，这一点却是没有，他想了想后，便归功于那碎片暖流的奇异作用造成。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;七天中，雷辰来过一次，给苏铭宋来了貂鹿血，顺便取走了一些乌龙涎，雷辰早在七岁那次参拜蛮像时就具备了蛮体，如今更是达到了凝血境的第四层，体内凝聚出了二十三条血线，即便是在猎队中也是中坚之人。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;临走时他犹豫了一下，有心想要安慰苏铭几句，但最终却是看着苏铭，声音闷憨的说了一句话。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“苏铭，我们从小一起长大，以后有我保护你，谁敢惹你，就是和我雷辰过不去！”他说完，用力的挥了一下手臂，咧嘴憨笑，走了出去。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;苏铭望着雷辰离去，内心很是温暖。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;部落的生活虽说简单，可却并不枯燥，几乎每个族人都有自己的事情，需要为部落的存在奉献自己的力量。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;苏铭在蛮启结束半个月后，再次背上了编篓，在与族人打着招呼中，一个人离开了部落，向着远处的丛林走去。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;踏入丛林的苏铭，仿若变了一个人般，充满了灵动之意，其身一晃，如利箭一般疾驰而去，灵巧中几个起跃就爬上了一颗大树，坐在树枝上，他脸上露出微笑，似很满意自己的速度。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“凝血第一层尽管没有全部炼成，但我的身体却是比之前最少灵活了数筹之多。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;苏铭把右手两支放在嘴边，吹了一声口哨。那口哨的声音远远散开，掀起了阵阵回音。不多时，只见远处一道红线瞬息而来，那红线速度之快，转眼临近，直奔苏铭。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;苏铭咧嘴一笑，在那红线来临的刹那身子一跃而起，向前而去，那红线内传出吱吱之声，紧追不断。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“小红，咱们今天再比一次，看谁先上山！”苏铭的声音带着欢快，话语中快速向前跳跃跑去，其身后那红线，正是那红色的小猴，这小猴脸上露出轻蔑，手里还拿着一个果核在啃着，似不屑与苏铭认真比较，抓了抓脸，懒洋洋的追了上去。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;但追着追着，这小猴却是双眼红了，吱吱的声音也尖锐起来，神色更是露出错愣，气急败坏的把手中的果核扔掉，展开全力追去。<br></p>', 1, 5, '2019-03-12'),
	(22, '陨落的天才', '<p>“斗之力，三段！”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;望着测验魔石碑上面闪亮得甚至有些刺眼的五个大字，少年面无表情，唇角有着一抹自嘲，紧握的手掌，因为大力，而导致略微尖锐的指甲深深的刺进了掌心之中，带来一阵阵钻心的疼痛…&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“萧炎，斗之力，三段！级别：低级！”测验魔石碑之旁，一位中年男子，看了一眼碑上所显示出来的信息，语气漠然的将之公布了出来…&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;中年男子话刚刚脱口，便是不出意外的在人头汹涌的广场上带起了一阵嘲讽的骚动武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“三段？嘿嘿，果然不出我所料，这个“天才”这一年又是在原地踏步！”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“哎，这废物真是把家族的脸都给丢光了。”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“要不是族长是他的父亲，这种废物，早就被驱赶出家族，任其自生自灭了，哪还有机会待在家族中白吃白喝。”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“唉，昔年那名闻乌坦城的天才少年，如今怎么落魄成这般模样了啊？”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“谁知道呢，或许做了什么亏心事，惹得神灵降怒了吧…”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;周围传来的不屑嘲笑以及惋惜轻叹，落在那如木桩待在原地的少年耳中，恍如一根根利刺狠狠的扎在心脏一般，让得少年呼吸微微急促武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;少年缓缓抬起头来，露出一张有些清秀的稚嫩脸庞，漆黑的眸子木然的在周围那些嘲讽的同龄人身上扫过，少年嘴角的自嘲，似乎变得更加苦涩了武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“这些人，都如此刻薄势力吗？或许是因为三年前他们曾经在自己面前露出过最谦卑的笑容，所以，如今想要讨还回去吧…”苦涩的一笑，萧炎落寞的转身，安静的回到了队伍的最后一排，孤单的身影，与周围的世界，有些格格不入武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“下一个，萧媚！”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;听着测验人的喊声，一名少女快速的人群中跑出，少女刚刚出场，附近的议论声便是小了许多，一双双略微火热的目光，牢牢的锁定着少女的脸颊…&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;少女年龄不过十四左右，虽然并算不上绝色，不过那张稚气未脱的小脸，却是蕴含着淡淡的妩媚，清纯与妩媚，矛盾的集合，让得她成功的成为了全场瞩目的焦点…&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;少女快步上前，小手轻车熟路的触摸着漆黑的魔石碑，然后缓缓闭上眼睛…&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在少女闭眼片刻之后，漆黑的魔石碑之上再次亮起了光芒…&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“斗之气：七段！”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“萧媚，斗之气：七段！级别:高级！”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“耶！”听着测验员所喊出的成绩，少女脸颊扬起了得意的笑容…&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“啧啧，七段斗之气，真了不起，按这进度，恐怕顶多只需要三年时间，她就能称为一名真正的斗者了吧…”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“不愧是家族中种子级别的人物啊…”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;听着人群中传来的一阵阵羡慕声，少女脸颊上的笑容更是多了几分，虚荣心，这是很多女孩都无法抗拒的诱惑…&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;与平日里的几个姐妹互相笑谈着，萧媚的视线，忽然的透过周围的人群，停在了人群外的那一道孤单身影上…&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;皱眉思虑了瞬间，萧媚还是打消了过去的念头，现在的两人，已经不在同一个阶层之上，以萧炎最近几年的表现，成年后，顶多只能作为家族中的下层人员，而天赋优秀的她，则将会成为家族重点培养的强者，前途可以说是不可限量武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“唉…”莫名的轻叹了一口气，萧媚脑中忽然浮现出三年前那意气风发的少年，四岁练气，十岁拥有九段斗之气，十一岁突破十段斗之气，成功凝聚斗之气旋，一跃成为家族百年之内最年轻的斗者！&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;当初的少年，自信而且潜力无可估量，不知让得多少少女对其春心荡漾，当然，这也包括以前的萧媚武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;然而天才的道路，貌似总是曲折的，三年之前，这名声望达到巅峰的天才少年，却是突兀的接受到了有生以来最残酷的打击，不仅辛辛苦苦修炼十数载方才凝聚的斗之气旋，一夜之间，化为乌有，而且体内的斗之气，也是随着时间的流逝，变得诡异的越来越少武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;斗之气消失的直接结果，便是导致其实力不断的后退武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;从天才的神坛，一夜跌落到了连普通人都不如的地步，这种打击，让得少年从此失魂落魄，天才之名，也是逐渐的被不屑与嘲讽所替代武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;站的越高，摔得越狠，这次的跌落，或许就再也没有爬起的机会武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“下一个，萧薰儿！”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;喧闹的人群中，测试员的声音，再次响了起来武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;随着这有些清雅的名字响起，人群忽然的安静了下来，所有的视线，豁然转移武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在众人视线汇聚之处，一位身着紫色衣裙的少女，正淡雅的站立，平静的稚嫩俏脸，并未因为众人的注目而改变分毫武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;少女清冷淡然的气质，犹如清莲初绽，小小年纪，却已初具脱俗气质，难以想象，日后若是长大，少女将会如何的倾国倾城…&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这名紫裙少女，论起美貌与气质来，比先前的萧媚，无疑还要更胜上几分，也难怪在场的众人都是这般动作武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;莲步微移，名为萧薰儿的少女行到魔石碑之前，小手伸出，镶着黑金丝的紫袖滑落而下，露出一截雪白娇嫩的皓腕，然后轻触着石碑…&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;微微沉静，石碑之上，刺眼的光芒再次绽放武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“斗之气：九段！级别：高级！”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;望着石碑之上的字体，场中陷入了一阵寂静武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“…竟然到九段了，真是恐怖！家族中年轻一辈的第一人，恐怕非薰儿小姐莫属了。”寂静过后，周围的少年，都是不由自主的咽了一口唾沫，眼神充满敬畏…&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;斗之气，每位斗者的必经之路，初阶斗之气分一至十段，当体内斗之气到达十段之时，便能凝聚斗之气旋，成为一名受人尊重的斗者！&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;人群中，萧媚皱着浅眉盯着石碑前的紫裙少女，脸颊上闪过一抹嫉妒…&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;望着石碑上的信息，一旁的中年测验员漠然的脸庞上竟然也是罕见的露出了一丝笑意，对着少女略微恭声道：“薰儿小姐，半年之后，你应该便能凝聚斗气之旋，如果你成功的话，那么以十四岁年龄成为一名真正的斗者，你是萧家百年内的第二人！”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;是的，第二人，那位第一人，便是褪去了天才光环的萧炎武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“谢谢。”少女微微点了点头，平淡的小脸并未因为他的夸奖而出现喜悦，安静的回转过身，然后在众人炽热的注目中，缓缓的行到了人群最后面的那颓废少年面前…&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“萧炎哥哥。”在经过少年身旁时，少女顿下了脚步，对着萧炎恭敬的弯了弯腰，美丽的俏脸上，居然露出了让周围少女为之嫉妒的清雅笑容武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“我现在还有资格让你怎么叫么?”望着面前这颗已经成长为家族中最璀璨的明珠，萧炎苦涩的道，她是在自己落魄后，极为少数还对自己依旧保持着尊敬的人武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“萧炎哥哥，以前你曾经与薰儿说过，要能放下，才能拿起，提放自如，是自在人！”萧薰儿微笑着柔声道，略微稚嫩的嗓音，却是暖人心肺武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“呵呵，自在人？我也只会说而已，你看我现在的模样，象自在人吗？而且…这世界，本来就不属于我。”萧炎自嘲的一笑，意兴阑珊的道武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;面对着萧炎的颓废，萧薰儿纤细的眉毛微微皱了皱，认真的道：“萧炎哥哥，虽然并不知道你究竟是怎么回事，不过，薰儿相信，你会重新站起来，取回属于你的荣耀与尊严…”话到此处，微顿了顿，少女白皙的俏脸，头一次露出淡淡的绯红：“当年的萧炎哥哥，的确很吸引人…”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“呵呵…”面对着少女毫不掩饰的坦率话语，少年尴尬的笑了一声，可却未再说什么，人不风流枉少年，可现在的他，实在没这资格与心情，落寞的回转过身，对着广场之外缓缓行去…&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;站在原地望着少年那恍如与世隔绝的孤独背影，萧薰儿踌躇了一会，然后在身后一干嫉妒的狼嚎声中，快步追了上去，与少年并肩而行…&nbsp;&nbsp;<br></p>', 1, 7, '2019-03-13'),
	(23, '斗破大陆', '<p>&nbsp;月如银盘，漫天繁星武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;山崖之颠，萧炎斜躺在草地之上，嘴中叼中一根青草，微微嚼动，任由那淡淡的苦涩在嘴中弥漫开来…&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;举起有些白皙的手掌，挡在眼前，目光透过手指缝隙，遥望着天空上那轮巨大的银月武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“唉…”想起下午的测试，萧炎轻叹了一口气，懒懒的抽回手掌，双手枕着脑袋，眼神有些恍惚…&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“十五年了呢…”低低的自喃声，忽然毫无边际的从少年嘴中轻吐了出来武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在萧炎的心中，有一个仅有他自己知道的秘密：他并不是这个世界的人，或者说，萧炎的灵魂，并不属于这个世界，他来自一个名叫地球的蔚蓝星球，至于为什么会来到这里，这种离奇经过，他也无法解释，不过在生活了一段时间之后，他还是后知后觉的明白了过来：他穿越了！&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;随着年龄的增长，对这块大陆，萧炎也是有了些模糊的了解…&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;大陆名为斗气大陆，大陆上并没有小说中常见的各系魔法，而斗气，才是大陆的唯一主调！&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在这片大陆上，斗气的修炼，几乎已经在无数代人的努力之下，发展到了巅峰地步，而且由于斗气的不断繁衍，最后甚至扩散到了民间之中，这也导致，斗气，与人类的日常生活，变得息息相关，如此，斗气在大陆中的重要性，更是变得无可替代！&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;因为斗气的极端繁衍，同时也导致从这条主线中分化出了无数条斗气修炼之法，所谓手有长短，分化出来的斗气修炼之法，自然也是有强有弱武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;经过归纳统计，斗气大陆将斗气功法的等级，由高到低分为四阶十二级：天.地.玄.黄！&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;而每一阶，又分初，中，高三级！&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;修炼的斗气功法等级的高低，也是决定日后成就高低的关键，比如修炼玄阶中级功法的人，自然要比修炼黄阶高级功法的同等级的人要强上几分武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;斗气大陆，分辩强弱，撒于三种条件武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;首先，最重要的，当然是自身的实力，如果本身实力只有一星斗者级别，那就算你修炼的是天阶高级的稀世功法，那也难以战胜一名修炼黄阶功法的斗师武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;其次，便是功法！同等级的强者，如果你的功法等级较之对方要高级许多，那么在比试之时，种种优势，一触既知武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;最后一种，名叫斗技！&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;顾名思义，这是一种发挥斗气的特殊技能，斗技在大陆之上，也有着等级之分，总的说来，同样也是分为天地玄黄四级武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;斗气大陆斗技数不胜数，不过一般流传出来的大众斗技，大多都只是黄级左右，想要获得更高深的斗技，便必须加入宗派，或者大陆上的斗气学院武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;当然，一些依靠奇遇所得到前人遗留而下的功法，或者有着自己相配套的斗技，这种由功法衍变而出的斗技，互相配合起来，威力要更强上一些武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;依靠这三种条件，方才能判出究竟孰强孰弱，总的说来，如果能够拥有等级偏高的斗气功法，日后的好处，不言而喻…&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;不过高级斗气修炼功法常人很难得到，流传在普通阶层的功法，顶多只是黄阶功法，一些比较强大的家族或者中小宗派，应该有玄阶的修炼之法，比如萧炎所在的家族，最为顶层的功法，便是只有族长才有资格修炼的：狂狮怒罡，这是一种风属性，并且是玄阶中级的斗气功法武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;玄阶之上，便是地阶了，不过这种高深功法，或许便只有那些超然势力与大帝国，方才可能拥有…&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;至于天阶…已经几百年未曾出现了武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;从理论上来说，常人想要获得高级功法，基本上是难如登天，然而事无绝对，斗气大陆地域辽阔，万族林立，大陆之北，有号称力大无穷，可与兽魂合体的蛮族，大陆之南，也有各种智商奇高的高级魔兽家族，更有那以诡异阴狠而著名的黑暗种族等等…&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;由于地域的辽阔，也有很多不为人知的无名隐士，在生命走到尽头之后，性子孤僻的他们，或许会将平生所创功法隐于某处，等待有缘人取之，在斗气大陆上，流传一句话：如果某日，你摔落悬崖，掉落山洞，不要惊慌，往前走两步，或许，你，将成为强者！&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;此话，并不属假，大陆近千年历史中，并不泛这种依靠奇遇而成为强者的故事.&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这个故事所造成的后果，便是造就了大批每天等在悬崖边，准备跳崖得绝世功法的怀梦之人，当然了，这些人大多都是以断胳膊断腿归来…&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;总之，这是一片充满奇迹，以及创造奇迹的大陆！&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;当然，想要修炼斗气秘籍，至少需要成为一名真正的斗者之后，方才够资格，而现在的萧炎隔那段距离，似乎还很是遥远…&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“呸。”吐出嘴中的草根，萧炎忽然跳起身来，脸庞狰狞，对着夜空失态的咆哮道：“我草你***，把劳资穿过来当废物玩吗？草！”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在前世，萧炎只是庸碌众生中极其平凡的一员，金钱，美人，这些东西与他根本就是两条平行线，永远没有交叉点，然而，当来到这片斗气大陆之后，萧炎却是惊喜的发现，因为两世的经验，他的灵魂，竟然比常人要强上许多！&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;要知道，在斗气大陆，灵魂是天生的，或许它能随着年龄的增长而稍稍变强，可却从没有什么功法能够单独修炼灵魂，就算是天阶功法，也不可能！这是斗气大陆的常识武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;灵魂的强化，也造就出萧炎的修炼天赋，同样，也造就了他的天才之名武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;当一个平凡庸碌之人，在知道他有成为无数人瞩目的本钱之后，若是没有足够的定力，很难能够把握本心，很显然的，前世仅仅是普通人的萧炎，并没有这种超人般的定力，所以，在他开始修炼斗之气后，他选择了成为受人瞩目的天才之路，而并非是在安静中逐渐成长！&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;若是没有意外发生的话，萧炎或许还真能够顶着天才的名头越长越大，不过，很可惜，在十一岁那年，天才之名，逐渐被突如其来的变故剥夺而去，而天才，也是在一夜间，沦落成了路人口中嘲笑的废物！&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;……&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在咆哮了几嗓子之后，萧炎的情绪也是缓缓的平息了下来，脸庞再次回复了平日的落寞，事与至此，不管他如何暴怒，也是挽不回辛苦修炼而来的斗之气旋武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;苦涩的摇了摇头，萧炎心中其实有些委屈，毕竟他对自己身体究竟发生了什么事，也是一概不知，平日检查，却没有发现丝毫不对劲的地方，灵魂，随着年龄的增加，也是越来越强大，而且吸收斗之气的速度，比几年前最巅峰的状态还要强盛上几分，这种种条件，都说明自己的天赋从不曾减弱，可那些进入体内的斗之气，却都是无一例外的消失得干干净净，诡异的情形，让得萧炎黯然神伤…&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;黯然的叹了口气，萧炎抬起手掌，手指上有一颗黑色戒指，戒指很是古朴，不知是何材料所铸，其上还绘有些模糊的纹路，这是母亲临死前送给他的唯一礼物，从四岁开始，他已经佩戴了十年，母亲的遗物，让得萧炎对它也是有着一份眷恋，手指轻轻的抚摸着戒指，萧炎苦笑道：“这几年，还真是辜负母亲的期望了…”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;深深的吐了一口气，萧炎忽然回转过头，对着漆黑的树林温暖的笑道：“父亲，您来了？”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;虽然斗之气只有三段，不过萧炎的灵魂感知，却是比一名五星斗者都要敏锐许多，在先前说起母亲的时候，他便察觉到了树林中的一丝动静武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“呵呵，炎儿，这么晚了，怎么还待在这上面呢？”树林中，在静了片刻后，传出男子的关切笑声武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;树枝一阵摇摆，一位中年人跃了出来，脸庞上带着笑意，凝视着自己那站在月光下的儿子武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;中年人身着华贵的灰色衣衫，龙行虎步间颇有几分威严，脸上一对粗眉更是为其添了几分豪气，他便是萧家现任族长，同时也是萧炎的父亲，五星大斗师，萧战！&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“父亲，您不也还没休息么？”望着中年男子，萧炎脸庞上的笑容更浓了一分，虽然自己有着前世的记忆，不过自出生以来，面前这位父亲便是对自己百般宠爱，在自己落魄之后，宠爱不减反增，如此行径，却是让得萧炎甘心叫他一声父亲武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“炎儿，还在想下午测验的事呢？”大步上前，萧战笑道武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“呵呵，有什么好想的，意料之中而已。”萧炎少年老成的摇了摇头，笑容却是有些勉强武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“唉…”望着萧炎那依旧有些稚嫩的清秀脸庞，萧战叹了一口气，沉默了片刻，忽然道：“炎儿，你十五岁了吧？”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“嗯，父亲。”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“再有一年，似乎…就该进行成年仪式了…”萧战苦笑道武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“是的，父亲，还有一年！”手掌微微一紧，萧炎平静的回道，成年仪式代表什么，他自然非常清楚，只要度过了成年仪式，那么没有修炼潜力的他，便将会被取消进入斗气阁寻找斗气功法的资格，从而被分配到家族的各处产业之中，为家族打理一些普通事物，这是家族的族规，就算他的父亲是族长，那也不可能改变！&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;毕竟，若是在二十五岁之前没有成为一名斗者，那将不会被家族所认可！&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“对不起了，炎儿，如果在一年后你的斗之气达不到七段，那么父亲也只得忍痛把你分配到家族的产业中去，毕竟，这个家族，还并不是父亲一人说了算，那几个老家伙，可随时等着父亲犯错呢…”望着平静的萧炎，萧战有些歉疚的叹道武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“父亲，我会努力的，一年后，我一定会到达七段斗之气的！”萧炎微笑着安慰道武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“一年，四段？呵呵，如果是以前，或许还有可能吧，不过现在…基本没半点机会…”虽然口中在安慰着父亲，不过萧炎心中却是自嘲的苦笑了起来武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;同样非常清楚萧炎底细的萧战，也只得叹息着应了一声，他知道一年修炼四段斗之气有多困难，轻拍了拍他的脑袋，忽然笑道：“不早了，回去休息吧，明天，家族中有贵客，你可别失了礼。”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“贵客？谁啊？”萧炎好奇的问道武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“明天就知道了.”对着萧炎挤了挤眼睛，萧战大笑而去，留下无奈的萧炎武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“放心吧，父亲，我会尽力的！”抚摸着手指上的古朴戒指，萧炎抬头喃喃道武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在萧炎抬头的那一刹，手指中的黑色古戒，却是忽然亮起了一抹极其微弱的诡异毫光，毫光眨眼便逝，没有引起任何人的察觉…[&nbsp;&nbsp;<br></p>', 1, 7, '2019-03-13'),
	(24, '客人', '<p>床榻之上，少年闭目盘腿而坐，双手在身前摆出奇异的手印，胸膛轻微起伏，一呼一吸间，形成完美的循环，而在气息循环间，有着淡淡的白色气流顺着口鼻，钻入了体内，温养着骨骼与**武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在少年闭目修炼之时，手指上那古朴的黑色戒指，再次诡异的微微发光，旋即沉寂…&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“呼…”缓缓的吐出一口浊气，少年双眼乍然睁开，一抹淡淡的白芒在漆黑的眼中闪过，那是刚刚被吸收，而又未被完全炼化的斗之气武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“好不容易修炼而来的斗之气，又在消失…我，我草！”沉神感应了一下体内，少年脸庞猛然的愤怒了起来，声音有些尖锐的骂道武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;拳头死死的捏在了一起，半晌后，少年苦笑着摇了摇头，身心疲惫的爬下了床，舒展了一下有些发麻的脚腕与大腿，仅仅拥有三段斗之气的他，可没有能力无视各种疲累武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;简单的在房间中活动了下身体，房间外传来苍老的声音：“三少爷，族长请你去大厅！”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;三少爷，萧炎在家中排行老三，上面还有两位哥哥，不过他们早已经外出历练，只有年终，才会偶尔回家，总的说来，两位哥哥对萧炎这位亲弟弟，也很是不错武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“哦。”随口的应了下来，换了一身衣衫，萧炎走出房间，对着房外的一名青衫老者微笑道：“走吧，墨管家。”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;望着少年稚嫩的脸庞，青衫老者和善的点了点头，转身的霎那，浑浊的老眼，掠过一抹不易察觉的惋惜，唉，以三少爷以前的天赋，恐怕早该成为一名出色的斗者了吧，可惜…&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;跟着老管家从后院穿过，最后在肃穆的迎客大厅外停了下来，恭敬的敲了门，方才轻轻的推门而入武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;大厅很是宽敞，其中的人数也是不少，坐于最上方的几位，便是萧战与三位脸色淡漠的老者，他们是族中的长老，权利不比族长小武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在四人的左手下方，坐着家族中一些有话语权且实力不弱的长辈，在他们的身旁，也有一些在家族中表现杰出的年轻一辈武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;另外一边，坐着三位陌生人，想必他们便是昨夜萧战口中所说的贵客武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;有些疑惑的目光在陌生的三人身上扫过，三人之中，有一位身穿月白衣袍的老者，老者满脸笑容，神采奕奕，一双有些细小的双眼，却是精光偶闪，萧炎的视线微微下移，最后停在了老者胸口上，心头猛然一凛，在老者的衣袍胸口处，赫然绘有一弯银色浅月，在浅月周围，还有点缀着七颗金光闪闪的星辰武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“七星大斗师！这老人竟然是一位七星大斗师？真是人不可貌相！”萧炎心中大感惊异，这老者的实力，竟然比自己的父亲，还要高出两星武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;能够成为大斗师的人，至少都是名动一方的强者，那样的实力，将会让得任何势力趋之若鹜，而忽然间看见一位如此等级的强者，也难怪萧炎会感到诧异武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;老者身旁，坐有一对年轻的男女，他们的身上同样穿着相同的月白袍服，男子年龄在二十左右，英俊的相貌，配上挺拔的身材，很是具有魅力，当然，最重要的，还是其胸口处所绘的五颗金星，这代表着青年的实力：五星斗者！&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;能够以二十岁左右的年龄成为一名五星斗者，这说明青年的修炼天赋，也很是不一般武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;英俊的相貌，加上不俗的实力，这位青年，不仅将家族中的一些无知少女迷得神魂颠倒，就是连那坐在一旁的萧媚，美眸中在移向这边之时，也是微放着异彩武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;少女虽然暗送秋波，不过这似乎对青年并没有什么吸引力，此时，这位青年正将所有的注意力，集中在自己身旁的美丽少女身上…&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这位少女年龄和萧炎相仿，让萧炎有些意外的，她的容貌，竟然比萧媚还要美上几分，在这家族之中，恐怕也只有那犹如青莲一般的萧熏儿能够与之相比，难怪这男子对族中的这些胭脂俗粉不屑一顾武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;少女娇嫩的耳垂上吊有着绿色的玉坠，微微摇动间，发出清脆的玉响，突兀的现出一抹娇贵…&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;另外，在少女那已经开始发育的玲珑小胸脯旁，绘有三颗金星武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“三星斗者，这女孩…如果没有靠外物激发的话，那便是一个绝顶天才！”心头轻轻的吸了一口凉气，萧炎的目光却只是在少女冷艳的小脸上停留了瞬间便是移了开去，不管如何说，在他幼稚的外貌下，也是拥有一个成熟的灵魂，虽然少女很美丽，不过他也没闲心露出流口水的猪哥状来讨人嫌武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;萧炎的这举动似乎有些让得少女略感诧异，虽然她并不是那种以为世界围着自己转的女孩，不过自己的美貌与气质如何，她再清楚不过，萧炎的这番随意动作，倒真让她有点意外，当然，也仅此而已！&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“父亲，三位长老！”快步上前，对着上位的萧战四人恭敬的行了一礼武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“呵呵，炎儿，来了啊，快坐下吧。”望着萧炎的到来，萧战止住了与客人的笑谈，冲着他点了点头，挥手道武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;微笑点头，萧炎只当做没有看见一旁三位长老射来的不耐以及淡淡的不屑，回头在厅中扫了扫，却是愕然发现，竟然没自己的位置…&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“唉，自己在这家族中的地位，看来还真是越来越低啊，往日倒好，现在竟然是当着客人的面给我难堪，这三个老不死的啊…”心头自嘲的一笑，萧炎暗自摇头武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;望着站在原地不动的萧炎，周围的族中年轻人，都是忍不住的发出讥笑之声，显然很是喜欢看他出丑的模样武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;此时，上面的萧战也是发现了萧炎的尴尬，脸庞上闪过一抹怒气，对着身旁的老者皱眉道：“二长老，你…”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“咳，实在抱歉，竟然把三少爷搞忘记了，呵呵，我马上叫人准备!”被萧战瞪住的黄袍老者，淡淡的笑了笑，“自责”的拍了拍额头，只是其眼中的那抹讥讽，却并未有多少遮掩武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“萧炎哥哥，坐这里吧！”少女淡淡的笑声，忽然的在大厅中响了起来武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;三位长老微愣，目光移向角落中安静的萧熏儿，嘴巴蠕了蠕，竟然是都没有敢再说话…&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在大厅的角落处，萧熏儿微笑着合拢了手中厚厚的书籍，气质淡雅从容，对着萧炎可爱的眨了眨眼睛武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;望着萧熏儿那微笑的小脸，萧炎迟疑了一下，摸着鼻子点了点头，然后在众多少年那嫉妒的目光中，走了过去，挨着她坐了下去武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“你又帮我解围了。”嗅着身旁少女的淡淡体香，萧炎低笑道武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;萧熏儿浅浅一笑，小脸上露出可爱的衅窝，纤细的指尖再次翻开手中那本古朴的书籍，小小年纪，却有一种知性的美感，眨动着修长的睫毛在书中徘徊了片刻，忽然有些幽幽的道：“萧炎哥哥有三年没和熏儿单独坐一起了吧？”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“呃…现在熏儿可是家族中的天才了，想要朋友还不简单么？”瞧得少女有些幽怨的光洁侧脸，萧炎干笑道武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“在熏儿四岁到六岁的时候，每天晚上都有人溜进我的房间，然后用一种很是笨拙的手法以及并不雄厚的斗之气，温养我的骨骼与经脉，每次都要弄得自己大汗淋漓后，方才疲惫离开，萧炎哥哥，你说，他会是谁？”熏儿沉默了半晌，忽然的偏过头，对着萧炎嫣然一笑，少女独有的风情，让得周围的少年眼睛有些放光武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“咳…我，我怎么知道？那么小，我们都还在地上爬呢，我哪知道。”心头猛的一跳，萧炎讪笑了两声，旋即有些心虚的将目光转向大厅内武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“嘻嘻…”望着萧炎的反映，萧熏儿小嘴泛起了柔和的笑意，目光转移到书籍之上，口中似乎是自喃般的淡淡道：“虽然知道他是好意，可熏儿不管怎么说也是女孩子吧？哪有偷偷摸女孩子身体的道理，若是熏儿寻出了那人，哼…”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;嘴角裂了裂，萧炎心头有些心虚，眼观鼻，鼻观心，不言不语…&nbsp;&nbsp;<br></p>', 1, 7, '2019-03-13'),
	(25, '云岚宗', '<p>大厅中,萧战以及三位长老,正在颇为热切的与那位陌生老者交谈着,不过这位老者似乎有什么难以启齿的事情一般,每每到口的话语,都将会有些无奈的咽了回去,而每当这个时候,一旁的娇贵少女,都是忍不住的横了老者一眼…&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;倾耳听了一会，萧炎便是有些无聊的摇了摇头…&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“萧炎哥哥，你知道他们的身份吗？”就在萧炎无聊得想要打瞌睡之时，身旁的熏儿，纤指再次翻开古朴的书页，目不斜视的微笑道武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“你知道？”好奇的转过头来，萧炎惊诧的问道武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“看见他们袍服袖口处的云彩银剑了么？”微微一笑，熏儿道武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“哦？”心头一动，萧炎目光转向三人袖口，果然是发现了一道云彩形状的银剑武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“他们是云岚宗的人？”萧炎惊讶的低声道武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;虽然并没有外出历练，不过萧炎在一些书籍中却看过有关这剑派的资料，萧家所在的城市名为乌坦城，乌坦城隶属于加玛帝国，虽然此城因为背靠魔兽山脉的地利，而跻身进入帝国的大城市之列，不过也仅仅只是居于末座武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;萧炎的家族，在乌坦城颇有份量，不过却也并不是唯一，城市中，还有另外两大家族实力与萧家相差无几，三方彼此明争暗斗了几十年，也未曾分出胜负…&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;如果说萧家是乌坦城的一霸，那么萧炎口中所说的云岚宗，或许便应该说是整个加玛帝国的一霸！这之间的差距，犹如鸿沟，也难怪连平日严肃的父亲，在言语上很是敬畏武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“他们来我们家族做什么？”萧炎有些疑惑的低声询问道武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;移动的纤细指尖微微一顿，熏儿沉默了一会，方才道：“或许和萧炎哥哥有关…”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“我？我可没和他们有过什么交集啊？”闻言，萧炎一怔，摇头否认武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“知道那少女叫什么名字吗？”熏儿淡淡的扫了一眼对面的娇贵少女武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“什么？”眉头一皱，萧炎追问道武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“纳兰嫣然！”熏儿小脸浮现点点古怪之意，斜瞥着身子有些僵硬的萧炎武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“纳兰嫣然？加玛帝国狮心元帅纳兰桀的孙女纳兰嫣然？那位…那位与我指腹为婚的未婚妻？”萧炎脸色僵硬的道武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“嘻嘻，爷爷当年与纳兰桀是生死好友，而当时恰逢你与纳兰嫣然同时出生，所以，两位老爷子便定了这门亲事，不过，可惜，在你出生后的第三年，爷爷便因与仇人交战重伤而亡，而随着时间的流逝，萧家与纳兰家的关系也是逐渐的浅了下来…”熏儿微微顿了顿，望着萧炎那瞪大的眼睛，不由得轻笑了一声，接着道：“纳兰桀这老头不仅性子桀骜，而且为人又极其在乎承喏，当年的婚事，是他亲口应下来的，所以就算萧炎哥哥最近几年名声极差，他也未曾派人过来悔婚…”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“这老头还的确桀得可爱…”听到此处，萧炎也是忍不住笑着摇了摇头武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“纳兰桀在家族中拥有绝对的话语权，他说的话，一般都没人敢反对，虽然他也很疼爱纳兰嫣然这孙女，不过想要他开口解除婚约，却是有些困难…”熏儿美丽的眼睛微弯，戏谑道：“可五年之前，纳兰嫣然被云岚宗宗主云韵亲自收做弟子，五年间，纳兰嫣然表现出了绝佳的修炼天赋，更是让得云韵对其宠爱不已…当一个人拥有了改变自己命运的力量时候，那么她会想尽办法将自己不喜欢的事，解决掉…很不幸的，萧炎哥哥与她的婚事，便是让她最不满意的地方！”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“你是说，她此次是来解除婚约的？”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;脸色一变，萧炎心头猛的涌出一阵怒气，这怒气并不是因为纳兰嫣然对他的歧视，说实在的，对面的少女虽然美丽，可他萧炎也不是一个被下半身支配心智的色狼，就算与她结不成秦晋之好，那萧炎也顶多只是有些男人惯性的遗憾而已，可如果她真的在大庭广众下对自己的父亲提出了解除婚约的请求，那么父亲这族长的脸，可就算是丢尽了！&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;纳兰嫣然不仅美丽娇俏，地位显赫，而且天赋绝佳，任何人在说起此事时，都将会认为他萧炎是癞想吃天鹅肉不成，却反被天鹅踏在了脚下…&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;如此的话，日后不仅萧炎，就算是他的父亲，也将会沦落为他人笑柄，威严大失武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;轻轻的吸了一口冰凉的空气，萧炎那藏在袖间的手掌，却已是紧紧的握拢了起来：“如果自己现在是一名斗师，谁又敢如此践踏于我？”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;的确，如果萧炎此时拥有斗师实力，那么，就算纳兰嫣然有着云岚宗撑腰，那也不可能做出如此行径，年仅十五岁的斗师，嘿，在斗气大陆这么多年的历史中，可唯有那寥寥数人而已，而且这几人，都早已经成为了斗气修炼界中的泰山北斗！&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一只娇嫩的小手，悄悄的穿过衣袖，轻轻的按着萧炎紧握的手掌，熏儿柔声道：“萧炎哥哥，她若真如此行事，只是她的损失而已，熏儿相信，日后，她会为今日的短浅目光后悔！”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“后悔？”嗤笑了一声，萧炎脸庞满是自嘲：“现在的自己，有那资格？”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“熏儿，你对他们似乎知道得很清楚？你先前所说的一些东西中，或许就是连我父亲，也不知道吧？你是如何得知的？”轻摆了摆手，萧炎话音忽然一转，问道武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;熏儿一怔，却是含笑不语武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;望着熏儿的躲避态势，萧炎只得无奈的撇了撇嘴，熏儿虽然也姓萧，不过与他却没有半点血缘关系，而且熏儿的父母，萧炎也从未见过，每当他询问自己的父亲时，满脸笑容的父亲便会立刻闭口不语，显然对熏儿的父母很是忌讳，甚至…惧怕！&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在萧炎心中，熏儿的身份，极为神秘，可不管他如何侧面询问，这小妮子都会机灵的以沉默应对，让得萧炎就算有计也是无处可施武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“唉，算了，懒得管你，不说就不说吧…”摇了摇头，萧炎的脸色忽然阴沉了下来，因为对面那在纳兰嫣然不断示意的眼色下，那位老者，终于是站起来了…&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“呵呵，借助着云岚宗向父亲施威么？这纳兰嫣然，真是好手段呐…”萧炎的心头，响起了愤怒的冷笑。&nbsp;&nbsp;<br></p>', 1, 7, '2019-03-13'),
	(26, ' 聚气散', '<p>“咳。”白袍老者轻咳了一声，站起身来对着萧战拱了拱手，微笑道：“萧族长，此次前来贵家族，主要是有事相求！”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“呵呵，葛叶先生，有事请说便是，如果力所能及，萧家应该不会推辞。”对于这位老者，萧战可不敢怠慢，连忙站起来客气的道，不过由于不知道对方到底所求何事，所以也不敢把话说得太满武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“呵呵，萧族长，你可认识她么？”葛叶微微一笑，指着身旁的少女含笑问道武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“呃…恕萧战眼拙，这位小姐…”闻言，萧战一愣，上下打量了一下少女，略微有些尴尬的摇了摇头武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;当年纳兰嫣然被云韵收为弟子之时，年仅十岁，在云岚宗中修炼了五年时间，所谓女大十八变，好多年未见，萧战自然不知道面前的少女，便是自己名义上的儿媳妇武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“咳…她的名字叫纳兰嫣然。”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“纳兰嫣然？纳兰老爷子的孙女纳兰嫣然？”萧战先是一怔，紧接着满脸大喜，想必是记起了当年的那事，当下，急忙对着少女露出温和的笑容：“原来是纳兰侄女，萧叔叔可有好多年未曾与你见面了，可别怪罪叔叔眼拙。”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;忽然出现的一幕，让得众人也是略微一愣，三位长老互相对视了一眼，眉头不由得皱了皱…&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“萧叔叔，侄女一直未曾前来拜见，该赔罪的，可是我呢，哪敢怪罪萧叔叔。”纳兰嫣然甜甜的笑道武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“呵呵，纳兰侄女，以前便听说了你被云韵大人收入门下，当时还以为是流言，没想到，竟然是真的，侄女真是好天赋啊…”萧战笑着赞叹道武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“嫣然只是好运罢了…”浅浅一笑，纳兰嫣然有些吃不消萧战的热情，桌下的手掌，轻轻扯了扯身旁的葛叶武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“呵呵，萧族长，在下今日所请求之事，便与嫣然有关，而且此事，还是宗主大人亲自开口…”葛叶轻笑了一声，在提到宗主二字时，脸庞上的表情，略微郑重武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;脸色微微一变，萧战也是收敛了笑容，云岚宗宗主云韵可是加玛帝国的大人物，他这小小的一族之长，可是半点都遭惹不起，可以她的实力与势力，又有何事需要萧家帮忙？葛叶说是与纳兰侄女有关，难道？&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;想到某种可能，萧战的嘴角忍不住的抽搐了几下，硕大的手掌微微颤抖，不过好在有着袖子的遮掩，所以也未曾被发现，强行压下心头的怒火，声音有些发颤的凝声道：“葛叶先生，请说！”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“咳…”葛叶脸色忽然出现了一抹尴尬，不过想起宗主对纳兰嫣然的疼爱，又只得咬了咬牙，笑道：“萧族长，您也知道，云岚宗门风严厉，而且宗主大人对嫣然的期望也是很高，现在基本上已经是把她当做云岚宗下一任的宗主在培养…而因为一些特殊的规矩，宗主传人在未成为正式宗主之前，都不可与男子有纠葛…”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“宗主大人在询问过嫣然之后，知道她与萧家还有一门亲事，所以…所以宗主大人想请萧族长，能够…解除了这婚约。”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“咔！”萧战手中的玉石杯，轰然间化为了一蓬粉末武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;大厅之中，气氛有些寂静，上方的三位长老也是被葛叶的话震了了震，不过片刻之后，他们望向萧战的目光中，已经多出了一抹讥讽与嘲笑武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“嘿嘿，被人上门强行解除婚约，看你这族长，以后还有什么威望管理家族！”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一些年轻一辈的少年少女并不知晓萧炎与纳兰嫣然的婚约，不过在向身旁的父母打听了一下之后，他们的脸色，顿时变得精彩了起来，讥诮的嘲讽目光，投向了角落处的萧炎…&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;望着萧战那阴沉至极的脸色，纳兰嫣然也是不敢抬头，将头埋下，手指紧张的绞在了一起武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“萧族长，我知道这要求有些强人所难，不过还请看在宗主大人的面上，解除了婚约吧…”无奈的叹了一口气，葛叶淡淡的道武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;萧战拳头紧握，淡淡的青色斗气，逐渐的覆盖了身躯，最后竟然隐隐约约的在脸庞处汇聚成了一个虚幻的狮头武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;萧家顶级功法：狂狮怒罡！等级：玄阶中级！&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;望着萧战的反映，葛叶脸庞也顿时凝重了起来，身体挡在纳兰嫣然身前，鹰爪般的双手猛的曲拢，青色斗气在鹰爪中汇聚而起，散发着细小而凌厉的剑气武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;云岚宗高深功法，青木剑诀！等级：玄阶低级！&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;随着两人气息的喷发，大厅之中，实力较弱的少年们，脸色猛的一白，旋即胸口有些发闷武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;就在萧战的呼吸越加急促之声，三位长老的厉喝声，却是宛如惊雷般的在大厅中响起：“萧战，还不住手！你可不要忘记，你是萧家的族长！”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;身子猛的一僵，萧战身体上的斗气缓缓的收敛，最后完全消失武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一屁股坐回椅子上，萧战脸色淡漠的望着低头不言的纳兰嫣然，声音有些嘶哑的道：“纳兰侄女呐，好魄力啊，纳兰肃有你这女儿，真是很让人羡慕啊！”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;娇躯微微一颤，纳兰嫣然呐呐的道：“萧叔叔…”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“呵呵，叫我萧族长就好，叔叔这称谓，我担不起，你是未来云岚宗的宗主，日后也是斗气大陆的风云人物，我家炎儿不过是资质平庸之辈，也的确是配不上你…”淡淡的挥了挥手，萧战语气冷漠的道武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“多谢萧族长体谅了。”闻言，一旁的葛叶大喜，对着萧战赔笑道:“萧族长，宗主大人知道今天这要求很是有些不礼貌，所以特地让在下带来一物，就当做是赔礼！”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;说着，葛叶伸手抹了抹手指上的一枚戒指，一只通体泛绿的古玉盒子在手中凭空出现…&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;小心的打开盒子，一股异香顿时弥漫了大厅，闻者皆都是精神为之一畅武动乾坤。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;三位长老好奇的伸过头，望着玉匣子内，身体猛的一震，惊声道：“聚气散？”&nbsp;&nbsp;<br></p>', 1, 7, '2019-03-13'),
	(27, '沙漠中的彼岸花', '<p>大漠孤烟直，长河落日圆。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一望无垠的大漠，空旷而高远，壮阔而雄浑，当红日西坠，地平线尽头一片殷红，磅礴中亦有种苍凉感。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;上古的烽烟早已在岁月中逝去，黄河古道虽然几经变迁，但依旧在。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风一个人在旅行，很疲惫，他躺在黄沙上，看着血色的夕阳，不知道还要多久才能离开这片大漠。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;数日前他毕业了，同时也跟校园中的女神说再见，或许见不到了吧，毕竟他曾被委婉的告知，从此天各一方，该分手了。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;离开学院后，他便出来旅行。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;落日很红，挂在大漠的尽头，在空旷中有一种宁静的美。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风坐起来喝了一些水，感觉精力恢复了不少，他的身体属于修长强健那一类型，体质非常好，疲惫渐消退。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;站起来眺望，他觉得快要离开大漠了，再走一段路程或许就会见到牧民的帐篷，他决定继续前行。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一路西进，他在大漠中留下一串很长、很远的脚印。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;无声无息，竟起雾了，这在沙漠中非常罕见。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风惊讶，而这雾竟然是蓝色的，在这深秋季节给人一种凉意。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;不知不觉间，雾霭渐重，蓝色缭绕，朦朦胧胧，笼罩了这片沙漠。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;大漠尽头，落日都显得有些诡异了，渐渐化成一轮蓝日，有种魔性的美，而火云也被染成了蓝色。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风皱眉，虽然他知道，沙漠的天气最是多变，但眼前实在不太正常。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一片寂静，他停下脚步。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在进大漠前，他曾听当地的老牧民讲过，一个人走在沙漠中，有时会听到一些古怪的声音，会见到一些奇异的东西，要格外谨慎。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;当时他并未在意。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;依旧宁静，沙漠中除却多了一层朦胧的蓝雾，并没有其他变故发生，楚风加快脚步，他想尽快离开这里。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;大漠的尽头，落日蓝的妖异，染蓝了西部的天空，不过它终究快要消失在地平线上了。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风的速度越来越快，开始奔跑，他不想呆在这种诡异、充满不确定性的地方。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在沙漠中，海市蜃楼那样的奇景多发生在烈日当空下，眼下不相符，这不像是什么蜃景。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;突然，前面传来轻响，像是有什么东西破沙而出，而且声音很密集，此起彼伏。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风倏地停下脚步，盯着沙漠，前方地面蓝光星星点点，像是散落一地蓝钻，晶莹透亮，在落日的余晖中闪耀着。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;那是一棵又一棵嫩苗，不足一寸高，自沙漠中破土而出，带着美丽的光泽，剔透而妖异，遍地皆是。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;短暂的停滞，随后沙沙声成片，蓝色灿灿，所有嫩苗都快速拔高，一瞬间生长起来。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;天边，蓝日下沉，即将消失，雾气弥漫，浩瀚的大漠如同披上了一层诡异的蓝色薄纱。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“啵！”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;花朵绽放的声音传出，沙漠中一片湛蓝，在夕阳即将消失的刹那，这些植物开始绽放出成片的花朵。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;大量的蓝花，晶莹点点，犹若梦幻，有些醉人，遍开在沙漠中，非常不真实。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这种植物一尺多高，通体如蓝珊瑚般透亮，花瓣一条条，妖艳而迷人，宛若盛放在另一片国度，带着魔性，吸引人的心神。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风退后一步，然而，身后也已满是这种植物，蓝光流动，一眼望不到边。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他很吃惊，仔细的看着，努力辨认，这像极了彼岸花，一条条花瓣展开，又向后弯曲，极其美丽。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;不过，彼岸花红的鲜艳，而它却是蓝色的，从未听闻有蓝色彼岸花。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;彼岸花真实存在，带着浓烈的宗教色彩，关于它有太多的传说，但楚风不信这些，只为眼前的景象而惊。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;沙漠干燥、缺水，只有极其稀少的耐旱植物偶尔可见，零星散落着。而彼岸花喜欢阴森、潮湿的环境，无论如何也不该在这里出现，还如此的妖艳。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这里遍地都是，一眼望不到尽头。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;大漠浩瀚，薄雾染蓝了落日，浸透了天边，而整片空旷无垠的沙漠都生出蓝色的彼岸花，说不出的奇异、神秘！&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一缕淡淡的芬芳飘漾，让人沉迷。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风用力摇头，小心的迈步，避开这些花，他发现只有一个地带没有这种植物，那就是———黄河古道。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在岁月中多次变迁，几经改道，它贯穿这片大漠，如今已近干涸，蓝色彼岸花开遍两岸，拥簇着它。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;花开两岸，彼此遥见。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;终于，太阳沉下去了，而也正是在此时，这些植物盛放，花开到极致，化作蓝色的海洋，流光溢彩。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;虽然暮色降临，但这里蓝色光泽缭绕，极致炫目，艳丽的出奇。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风站在黄河古道上，心中无法宁静，但是他却不作停留，沿河道快速前进。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;天色渐暗，最后的落日余晖也已不见了。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;蓝色的大漠光彩点点，而后突然间，砰然一声，所有蓝色彼岸花怒放后，竟然在一瞬间同时凋零。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;妖艳的花瓣枯萎，接着整株的植物开始干枯，它们失去色彩，耗尽生机，迅速发黄，而后碎裂，像是在一瞬间失去了数十年。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“砰！”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;最后的刹那，遍地干枯的蓝色彼岸花寸寸断裂，化成了粉末。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这诡异的景象，很难解释。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;它们如同烟花般，短暂的绚烂，美丽到极致，而后便凋零，成为灰烬。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;枯黄的粉末落在沙地间，在暮色中很难辨出，而此时蓝雾也早已消失，大漠恢复了原样，像是什么都不曾发生过，再次宁静。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风没有驻足，大步前行，在暮色中，他翻过许多座沙丘，终于见到了地平线上的山影，要离开大漠了。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;天色渐黑，他终于走出来了，清晰的看到了山地，也隐约间看到了山脚下牧民的帐篷。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;再回头时，身后大漠浩瀚，很寂静，跟平日没什么两样。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;山地前方，灯火摇曳，离山脚下还较远时就听到了一些嘈杂声，那里不平静，像是有什么事情正在发生。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;此外，还有牛羊等牲畜惶恐的叫声，以及藏獒沉闷的低吼声。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;有异常之事吗？楚风加快脚步，赶到山脚下，临近牧民的栖居地。&nbsp;&nbsp;<br></p>', 1, 6, '2019-03-18'),
	(28, '后文明时代', '<p>牛羊等牲畜疑似受惊，差点闯出栅栏，牧民阻拦，大声喝斥着，平日间几头很凶的藏獒此时低伏在地，嘶吼着，很不安。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;同时还有孩子在哭泣，女人在安抚。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;有几名老牧民在祷告，很虔诚，最后更是跪下，朝着远方的高大山脉叩首，非常郑重。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;对于楚风的到来，牧民并不吃惊，因为经常有外来者路过，借宿牧民的帐篷中。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;很长时间后，山脚下的嘈杂声才变弱。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风用热水擦洗过身体，喝着浓香的酥油茶，身上的疲惫顿时消退不少，他将身上所有的糖果都送给了孩子们。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;几个孩子小脸红扑扑，那是高原红，带着腼腆的笑，看起来非常淳朴，分完糖果后一哄而散，很快乐与满足。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;不久前这片山地究竟发生了什么，这是楚风心中的疑问，难道这里也曾经盛开近乎妖异的蓝色彼岸花？&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;帐篷中的老牧民头发花白，脸上皱纹很深，带着明显的忧色，他看向帐篷外，凝视着远方的山脉。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;很快，楚风得悉，这里果真也出现过蓝雾，在山地中缭绕，许多牲畜都因此受惊，表现的非常暴躁，想要逃离。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;不过，并没有诡异的蓝色彼岸花，且雾气很稀薄。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“您为什么要向山中叩首？”楚风问道。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“那是圣山的方向。”老牧民答道。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;昆仑，素有神山、圣山之称，神话色彩最为浓烈，从《山海经》到《淮南子》，再到《史记》等，各种古册记载繁多。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;早先时，这片山地附近只有一层较浅的蓝雾，可是有人看到，在昆仑山脉方向却是蓝的异常浓郁。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;那里大雾汹涌，晶莹通亮，氤氲沸腾，后来更是如同光束般，蓝光滔滔，炽霞一道道，非常的盛烈。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;仿佛有一轮炫目的蓝色大日被浓雾包裹着，在那里沉浮，虽然隔着很远，但不时射出光芒，如同闪电般。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;远远望去，无比神秘，蓝光璀璨，不断飞舞，极其耀眼。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;所以，一些年老的牧民向那个方向叩首，虔诚祷告。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;显然，那里的异象极度惊人，蓝雾浓郁的化不开，带着炽盛霞光，比楚风在沙漠中所见到的更甚。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这些神异景象到底因何而起？楚风思忖着。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他想到了一种可能，或许是山中的地震引发的。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;过去，有一地曾发生过类似之事，那山谷中时常突发惊雷，轰击活物。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;山脉如果发生剧烈地震，可能会引起磁场异常值超高，在电磁场效应下，云层中的电荷和山中的磁场作用，导致电荷放电，再加上极光效应，使那里色彩斑斓，成为十分特殊的雷区，迥异于常。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风并不迷信，觉得山中发生的事多半只是一种自然现象。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;可是，任他怎么解释，老牧民说什么都不信，并且对他怒视，认为他亵渎了圣山，差点把他赶走。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;事实上，的确有说不通与牵强的地方，就是楚风自己也未彻底弄透彻呢，比如不久前大漠中的妖艳之花。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他轻叹，在这“后文明时代”，有许多事都无解，尽管人们努力用过去的规律来解释，可是这世界却越来越难以被理解了。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;战争曾将大地半摧毁，险些化作废土，虽然经过漫长的恢复，大地再次生机盎然，但昔年的灿烂时代终究难以还原。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在后文明时代较为漫长的岁月中，曾经发生数起神秘变故，影响很大，可至今无解。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;清晨，红彤彤的太阳跃出地平线，灿灿的朝霞滑过山丘，落在帐篷前，草地上，尽显朝气蓬勃之象。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风告别这个部族，再次上路。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他一路向西，进入高原。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;沿途，他了解到，那神秘的蓝雾波及范围极广，最起码所走过的区域都曾出现。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“不会又是一起神秘变故吧？”他自语。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;历史上那几次，曾闹出很大的风波，到现在还没有确切答案呢。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;藏区的天空格外的蓝，云层洁白，离地面很低，仿佛稍微一伸手就能触碰到，戈壁、山地、草场都很宁静，这里像是一片与世无争的净土。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一路上，楚风听到很多传闻。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;有牧民说，圣山上的活佛苏醒了，所以才有蓝光流淌，大雾弥漫，遮拢四方。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;还有人说是金刚菩提神树在生长，要开花结果了。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“龙獒要出世了！”也有人这样说。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在当地人看来，真正的獒生在野外，可与狮虎为敌，而被人所豢养的不属于真獒。更有一种传说，圣山中有龙獒，数百年能出现一头，力大无穷，可以降魔。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;数日后，楚风临近圣山区域。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他已经了解到，沿途各地都曾出现淡淡的蓝雾，多半跟历史上的那几次一样，又是一次重大的异变。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;同时，这也意味着，又像前几次那样，一般的人始终都不会了解到内因！&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;甚至，大部分人都不知道会引发怎样的后果。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;说也奇怪，本已是深秋季节，藏区的天气应该很冷才对，可近几日来，楚风一路西行，却渐渐感觉到了一股暖意。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;前些天，黄叶凋零，败叶满地，而现在却不同了。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;树上残存的叶子仿佛又恢复了生气，不再枯黄，不再飘落。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;尤其是临近昆仑，沿途上，无论是野草，还是荆棘树木等，在暖和的天气中绿的发亮，生机勃勃。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;已是深秋，却缺少那种萧瑟。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“天气变暖了，难道是异变导致的？”楚风猜测。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;终于，昆仑山在望。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;隔着很远，就感觉到一种压迫感。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;巍峨高大的山脉，气势磅礴，连绵而恢宏，宛若天地的脊梁，横亘在那里。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;它壮阔而雄浑，无以伦比，古之大山莫出其右者。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这片山脉承载着无穷的传说，自古至今都笼罩着极其浓重的神话色彩。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;原本进入藏区后楚风就要踏上回程了，然而一路上不断听闻到昆仑山异动，曾蓝光烁烁，他想临近看一看。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“就是这里。”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风到了目的地，站在山脚下，巍巍巨山犹若神之巨城，恢宏而庞大，坐落在西部大地上，苍莽与雄浑之气扑面而来。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这仅是昆仑山脉的一小段而已，前些天的那个傍晚这块区域曾经蓝光炽盛，附近的人都看到了，不过这些日子罕有人敢真正走近。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风进山，逐渐攀登。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;山势渐高，有些陡峭，巨石横陈，路越发不好走，而一路上草木葱郁，在这深秋季节明显不正常。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“前些天真的发生过地震？”楚风观察着。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;山体上有龟裂的痕迹，地表有不少粗大的裂缝，还有明显是从山势较高处滚下的巨石，一些崖壁更是断落。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;就是这座巨山，曾经发生异常景象。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“这是什么？”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风看到一块巨石，上面有字，刻痕很深，被土石埋着大部分。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;地震后，山体小部分断落、滑坡，这巨石是从地下深处显露出来的。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;大石上有一层绿意，像是干枯的苔藓。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“西……王！”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风用手抚摸石上的刻字，认出这两个字，这是金文，一种年代非常久远的文字，古时多刻于钟鼎之上。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一般人很难辨识。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一刹那，浮思不断，楚风出神，怎么会是这两个字？&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在这里见到西王二字，怎能不让人作出联想，在那上古时代还真有西王母不成？&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“或许只是古人来此凭吊所留下的碑文吧。”楚风摇头，自我解释。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“有些不对！”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;忽然，他怔住了，在抚摸那刻字痕迹时，他发现所谓的“干枯苔藓”不正常。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“绿铜锈！”这个发现让他心中一震。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这块碑曾被尘封，埋在山体中，仔细想来也不会有什么苔藓才对，它所历经的时代十分漫长，直到剧烈地震后才重见天光。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;它竟为青铜材质！&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;可是，这么大的一块古铜属实罕见。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“殷墟出土的司母戊鼎也不足两千斤，便号称最大青铜古器，而这块铜碑……”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风推开一些土石，保守估计，这块青铜最起码也得重达五六千斤，实在骇人听闻，在古代这绝对是一块稀世重器。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;它绿锈斑斑，一看就是埋了漫长岁月的古物。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;如果是石碑，楚风还认为是前人来此凭吊所留，现在这么大一块铜碑，他不确定了。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在那遥远的古代，仅为了怀古，谁能消耗的起？&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;好久不见^_^新书圣墟开始上传，会员点击、推荐票、收藏，请大家伸伸手吧。&nbsp;&nbsp;<br></p>', 1, 6, '2019-03-18'),
	(29, '青铜昆仑', '<p>当！”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;青铜颤音震动，带着岁月的沧桑感。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风放下手中的石块，确信这是铜碑无疑，这让他有些难以置信，数千斤重的青铜古器，这可不是小事。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;如果传出去的话，肯定会引起轰动。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;上面刻着西王二字，带着时光的沉淀，古朴中亦有神秘，吸引人的心神，真不知道是何年代所留。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“什么人将它埋在昆仑山中？”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风敲打铜碑，金属颤音不断，可惜他并不是考古者，得不出什么有价值的结论。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“或许漫长岁月前这里曾经有一个极其辉煌的青铜文明。”他自语，这般推测。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他一向不迷信，即便昆仑有着太多的神话色彩，并且突兀见到刻有西王二字的青铜巨碑，他也不怎么相信那些传说。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风觉得，西王母即便真的曾经存在过，或许也只是某个远古强大部落的首领，而这里只是一片遗迹。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“剧烈的地震，引发山体磁场值异常，引动云层放电，再加上山上露出的巨大青铜，所以引来雷电缭绕？”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风越发觉得，可能就是这一原因。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他很想将这块铜碑挖出来，仔细看个究竟，奈何它埋在地下有小半截呢，没有工具在手，很难成功。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在这里徘徊片刻，他再次向上攀登。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;山上的大裂缝很宽也很深，黑乎乎，触目惊心，一片破败景象。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;沿途并没有什么路，山势险陡，巨石横陈，越向上越不好走。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;山体宏大，独自走在上面，感受着它的雄浑，再想到关于它的诸多传说，楚风心中有些异样，眺望远方，巨山与天穹相连，景象十分壮阔。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;向上攀走了千余米，在地震后的大山上前行，这可不是一段好走的路程，遇到山石松动而滚落时，非常危险。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;前面，有一大堆土石，山壁在不久前断落下一截。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;隔着有段距离，楚风就看到了异常，他露出惊容，快速前进，向上攀去，想要确定所见是否为真。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“像是铜绿！”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;远远的，他看到了一片绿痕，锈迹斑驳，就在断崖那里，这可不是一小簇，而是很大一片。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;终于到了近前，他看清楚了。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“真是！”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这可比早先看到的铜碑更让人心惊。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;山体上有一大块崖壁断落后，断开的地方、贴着山体陡峭处那里，露出尘封的真相。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;依着石壁，那里绿锈成片，古旧中带着神秘，这是铜质的建筑体，随着山体滑落部分而显现出。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;三座青铜房屋，古朴而寂静，背靠石壁，修建在那里，有些部分被土石埋着，但还是能看到大部分。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;青铜房舍式样古老，恢宏中带着历史的厚重感。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;铜质房屋上，那些瓦片也是青铜所铸，一块又一块，整齐而有规律，一眼望去，像是绿色的鳞片铺盖在那里。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风着实吃惊，心中无法平静下来。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这可是轰动性的发现，青铜器物，这么的庞大，这可是几座铜质房屋啊，建在昆仑山上，曾被深埋着。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这是什么年代的东西，是何人所建？&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;依照他的猜测，这片区域绝对曾经有过一片璀璨的青铜文明，存在岁月极其古远，现存的史书都不曾记载。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;只是在心惊的同时，他也有不安与不解。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;司母戊鼎号称最大青铜古器，可现在看来不仅比那铜碑轻，和眼前的房屋比起来，那就更加不能算是重器了。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;毫无疑问，这种房舍的建造比之铸鼎更难。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;青铜房屋是铸成的，浇铸在一起，恢宏与肃穆中透着神秘。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这如果被外界发现，一定会被视为瑰宝级青铜大器，过去从未见过样庞大的器物，具有颠覆性。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风遇事一向很镇定，但今日却不能从容了，西部大地上的山脉中竟然有这种青铜遗迹，实在惊人！&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他尝试用力推开一扇青铜门，略微刺耳的金属摩擦声传出，铜门被打开了。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风没有立刻进去，在外面站了片刻，通风后才谨慎的迈步，里面很安静，仿佛与外界隔绝了，落针可闻，但光秃秃，什么都没有。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;无论是地上还是墙上，没有其他任何器物。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;其他两座青铜房屋也是如此，内部空旷，并无桌椅等。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;仔细查看，没有遗露，铜屋就是空的。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风退出来，看着三座青铜房屋，心中有着太多的不解，这是古人居住的建筑物，还是祭祀时所用？&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在那遥远的个古代，这真的太奢侈了！&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;史书中有记载，殷商时代的司母戊鼎铸造时足足动用了两三百名工匠，密切配合才艰难完成。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;古时，如果铸造三座铜屋，那得有多么大的难度？！&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风在这里驻足良久，最后再次上山，几个小时后，终于快要到山顶了，距离还有两百米左右，他出了很多汗水。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他的体质超好，身体修长，很强健，只是登这样的大山，长时间下来还是非常疲累。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;临近山顶，极目远眺，山脉起伏，大地壮阔，而个人则显得无比渺小，如同尘埃。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;站在巨山上，抬头看向那近在咫尺的碧蓝天穹，让人心胸都为之舒畅，可以忘记一切烦忧，个人的荣辱，所有的一切，都显得微不足道了。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这里的海拔已经非常高，但却未见到积雪，并且依旧有草木，这让楚风觉得有些奇怪。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“有雷击的痕迹！”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风发现山上有焦痕，曾被雷电劈过，有大块区域的草木化成灰烬，一片乌黑。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;除此之外，山石都被劈开了，大片的山体损毁。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这就更加让他确信，数日前这里大雾浓重，蓝光缭绕，其实是闪电，这片地方曾经遭遇过雷击。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;前方的路不好走，巨石堆积，楚风绕行，想从山体另一侧上去。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;然而，当他转过来后，到了山顶的另一边时，身体微微发僵，瞳孔一阵收缩，第一次感觉这般的震惊。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;就是见到铜屋时，他也没有如此。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这边的山体曾经发生过滑坡，土石翻滚下去很厚的一层，露出金属质感。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“铜山！”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;土石大面积翻落下去后，露出的景象实在过于惊人。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;山顶这块区域竟然是铜质的，曾被土层埋在下方。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这可不是一小块区域，离山顶近两百米远，这大片地带都已露出青铜质感。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这怎能不让人产生其他想法，这座山是青铜的，外表掩盖着土石，下方才是“真景”？&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;实在是匪夷所思！&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;真相如何，不得而知，但最起码这两百米高的山体是青铜，足以惊世。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风被惊住了，这是昆仑中的一座山，内部是铜质，颠覆了他的思绪，考验过去久已成型的观念。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他不信那些玄异的事，一向将所谓的传说当故事听。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;但眼下这里透着诡异，解释不清。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;被雷击后，露出青铜山顶真相。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这里的事着实有些惊世骇俗！&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风沿着铜山向上攀，距离山顶将近百米时止步了，因为这里异常陡峭，是一片青铜绝壁，很难向上了。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;同时，他闻到了一股清香，随风送来。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这块地方都是铜料，是冰冷的金属，早先并没有看到草木，光秃秃。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风仰头观看，仔细寻找。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;果然见到了，在青铜山崖上，有一株植物！&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;那里已经属于山顶区域，他退回后，从其他方位再次寻找可以向上攀爬的路径，逐渐接近，想看的仔细一些。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;不久后，虽然依旧到不了山顶，但却看清了。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一株绿油油的小树，三尺多高，它竟然扎根在青铜崖壁上，结有一朵花，含苞待放。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风确信自己没有看错，那里没有土石，有的只是青铜，它扎根在铜质的崖壁上，实在匪夷所思。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这过于惊人，有些不可理解。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他换了个方位，选择了一处更利于向上攀的地方，距离更近了，看的清楚，小树的确是扎根在青铜上！&nbsp;<br></p>', 1, 6, '2019-03-18'),
	(30, '奇树与猛兽', '<p>昆仑山顶，最高崖壁处，一株三尺小树独自生长。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;视青铜为土壤，作养料，顽强扎根，通体绿莹莹，带着光泽。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;树干有手腕粗细，它虽不高大，但却带着老树皮，张裂着，如同一层层鳞片，竟有苍劲感。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;它的叶片如同绿玉雕刻而成，通透中富有灵性，形状如同幼儿的手掌，托着一些晶莹的露珠，清风拂过时，如同洁白珍珠在碧玉盘上滚动。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在小树的顶端长有一个花骨朵，拳头大，通体银白，但带着金斑，于绝壁中待放，已经清香飘漾，很美。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;妖异的小树，静静傲立着。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风尝试了几次，青铜山体这一侧的确上不去，他决定冒险，从满是滚石的那边攀登，但需要格外小心，不然可能会丢掉性命。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他从铜壁这一边退下，来到平坦地带，绕着山体而行，同时向上观望。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“它怎么会生长在青铜上？”楚风想不通。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他只能归根于，曾发生过数起变故的世界越来越无法让人理解了。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风心绪已平静，他皱着眉，细想着这一切，奇异的植物，诡异的青铜山，这一切都不符合常理。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一道身影在他心间浮现，因为那个人曾说过一些话，当时他并未在意，可眼下却让他有些触动。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“有一天，也许路边的一株杂草都会结出拳头大的鲜红果实，我们所见到的平凡或许都将不在了。”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这是林诺依说的话，很平淡，像是随口而出。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;就像她跟楚风说分手时一样，略有淡漠，声音有些远，像是她站在很高处，说了那些话语。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风以为她是在说两人间的事，无论是人生还是情感等没什么不可改变。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“她言有所指？”&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在这后文明时代，世界曾发生过数起变故，虽然绝大多数人都不知道内情，但总有小部分人知晓真相。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;林诺依到底知道什么？&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;心中浮现她的身影，楚风叹了一口气，虽然有些怅然，但过去的就让它过去吧。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他再次抬头，看向青铜山顶，露出异色。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;她真的言有所指吗，沿着她的思路，许多平凡或许都将要改变，那么本就不平凡的稀有异树呢？&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这样的一株小树，即便在没有经历过异变前，它也肯定非凡！&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;脚下的乱石很多，楚风已经走到青铜山体的边缘了，这边的路太难走。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;突然，一阵狂风涌来，楚风眼皮直跳，他看到有一片阴影突然出现在地上，马上就要把他遮住了。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;有什么东西在接近！&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;他反应敏捷，身体矫健，直接一个翻身，快速躲避了出去，他在山上迅疾翻滚出去一段距离，在这个过程中更是取出防身的折叠弩，有效而快捷的组装。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在户外，尤其是一个人旅行，怎么能没有一些防身的器具呢，楚风转身就是一支钢箭，砰的一声射了出去。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;同时，他看到了那是什么！&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风的脸上浮现出难言的惊容，这生物的体形太大了，远超同类。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一头金色的猛禽，羽翼很亮，伸展开来足以五六米长，从天空中俯冲而下，刚才险些抓在他的身上。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;当的一声，钢箭横过，击中不远处的一块大青石，火星乱溅，擦着凶禽而过，竟被它躲开了。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;同时，那锋利的大爪子，擦中地上的土石，摩擦声刺耳，令人觉得发瘆，它腾空而上，风声呼呼。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风脊背发寒，刚才如果不是他反应迅疾，被这凶禽抓中的话，后果不堪设想。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一般的鹰隼可以轻易抓裂野兔等猎物的头骨，这么大的一头金色猛禽，其力量可想而知，刚才如果稍微迟一步，那场面绝对很惨。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风第一时间倒退，选择有利地势，背靠在一块巨石上，然后他手持折叠弩，对准天空，谨慎的戒备着。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;半空中，金色的猛禽盘旋，太凶猛了，贴着山体，荡起大风！&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;从未见过这么大的禽类。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;看它的样子，像是金雕，没有杂毛，通体光亮，体形大的吓人，非常威猛，金色瞳孔灿灿，野性十足，身上带着戾气。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一般的金雕怎么可能会这么大，这一头算是异类，太不正常了！&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;如果是在古代，有些部族或许会将这头金色的猛禽当成大鹏鸟。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;因为，古人进行记载时，多有些夸张。五六米长的金色凶禽绝对算是一个异数，如果出现的话，肯定会引发大波澜。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;尤其是在这昆仑山地带，更会平添神秘色彩。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;金色巨禽很凶，但是却没有立刻俯冲过来，而是在盘旋，它有着非同一般的敏锐，显然也意识到了楚风手中那把弩的威力。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;忽然，楚风闻到了一股腥味。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;三只雪豹从山体下方缓缓而来，悄无声息，瞳孔幽幽，嘴巴都粘着血迹，雪白獠牙锋利，显然不久前曾经猎杀过什么生物。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;它们盯着楚风，身体略微弓起，同时又看向半空中的金色猛禽，非常忌惮，发出不安的低吼声。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;三只雪豹远比一般的同类强壮，锋利的爪子泛着寒光，有力的躯体做出随时会扑杀的预备动作。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风蹙眉，没有想到会遇上这种危险，空中有金色凶禽，地上有奔跑极速的豹子，他处境堪忧。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;突然，三头雪豹皮毛抖动，颈部的毛根根倒竖了起来，它们快速躲避，腾跃而起，进入乱石堆中。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;无声无息，山上多了一头牦牛，通体乌黑光亮，黑毛跟绸缎子一样，流动乌光，一对粗大的牛角扬起，对着天空。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这称得上是一头耗牛王，足有一丈多长，四肢粗壮，体格巨大，跟一堵黑色的小山似的，站在那里。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这让楚风心惊，这头黑色的牦牛这么庞大，走路居然跟雪豹一样没有声息，也是突兀出现的，没能提前发现。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;而且，那三头雪豹很怕这头乌黑光亮的大牦牛，躲进了石碓中，这很不正常！&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;黑色的大牦牛抬头看了一眼半空中的金色凶禽，而后便不动了，静静站在那里，望向青铜山顶。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这三种生物怎么都到了这里？&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风知道身在险境中，没有轻举妄动，他在等待机会离开。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;远处，六七道兽影在奔跑，向山上而来，速度很快，露出白色獠牙，凶性一览无余。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;那是六头狼，都很高大，强壮于同类，为首的“头狼”通体雪白，只有一只眼睛，绿光幽幽，显得最为凶狞。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;它们到了近处后，短暂驻足，看到黑色的大牦牛后略有焦躁，又看向金色的凶禽，显出不安来。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;突然，平静被打破了，六头凶狼一起发力，沿着乱石区域同时向着山顶冲去。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;与此同时，三只雪豹也动了，风驰电掣，速度极快，向着青铜山顶奔行。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风倒退，他想就此离开。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;山顶附近，兽吼声不断，它们争着上去。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;砰！&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一声剧震传来，一头雪豹的面部变形了，血肉模糊，重重的从山上摔了下来，它是被一道黄影拍翻的。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;那像是一道闪电，太快了，斜刺里突兀的现身，冲入兽群中。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;那是一头獒，脖子那里的毛浓密而长，如同雄狮的鬓毛，个头跟一般的藏獒差不多大，爪子上带着豹血。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;但是，它太迅猛了，一跃就是数米远，扑向前方。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;狼嚎声响起，很惨烈，有血花溅起，一头青狼被咬断脖子，甩飞了出去。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;另一头狼则被猛犬撞的飞起，砸在石壁上，当场萎靡。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;“这是藏区传闻中的真獒！”楚风吃惊。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;据当地人讲，真獒生活在野外，可与虎豹为敌，数量极其稀少，罕被人发现。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;而这头真獒比传闻更厉害，快如闪电，闯入兽群中，才一接触而已，就解决掉一豹两狼。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这是獒王，楚风猜测，它甚至更厉害。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;那头獒再次跃起，足有七八米远，大爪子落下，威力堪比熊掌，噗的一声，将一只野狼的眼睛拍出，翻滚了出去。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;落地的同时，这头獒又扑倒一头雪豹，吼声骇人，它们在那里翻滚，这是野性的释放。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;雪豹倒在血泊中，喉咙被咬穿，眼看活不成了。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这头獒并没有受伤，颈项那里如同狮鬓的长毛炸立着，个头虽然不大，但是如同狻猊兽般，有股特别的气势，它再次跃起，冲向另外几头猛兽。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风几乎有些不敢相信，竟有这般威猛的獒犬！几个起落而已，就快要将那些野兽解决完了。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;最后一头雪豹也死了，只剩下那头独眼的银色“头狼”拼命奔逃，向山下冲去，想活着离开这里。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;可是，几个起落间，它就被那头獒给追上了，一张血盆大口张开，狠狠的咬断它的脖子，整颗狼头都差点断落下来。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;就这样九头凶猛的野兽在短时间内都被解决了。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风紧握折叠弩，严阵以待，在那里戒备着，这个地方实在太危险了！&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;那头獒安静了，嘴上全是兽血，但不是它自己的，它一动不动，仰头看着青铜山崖上的那株小树。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;它个头不是很高大，但是，却显得格外的威猛，土黄色的鬓毛尽染其他猛兽的血，威势凛凛。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在这个过程中，那头黑色的牦牛一直在盯着青铜山上的小树，自始至终都没有动过，竟然十分镇定。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;而那头金色凶禽在半空盘旋，也跟不久前一样，俯视着这里。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;三只诡异的生物都很沉稳，像是有人的灵性，非常沉得住气，目标是那株小树，但却没有立刻行动，似在等待着什么。&nbsp;<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;楚风深感惊诧，因为这三头生物实在非同寻常。&nbsp;&nbsp;<br></p>', 1, 6, '2019-03-18'),
	(31, '剧本不对', '<p>2008年，4月5日。<br>&nbsp;&nbsp;&nbsp;&nbsp;周六。<br>&nbsp;&nbsp;&nbsp;&nbsp;阳城一中，高三（4）班教室。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;方平花了半小时，总算确定了一件事，不是做梦，不是拍戏——废话，拍戏能让自己那些同学返老还童，这剧组可以上天了！<br><br>&nbsp;&nbsp;&nbsp;&nbsp;等确定了重生的事实，方平小小的忐忑片刻，紧接着便坦然接受了。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;作为新世纪的五毒青年，谁还没点重生的经验？<br><br>&nbsp;&nbsp;&nbsp;&nbsp;就算自己没重生，网上一大把，光看看就知道，这是矮矬穷逆袭高富帅的至高法宝！<br><br>&nbsp;&nbsp;&nbsp;&nbsp;再说了，自己要钱没钱，要妞没妞，重生了什么都不干还能多活一些年，怎么算都不亏。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;好不容易熬到下课铃响，熟悉中带些陌生的老师，硬是拖了好几分钟堂才恋恋不舍的离去。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;至于老师临走时最后说的那句话，方平就当自己听岔了。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“武科报名，下个星期开始了，有想法的同学做好准备。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;方平没当回事，他听成了“文科”报名，高考都是提前报名的，不过一般都由学校代劳，没必要再提醒才对。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;关键的关键，高三（4）班是理科班，和文科无关吧？<br><br>&nbsp;&nbsp;&nbsp;&nbsp;不过有了重生的事，方平哪还在意这点芝麻绿豆的小事。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;这当前，方平想的是拳打杰克马，脚踢马华腾才对。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;尽管08年这个时间段有些晚了，可重生者不干点出人预料的事，能叫重生者？<br><br>&nbsp;&nbsp;&nbsp;&nbsp;又或者不从商，改从政？<br><br>&nbsp;&nbsp;&nbsp;&nbsp;方平心里暗暗盘算，也没心思和四周的同学闲聊。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;这些小年轻，哪能想象到自己脑海中的雄伟目标，从今天起，咱们就不是一个世界的人了！<br><br>&nbsp;&nbsp;&nbsp;&nbsp;正当方平为自己的未来谋划添砖加瓦之际，前排那个长的五大三粗的杨建同学，忽然转头问道：“方平，陈凡，你们报不报名？”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;方平对杨建印象可谓深刻，倒不是杨建帅破天际。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;关键在于，大学毕业后，方平他们高中同学聚会，刚毕业的杨建，居然养了一脸的络腮胡子，差点让方平以为杨建他爸也来参加同学会了。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;打那以后，方平就没忘记过这位络腮胡子同学，特征太明显。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;心里想着事，方平一时间也没想着接话。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;方平同桌，被班上同学戏称为“平凡二人组”成员之一的陈凡，这时候则是摇头道：“我不报名了，浪费钱而已。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;报个名就要一万，指定考不上，有这么多钱，大学一年生活费加学费都差不多够了。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;杨建有些唏嘘道：“也是，可总有些不甘心，不试试，就怕后悔一辈子。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;杨建的同桌，也是个男生，名字方平有些模糊了，此时也转头加入讨论，脸色黯然道：“这是咱们唯一一次鲤鱼跃龙门的机会，可惜，跟我们无关。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;三人又是唏嘘，又是遗憾，听的方平满头雾水，莫名其妙。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;报名？<br><br>&nbsp;&nbsp;&nbsp;&nbsp;刚刚老师说的“文科”报名？<br><br>&nbsp;&nbsp;&nbsp;&nbsp;报名费就要一万？<br><br>&nbsp;&nbsp;&nbsp;&nbsp;这可是08年，要是没记错，阳城这时候的市区房价也才4000一平左右，考试报名要这么多钱？<br><br>&nbsp;&nbsp;&nbsp;&nbsp;这几个家伙没说错吧？<br><br>&nbsp;&nbsp;&nbsp;&nbsp;又或者被人骗了？<br><br>&nbsp;&nbsp;&nbsp;&nbsp;方平刚想插话问问，同桌陈凡扶了扶眼镜，脸色坚毅道：“就算不考武科，考文科，也不一定一辈子无法出人头地！<br><br>&nbsp;&nbsp;&nbsp;&nbsp;社会上也有武道培训班，等我们毕业了，挣了钱，到时候也可以进修。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;就算不如武科生，起码还有希望！”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;这话一出，杨建的同桌也面色激动道：“不错，考个文科名校，毕业了出来，工资待遇也不会低！”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“我还是想试试……”杨建有些犹豫，他家境还算不错，加上身体健壮，不试试不甘心。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;对于杨建想要尝试的话语，陈凡二人也没阻止，尽管希望渺茫，可机会就在眼前，总有人不甘心。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;虽然大家都还年轻，可也明白，这时候劝阻杨建，真要让杨建错过了机会，那就结下大仇了。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;三人说的火热，此刻的方平却是满脸懵。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;什么情况？<br><br>&nbsp;&nbsp;&nbsp;&nbsp;咽了咽口水，方平看了三人一会，没看到有开玩笑逗趣的意思，这时候方平总算察觉有些不对劲了。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;正想问话，结果再次被人抢了话语。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;方平他们旁边的课桌，原本有两个男生窃窃私语，这时候大概是觉得人少了讨论不够味道。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;等方平他们这边安静下来，旁边那桌的一个平头男生就面带激动，喜不自胜道：“杨建，陈凡，你们昨晚上网看新闻了吗？”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;杨建二人摇了摇头，高考在即，现在家里管的都严，哪有时间上网。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;见二人不知情，方平和另一位男生也是茫然，平头男生顿时笑道：“太可惜了！<br><br>&nbsp;&nbsp;&nbsp;&nbsp;昨晚可是爆出了大新闻！<br><br>&nbsp;&nbsp;&nbsp;&nbsp;你们知道吗？<br><br>&nbsp;&nbsp;&nbsp;&nbsp;马宗师突破八品了！<br><br>&nbsp;&nbsp;&nbsp;&nbsp;马宗师今年还不到40岁，已经是青年一代第一武道强者。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;昨天企鹅集团，正式向谷歌亚太区总裁，老牌八品强者泰姆下了挑战书！”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“什么？”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“真的假的？”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“马宗师突破八品了？他不是前几年才突破七品吗？”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“不敢置信！”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“张浩，快说说，是公开挑战，还是私下进行？”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“八品之战，好想去看，可惜咱们根本没资格去观战……”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;高三学生，临近高考，这时候放学还上网的不多，所以昨晚爆出的消息，尽管已经引起轰动，可班上知道的人却是不多。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;平头男生，也就是张浩，刚刚说话声音不小。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;等他说完，方平这伙人还没来得及接话茬，附近听到的同学都亢奋了起来。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;而张浩，这时候也享受到了万众瞩目的愉悦，满脸喜色道：“是真的！<br><br>&nbsp;&nbsp;&nbsp;&nbsp;马宗师真的突破了，不止我们，全世界谁敢相信他会这么快突破八品？<br><br>&nbsp;&nbsp;&nbsp;&nbsp;所以挑战书一出，全世界都震惊了！<br><br>&nbsp;&nbsp;&nbsp;&nbsp;只要这次马宗师战胜泰姆，企鹅集团就能大举进军亚洲各国，成为亚洲霸主集团之一！<br><br>&nbsp;&nbsp;&nbsp;&nbsp;再过些年，马宗师一旦突破九品，那企鹅集团就能成为世界霸主集团之一了！”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“天，这也太快了，八品宗师境！”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“宗师榜排名更新了吗？马宗师这是要杀进前三十啊！”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“去去去，还前三十，要我说，前二十都没问题。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“不至于吧，马宗师毕竟刚突破，哪有那么快上升到前二十，除非战胜泰姆之后，还有戏。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“……”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;这时候的同学们，已经各自议论了起来，每个人眼中都流露出兴奋、崇拜、激动、渴望的神态。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;哪怕那些女生，这时候也不例外。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;整个班级，唯一例外的便是方平。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;这时候的方平，满脑子浆糊，满脸的茫然，整个人都傻了。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;啥情况？<br><br>&nbsp;&nbsp;&nbsp;&nbsp;大家说的话他都听懂了，每个字写出来他也肯定认识。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;可为什么串联到一起，他完全听不懂？<br><br>&nbsp;&nbsp;&nbsp;&nbsp;马宗师是谁？<br><br>&nbsp;&nbsp;&nbsp;&nbsp;企鹅集团他当然知道，08年的时候，企鹅集团已经是IT业的一霸了。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;谷歌他也知道，不知道才怪了。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;刚刚张浩话中的意思是，企鹅和谷歌业务竞争？<br><br>&nbsp;&nbsp;&nbsp;&nbsp;可为什么……为什么这么违和！<br><br>&nbsp;&nbsp;&nbsp;&nbsp;方平喉咙再次鼓动了一下，觉得自己嘴唇有些干燥的厉害，这剧本，好像有些不对劲啊！&nbsp;&nbsp;<br></p>', 1, 13, '2019-03-18'),
	(32, '一定要考武科！', '<p>教室中。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;方平满头雾水，内心忐忑。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;好不容易等大家讨论的差不多了，方平捅了捅旁边的陈凡，低声道：“马……马宗师，是马华腾？”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;陈凡脸色一变，瞬间变的严肃起来，认真道：“方平，直呼宗师之名，这是最大的不尊重！”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;方平：“……”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;方平这时候有些想哭，好家伙，连名字都不能提了，小马哥这威风劲，简直比总统还要可怕。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;不过方平也确定了一件事，企鹅的老总果然还是小马哥。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;企业对，老总也对，唯一不对的就是大家口中的武道强者了。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;总觉得有什么奇怪的东西，突兀地夹杂其中，让方平如鲠在喉。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;强压下反驳的**，方平状若无意道：“最近忙着准备高考，也没看什么新闻，你跟我说说，听听有没有什么有意思的事？”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;陈凡不以为意道：“还不是之前的那些事，也就马宗师突破八品，这事有些出人预料。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;陈凡不是太喜欢八卦，可前排的未来络腮胡子杨建，还算健谈。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;这时候上课铃声还没响，杨建也听到了方平的问话，转头小声道：“确定的大新闻倒是不多，不过前几天我看了一下八卦新闻，倒是知道一点小道消息，也不知道准不准。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;听说阿狸的那位马大师，近期可能突破了七品，位列宗师之位！<br><br>&nbsp;&nbsp;&nbsp;&nbsp;白度的李宗师，好像也准备闭关，突破八品境，李宗师突破七品的时间和企鹅的马宗师相差无几，不过近几年李宗师武道进展没有马宗师快，能不能突破不太好说。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;对了，还有个消息，咱们南江省的张总督，很有可能在近期突破七品。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;一旦张总督突破七品，那可就了不得了，咱们南江积弱多年，如今就几位老一辈七品宗师坐镇一方，张总督年纪不大，突破的话，有望再进一步，一扫南江武道积弱的顽疾。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;还有，今年武科大考前，咱们学校好像请了去年考上南江武大的一位学长给我们做演讲……”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;杨建络绎不绝地说着八卦，可方平觉得自己好像在听天书。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;杨建说的八卦消息，大部分都是和武者有关的消息。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;某某某可能要突破了，某某某正在闭关。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;这其中，有些人方平听说过名字，不局限于商界，包括演艺界，甚至包括政界。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;从杨建不经意的一些话语中，方平可以感受到，武者在这个社会，地位很尊崇，七品以下的强者，杨建都以大师为后缀，七品以上更是称之为宗师。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;另一点也可以看出，凡是知名人物，好像都是武道强者。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;又或者换句话说，不是强者，也出不了名。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;期间，方平随口问了一句，这些人有多强？<br><br>&nbsp;&nbsp;&nbsp;&nbsp;结果杨建很不客气，来了句：“随便来一位武者，都能把咱们打成筛子！”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;等上课铃声响了，趁着老师还没来，杨建略有感慨道：“考不上武科，不成武者，一辈子也就这样了。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;当官不过市，经商也是如此，普通人想冒头，难啊！”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;这话一出，方平再次发愣。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;而之前没怎么开口的陈凡，也有些失落，低声道：“武者毕竟是少数，何况对我们而言，无论从商从政，恐怕都走不到那一步，也没必要在乎这些。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;通过两人的对话，方平隐隐约约间感受到了一点东西。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;不成为武者，按照他的理解，当官的不会超过厅级，从商的生意做不到其他市。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;不知道是硬性规定，还是大家默认的潜规则。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;可不管是哪种，方平都能感受到这个社会对他的浓浓恶意！<br><br>&nbsp;&nbsp;&nbsp;&nbsp;不成武者，不成强者，哪怕自己是重生者，恐怕也只能一辈子在底层打转。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;关键是，现在方平严重怀疑，自己到底是不是重生？<br><br>&nbsp;&nbsp;&nbsp;&nbsp;虽然同学样貌没变，名字没变，包括那些大佬，企业名称，都对的上号。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;可突然冒出个高能职业，其他的真和自己记忆中的相同吗？<br><br>&nbsp;&nbsp;&nbsp;&nbsp;有心想再问几句，不过此刻老师已经进了教室，其他人各回各位，没再闲聊。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;而且这些东西，在其他人看来都是常识性的东西，方平也不想再细问下去。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;再问下去，恐怕这些家伙都要怀疑他失忆了。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;想了想，趁着老师转身的空隙，方平低声询问陈凡道：“咱们学校……学校附近还有网吧吗？”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;陈凡有些奇怪地看了他一眼，不过还是回复了一句：“当然有，你之前不是经常去那个蓝天网吧吗？”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“呼！”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;方平吐了口气，点点头没再说话，看来有些东西还是一样的。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;比如这个名字俗套的网吧还在，网吧既然在，说明有些东西没变，自己放学了去网吧查查资料，应该能查到自己想要了解的讯息。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;见方平如释重负，陈凡还是善意提醒道：“虽然咱们考不上武科，可也不能放弃，文科考的好，一样有出人头地的机会，说不定以后还有成为武者的机会。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;高考在即，网吧还是少去……”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;方平笑着点了点头，作为“平凡二人组”的成员，陈凡虽然话不多，可大家条件差不多，对自己这个同桌还算关心。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;……<br><br>&nbsp;&nbsp;&nbsp;&nbsp;好不容易熬完了上午剩下的几节课，放学铃声一响，方平就急忙往外走去。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;这时候的他，满肚子的疑惑需要解疑。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;陈凡见方平急匆匆往外走，想了想还是追上前说道：“你去吃饭还是去网吧？”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“网吧。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“早点回来，下午第一节是班主任的课。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;方平应了一声，这时候的自己，因为父母都在上班，中饭都是在学校附近快餐店吃，不用回家。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;趁着中午的空闲，他那强烈的求知欲，让他恨不得马上去网吧了解一些自己不知道的东西。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;迈着快捷的步伐，方平简单扫了一圈沿途的环境。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;和记忆中一样，阳城一中并没有什么不同之处，同学和老师也都很正常，方平没看到有人飞檐走壁。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;要说反差，大概就是学校中多了一些奇怪的宣传标语和招牌。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“备战高考，武科我来了！”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“拼死一搏，力争武科！”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“考武科，来青鸟——青鸟武道辅导班，您最佳的选择！”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“一颗血气丸，保你上武科！”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“……”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;如果不是这些奇怪的玩意正大光明地出现在校园中，方平绝对有理由相信，神棍和卖大力丸的混进了学校。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;可此刻，看所有人习以为常的样子，方平明白，这终究不是自己记忆中的世界。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;至于差距到底有多大，还要等自己弄清楚了才能知道。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;……<br><br>&nbsp;&nbsp;&nbsp;&nbsp;十分钟后，方平抵达了距离学校不算太远的蓝天网吧。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;还是熟悉的味道，还是熟悉的招牌。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;当年看起来挺高大上的网吧，如今再看，显得破烂不堪，“蓝天网吧”四个字都快看不出来了。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;迈步踏入网吧，门口吧台坐着的还是那个熟悉而又有些陌生的女网管，年纪不大的小女生。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;想当年，荷尔蒙迸发的青春时代，打扮的比学生成熟的女网管，也有那么几次偷偷潜入方平的梦中，做一些羞羞的坏事。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;如今再看……<br><br>&nbsp;&nbsp;&nbsp;&nbsp;方平觉得自己青春时代，眼睛肯定长的有点歪。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;也是，真要美破天际，做啥网管。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;扫了一眼女网管，方平没心思撩妹，就算撩，这种档次的妹子也不适合高大上的重生者。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;不过一想到“马宗师”，那种油然而生的俯视感瞬间破灭。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;重生者是挺高大上的，可关键得重生对地方啊！<br><br>&nbsp;&nbsp;&nbsp;&nbsp;心里腹诽了几句，方平也不拖拉，直奔主题道：“开台机子，多少钱一小时？”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“3块。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;女网管回答的也干脆，紧接着就道：“是会员吗？不是会员开个会员，充50送10块。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;方平一脸高傲，懒得搭理她。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;充50才送10块，太小气了！<br><br>&nbsp;&nbsp;&nbsp;&nbsp;再说……你哪只眼睛看出来我像是有50块钱的人了？<br><br>&nbsp;&nbsp;&nbsp;&nbsp;上午几节课，方平已经摸清楚了自己的身家，全部财富28块整。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;这点钱，包括自己的中午饭钱，想充50也得拿的出来才行。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;无视了网管的推销，方平从兜里摸出一张5元大钞，满脸傲娇地拍在吧台上。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;当然，女网管也无视了他这种穷B，扔过来一张临时会员卡就不再搭理他。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;方平很想喊一句“莫欺少年穷”，不过考虑到网管级别太低，这话以后留给大佬就好，没再和网管较劲，拿着临时会员卡去了网吧角落。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;……<br><br>&nbsp;&nbsp;&nbsp;&nbsp;网吧一角。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;随着电脑打开，方平如饥似渴地搜索自己想要知道的讯息。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;幽蓝的显示屏灯光，映射的方平脸色有些阴森。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;如果这时候有人坐在方平附近，就能感受到方平的异样。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;时而脸色变幻，时而愤愤不平，时而咬牙切齿……<br><br>&nbsp;&nbsp;&nbsp;&nbsp;有时候，还有几句国骂从方平口中吐出，也不知道是在骂老天，还是在骂谁。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;一个多小时后，机器自动关机，网费用完了。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;方平没心思再充钱，带着些许迷茫和了然，迈步走出了网吧。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;踏出网吧的那一刹那，方平面色坚定了许多，咬牙切齿道：“考武科！”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;仿佛还不足以证明自己的决心，方平又补充道：“一定要考武科！”&nbsp;&nbsp;<br></p>', 1, 13, '2019-03-18'),
	(33, '穷文富武', '<p>网吧外。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;方平神情坚定，眼含执着。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;不执着不行！<br><br>&nbsp;&nbsp;&nbsp;&nbsp;通过一个多小时的浏览查询，方平总算搞明白了一些东西。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;比如说，武者是什么玩意？<br><br>&nbsp;&nbsp;&nbsp;&nbsp;现代武者和前世武侠小说，武侠电影没太大的差别，不过是背景换成了现代罢了，武者这个职业，正大光明地出现在了现代社会。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;如果只是单纯的出现了武者这个高能职业，方平就算对超人有兴趣，也不见得非要成为武者。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;可在如今的社会，武者却不再是单纯的一个高能职业，更意味着权利和地位！<br><br>&nbsp;&nbsp;&nbsp;&nbsp;弱肉强食，亘古不变的真理。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;前世，这种差异其实体现的不是太明显，毕竟大家都是能被一枪撂倒的普通人。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;而在这个充斥着武道强者的社会，差距却是明显的不能再明显了。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;比如说，方平之前对杨建说的从政不过市，从商不过市还理解的不深，那现在就彻底明白了。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;普通人是这个社会的主流，普通人从事任何行业，其实都没限制。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;可按照法律规定，是的，法律明文规定！<br><br>&nbsp;&nbsp;&nbsp;&nbsp;一家公司，跨越市区发展，注册的法人代表，必须是武者，等级不限。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;而想跨省发展，公司法人则是有了等级要求，不得低于武道四品！<br><br>&nbsp;&nbsp;&nbsp;&nbsp;按照方平在网上查到的资料，如今的武道，共分九品，一品最低，九品最高。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;四品以下为低段武者，七品以上包括七品，则是高段武者，这群人又被称之为宗师级强者。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;想成立一家跨省发展的公司，首先一点，公司法人得是武道四品以上的强者。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;而想成为跨国性的集团，虽然法律没规定，可方平也在网上简单查了查，没有七品武道境，哪怕你公司产品再好，受众再多，贸然进入别的国家，别的区域，那跟送死送钱没区别。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;就算集团有七品以上强者，也得证明自己的实力才行。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;比如如今的小马哥！<br><br>&nbsp;&nbsp;&nbsp;&nbsp;企鹅的产品，和前世差不多，也是以即时通讯为主，QQ也一如既往，是华国目前主要的网络聊天工具。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;可也仅仅局限于华国！<br><br>&nbsp;&nbsp;&nbsp;&nbsp;企鹅如今连亚洲都无法进入，因为之前马华腾实力虽然达到了七品，可作为世界性大集团，谷歌也开发了即时通讯软件，谷歌在亚洲坐镇的强者是八品武道境。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;马华腾一日无法证明自己的实力，有这个实力和谷歌争夺资源，那就别想往外扩张。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;因为不在国内，你派去扩张的人，很有可能一夜之间消失的无影无踪。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;唯有向外界，向同行证明自己拥有扩张的实力，你才能踏踏实实的扩张，这也是马华腾在突破八品之后，马上就选择挑战泰姆的原因。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;这次挑战，胜负不重要，重要的是证明他马华腾如今已经是八品的绝顶强者，有资格分杯羹。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;这是商界的规矩，而其他各界，也差不多都是如此。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;比如政界，和前世略有差别，一省之内总督权柄最大，一省总督，最低也是四品武道境。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;实际上，如今的各省总督，几乎没有低于五品的，一些强省，总督都是七品宗师级。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;南江省现任总督目前还是六品武道境，虽然在各省总督之中不是最弱，不过也不算强大，这也造成，无法为南江争取更多的资源。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;资源多少，决定了官方对武者培养的力度和投入。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;之前有小道消息传闻南江总督即将突破，杨建说起这事颇为兴奋，原因便在于此。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;宗师级的总督，可以为南江争取更多的资源。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;政商军各界如此，就连娱乐圈和其他行业也不例外。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;名声在外的天王天后们，不但要有才艺，要相貌过人，还得要有不俗的武道实力。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;因为你没实力，你挣了再多的财富，你也守不住。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;除非，你有实力强悍的父辈撑腰。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;如今的富二代官二代，几乎都有个统一的称呼——武二代！<br><br>&nbsp;&nbsp;&nbsp;&nbsp;因为有钱有权的，都是强大的武者。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;普通人也有极少一部分可以做到高层，可这些人无一例外，背后都有强大的武者撑腰，而且自身也要体现出绝对的价值，比武者要求苛刻无数倍的价值。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;成不了武者，注定和社会精英、有权有势无关。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;这种极致的差距，也造成了在现代社会，所有普通人都向往成为一名武者。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;哪怕再弱的武者，什么都不干，在一些小公司挂个名，那每年最少也是百万起步的收入。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;实际上，真正能成为武道强者的，无一例外，都是社会的精英阶层，这些人除非臣服于其他更强者，要不然挂靠这种事都干的很少，更多的还是自己创业。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;比起普通人，他们优势太明显了。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“不成武者，金点子再多，那也是给别人送菜。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;方平自语一声，这也是血淋淋的事实。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;刚刚上网查资料，除了和武者有关的事，其他的和前世差不多。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;也就是说，方平如果要创业的话，产品、点子这些东西还是不差的。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;关键在于，方平自己没有自保的能力，小赚一点关系不大，想做大做强，那就是痴人说梦了。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;稍有越界，最大的可能是产业被夺，因为华国法律机制还算完善，官方也有无数强者坐镇，别人要你小命不至于，可挣下的家业，最大的可能还是便宜其他人。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;比如此刻，社会上还没有微信的存在。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;如果方平弄了出来，只有两个结果，第一，成了别人的。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;第二，在本市流传，当成小众娱乐的玩意，超过本市，对不起，赚钱再多，那也不是你的，至于归谁，看各方角力。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;普通人，那就得踏踏实实地上班打工，了不起开一些小公司作坊，在本市内晃荡。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;就算在本市，也不见得一定安全。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;这种朝不保夕，战战兢兢的生活，比起前世的普通人限制要更大。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;方平要是不想当个咸鱼存在，那成为武者，就成了必然。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;哪怕他想成为咸鱼，可起码也是个衣食无忧、安全有保障的咸鱼吧？<br><br>&nbsp;&nbsp;&nbsp;&nbsp;好在这个世界还是普通人居多，社会也没真的抛弃这些普通人，断了他们上升的希望。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;高考，便是无数普通人一步登天，鲤鱼跃龙门的希望。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;如今的一部分名校，都成立了武科系，专门用于培养武者，不至于断了普通人的希望。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;还有一些专职武校，也会在高考的时候招生。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;不过俗话说的好，穷文富武。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;武者拥有强大的实力，耗费的资源也远远超出普通人，培养一个武者，耗费的金钱无数。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;在这种情况下，考取武科，获得国家的资源培养，非天之骄子不可。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;所以每年的高考，武科生要比前世考名校更难！<br><br>&nbsp;&nbsp;&nbsp;&nbsp;07年，华国参加高考的学生总计900万，而全国各地各大学，最终录取的武科生总共不到2万人，这是所有学校的合计数字。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;前世清华北大这些高校，虽然每年招生只有几千人，可名校也不在少数，985那些高校加在一起，每年招收的学生也超过10万以上了。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;而如今全国各地，设立武科系的大学和专职武校超过100所，100所大学，招生2万人，平均下来太少太少。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;接近五百分之一的录取比例，看似不低，实际上那是大都市，小城市这个比例要扩大十倍！<br><br>&nbsp;&nbsp;&nbsp;&nbsp;这也是让陈凡这些人绝望的主要原因。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;除此之外，报考武科，还有许许多多的门槛。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;之前陈凡说的报名费一万，这只是最低门槛罢了。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;如今其他条件不说，光是报考武科的报名费，就足以让方平头疼。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;而武科报名，下个星期就要开始了，报名也有时间限制。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;过了时间，有钱也没法报考武科了。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“一万的报名费……”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;刚刚还雄心壮志的方平，这时候有些蔫了，光是起步条件，就让心中的火焰熄灭了一大半。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;更别提，后面还有一系列的要求了。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“头疼啊！”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;方平唉声叹气，重生者重生到现代社会，混的还这么惨的，恐怕不多了。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;……<br><br>&nbsp;&nbsp;&nbsp;&nbsp;等方平吃完饭回到学校，此刻兜里只剩下15块钱了。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;下午课程还没开始，班上的同学，一部分在做题备战高考，一部分在闲聊，聊的更多的还是上午张浩说的宗师之战。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;见方平回来了，正在做题的陈凡连忙放下笔，询问道：“去玩游戏了？”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;方平翻着白眼，无语道：“这时候还有心情玩游戏，你当我傻啊？查高考资料去了。”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;说着，方平搓了搓手，笑眯眯道：“小凡凡，手里有余钱吗？”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;陈凡微微蹙眉，半晌才道：“还有10块钱……”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“咳咳，别开玩笑了，有钱的话，借个万儿八千的，回头我发达了，百倍偿还！”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;“呵呵！”<br><br>&nbsp;&nbsp;&nbsp;&nbsp;陈凡皮笑肉不笑，扶了扶眼镜继续埋头做题，方平穷疯了吧！<br><br>&nbsp;&nbsp;&nbsp;&nbsp;方平叹息一声，此路不通啊。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;都是学生，别说陈凡家境一般，就算班上条件不错的同学，谁傻到会借一万给你。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;难道只能跟爸妈开口？<br><br>&nbsp;&nbsp;&nbsp;&nbsp;可自家条件一般，如今的一万可不是小数字，按照自己的标准，报考武科几乎没希望，这要是张口了，爸妈能同意吗？<br><br>&nbsp;&nbsp;&nbsp;&nbsp;今天是周六，只有高三学生上课，说是下星期报名，实际上距离下周一也就两天不到的时间。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;要是时间宽裕一些，自己还能想办法看看能不能凑个报名费，可两天不到的时间，自己狂开金手指也弄不到一万块吧。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;这还是报考武科的第一步，接下来想要考上武科，还得继续花钱。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;这个社会，强者都是靠资源，靠财富堆出来的。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;要不然，武道强者为什么都在经营企业，都想将企业做大做强。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;就算不经营企业，大部分也会进入官方机构。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;小说电视中的那些强者，潇洒自若，随随便便顿悟一下就能升级，不吃不喝都行，换成之前，方平还觉得没什么。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;可如今，方平知道这纯属扯淡！<br><br>&nbsp;&nbsp;&nbsp;&nbsp;中午上网简单看看，网上就有一些爆料，哪怕最弱的武道一品武者，培养这样的武者，耗费的资源换算成财富，起码也得百万了！<br><br>&nbsp;&nbsp;&nbsp;&nbsp;这还是一品，在方平眼中那是最弱，最不入流的武者。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;就这样，就得消耗百万金钱。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;越往上的武者，耗费的资源越多，花钱都是天文数字，没有产业支持，想走的更远根本不可能！<br><br>&nbsp;&nbsp;&nbsp;&nbsp;普通人家要是考不上武科，以现在普通人的工资水准，有几家可以在大学之外自学成才，成为武者的？<br><br>&nbsp;&nbsp;&nbsp;&nbsp;大家之所以对武科大学趋之若鹜，关键就在于大学里的武科，会给学生提供大半的资源，这是公家出钱培养。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;就算还有些欠缺，考上了武科，银行大门也会对你开放，贷款不是问题。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;又或者，提前和一些大公司签订合约，武科生很吃香，各家集团也乐得花钱培养一些预备种子。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;而这一切的前提，都是考上了武科才行。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;想不花钱就成为武者，痴人做梦。<br><br>&nbsp;&nbsp;&nbsp;&nbsp;方平再次叹息一声，今天都不知道叹了多少口气了，这重生怎么重生的这么憋屈呢！&nbsp;&nbsp;<br></p>', 1, 13, '2019-03-18');
